DROP TABLE IF EXISTS `ddsc_assemble_goods`;
CREATE TABLE `ddsc_assemble_goods` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL COMMENT '商品名称',
  `tag` varchar(255) DEFAULT NULL COMMENT '商品标签',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品价格',
  `floor_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '团购价格',
  `group_number` int(11) NOT NULL DEFAULT '0' COMMENT '拼团人数',
