DROP TABLE IF EXISTS `ddsc_bargain_dd`;
CREATE TABLE `ddsc_bargain_dd` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `bid` int(11) NOT NULL DEFAULT '0' COMMENT '砍价活动id',
  `gid` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',
  `name` varchar(255) DEFAULT NULL COMMENT '商品名称',
  `image` varchar(255) DEFAULT NULL COMMENT '商品图片',
  `goods_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品价格',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单价格',
  `code` varchar(255) DEFAULT NULL COMMENT '订单编号',
  `status` tinyint(3) NOT NULL DEFAULT '0' COMMENT '订单状态 0未支付 1已支付',
  `time` varchar(255) DEFAULT NULL COMMENT '订单生产时间',
  `content` varchar(255) DEFAULT NULL COMMENT '订单备注',
  `aid` int(11) NOT NULL DEFAULT '0' COMMENT '收货地址id',
  `fu_time` varchar(255) DEFAULT NULL COMMENT '付款时间',
  `shop_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='砍价订单';

