#
# Structure for table "ddsc_bargain_goods"
#

DROP TABLE IF EXISTS `ddsc_bargain_goods`;
CREATE TABLE `ddsc_bargain_goods` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL COMMENT '商品名称',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品原价',
  `floor_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '低价',
  `up` tinyint(3) NOT NULL DEFAULT '0' COMMENT '上下架 0否 1是',
  `sort` int(11) NOT NULL DEFAULT '50' COMMENT '排序',
  `content` longtext COMMENT '商品详情',
  `start_time` varchar(255) DEFAULT NULL COMMENT '开始时间',
  `end_time` varchar(255) DEFAULT NULL COMMENT '结束时间',
  `time` int(11) NOT NULL DEFAULT '0' COMMENT '砍价时长',
  `number` int(11) NOT NULL DEFAULT '0' COMMENT '参与人数',
  `nums` int(11) NOT NULL DEFAULT '0' COMMENT '前面砍价人数',
  `first_price` int(10) NOT NULL DEFAULT '0' COMMENT '砍价区间',
  `first_prices` int(11) NOT NULL DEFAULT '0',
  `end_price` int(11) NOT NULL DEFAULT '0',
  `end_prices` int(11) NOT NULL DEFAULT '0',
  `tag` varchar(255) DEFAULT NULL,
  `image` varchar(255) DEFAULT NULL,
  `shop_id` int(11) NOT NULL DEFAULT '0' COMMENT '商家id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 COMMENT='砍价商品';

