#
# Structure for table "ddsc_collect"
#

DROP TABLE IF EXISTS `ddsc_collect`;
CREATE TABLE `ddsc_collect` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `gid` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',
  `type` int(11) NOT NULL DEFAULT '0' COMMENT '0商品收藏 1乡村旅游收藏 2酒店民宿 3景区 4美食',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=50 DEFAULT CHARSET=utf8 COMMENT='收藏列表';

