#
# Structure for table "ddsc_coupon"
#

DROP TABLE IF EXISTS `ddsc_coupon`;
CREATE TABLE `ddsc_coupon` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `open` tinyint(3) NOT NULL DEFAULT '0' COMMENT '是否开启 0关闭 开启',
  `moneys` int(10) NOT NULL DEFAULT '0' COMMENT '订单金额',
  `money` int(10) NOT NULL DEFAULT '0' COMMENT '优惠券金额',
  `num` int(11) NOT NULL DEFAULT '1' COMMENT '领取次数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='优惠券';

#
# Data for table "ddsc_coupon"
#

INSERT INTO `ddsc_coupon` VALUES (1,1,199,50,2);

#
# Structure for table "ddsc_culture"
#

DROP TABLE IF EXISTS `ddsc_culture`;
CREATE TABLE `ddsc_culture` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `content` longtext COMMENT '详情',
  `status` tinyint(3) NOT NULL DEFAULT '1' COMMENT '状态 0开启 1关闭',
  `recome` tinyint(3) NOT NULL DEFAULT '0' COMMENT '推荐 0开启 1关闭',
  `sort` int(11) NOT NULL DEFAULT '50' COMMENT '排序',
  `time` varchar(255) DEFAULT NULL,
  `writer` varchar(255) DEFAULT NULL COMMENT '作者',
  `looks` int(11) NOT NULL DEFAULT '0' COMMENT '浏览量',
  `cid` int(11) NOT NULL DEFAULT '0' COMMENT '城市id',
  `xid` int(11) NOT NULL DEFAULT '0' COMMENT '县区id',
  `zid` int(11) NOT NULL DEFAULT '0' COMMENT '乡镇id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=61 DEFAULT CHARSET=utf8 COMMENT='区域文化';

#
# Data for table "ddsc_culture"
#

INSERT INTO `ddsc_culture` VALUES (14,'最美河南','/uploads/20190627/84dab9746e803e194ea020833aeceadf.jpg','',0,0,1,'1561605074','',41,1,-1,4),(15,'醉美平顶山','/uploads/20190627/7c0689a11a1c16ccbc316747200df03d.jpg','<p style=\"margin-top: 15px;margin-bottom: 15px;padding: 0px;list-style: none;border: 0px;text-indent: 0;font-family: FZZhongDengXian-Z07S;font-size: 18px;white-space: normal\"><span style=\"font-size: 16px;\">平顶山市位于河南省中部，<span style=\"padding: 0px; list-style: none; border: 0px;\">1957年建市，现辖2市4县4区，面积7882平方公里，总人口550万。2017年，全市完成生产总值2015.3亿元、一般公共预算收入137.5亿元。既是资源型工业城市，也是中国优秀旅游城市、国家园林城市、国家森林城市、国家卫生城市和省文明城市，还是中国曲艺城、中国书法城、中国观音文化之乡、中国汝窑陶瓷艺术之乡和中国唐钧基地。</span></span></p><p style=\"margin-top: 15px;margin-bottom: 15px;padding: 0px;list-style: none;border: 0px;text-indent: 0;font-family: FZZhongDengXian-Z07S;font-size: 18px;white-space: normal\"><span style=\"text-indent: 43px; padding: 0px; list-style: none; border: 0px; font-family: 宋体, SimSun;\"><strong>自然资源丰富。</strong></span><span style=\"text-indent: 43px; font-family: 宋体, SimSun; font-size: 16px;\">已探明各类矿藏<span style=\"padding: 0px; list-style: none; border: 0px;\">57种，其中原煤储量100亿吨，是中南地区最大的煤田；钠盐储量3300亿吨，是中国岩盐之都；铁矿石储量10亿吨，是全国十大优质铁矿之一。有各类水库169座，南水北调中线工程从我市穿境而过，有白鹭洲、白龟湖两个国家级湿地公园。旅游资源单体4200多个，居河南省第二位。尧山—中原大佛景区是国家5A级旅游景区，景区有绵延百里的温泉带和世界最高铜制立佛中原大佛，尧山地质公园是国家地质公园。平顶山博物馆、二郎山、灯台架、画眉谷、三苏园、尧山大峡谷漂流、香山寺等是国家<span style=\"padding: 0px; list-style: none;\">4A级旅游景区。</span></span></span></p><p style=\"margin-top: 15px;margin-bottom: 15px;padding: 0px;list-style: none;border: 0px;text-indent: 0;font-family: FZZhongDengXian-Z07S;font-size: 18px;white-space: normal\"><span style=\"text-indent: 43px; padding: 0px; list-style: none; border: 0px; font-family: 楷体_GB2312;\"><strong>历史文化深厚。</strong></span><span style=\"text-indent: 43px; font-family: 宋体, SimSun; font-size: 16px;\">西周时期为武王宗室应侯封地应国，应国以鹰为图腾，因此平顶山又称鹰城。境内全国重点文物保护单位<span style=\"padding: 0px; list-style: none; border: 0px;\">25处，有千手观音证道祖庭香山寺、千年古刹风穴寺、国家重大考古发现汝官窑遗址、苏轼父子三人的安息地三苏园、全国保存最完整的明代县衙叶县县署、国家级非物质文化遗产马街书会，是河南曲剧的发源地和世界叶姓、刘姓、应姓、沈姓的祖籍地。</span></span></p><p style=\"margin-top: 15px;margin-bottom: 15px;padding: 0px;list-style: none;border: 0px;text-indent: 0;font-family: FZZhongDengXian-Z07S;font-size: 18px;white-space: normal\"><span style=\"text-indent: 43px; padding: 0px; list-style: none; border: 0px; font-family: 楷体_GB2312;\"><strong>产业优势突出。</strong></span><span style=\"text-indent: 43px; font-family: 宋体, SimSun;\">拥有能源、化工、装备制造、冶金建材、轻工食品等支柱产业，正在打造国际新材料产业基地，培育壮大新能源、新医药、现代金融、全域旅游、现代物流、生态循环农业等产业。平煤神马集团、舞钢公司、平高集团、姚电公司、鲁阳电厂等一大批企业在全国同行业占有重要地位。被确定为第一批国家农业可持续发展试验示范区和国家农业绿色发展试点先行区，是全省唯一、全国<span style=\"padding: 0px; list-style: none; border: 0px;\">40个之一。</span></span></p><p style=\"margin-top: 15px;margin-bottom: 15px;padding: 0px;list-style: none;border: 0px;text-indent: 0;font-family: FZZhongDengXian-Z07S;font-size: 18px;white-space: normal\"><span style=\"text-indent: 43px; padding: 0px; list-style: none; border: 0px; font-family: 楷体_GB2312;\"><strong>基础条件较好。</strong></span><span style=\"text-indent: 43px; font-family: 宋体, SimSun;\">地处京广和焦枝两大铁路干线之间，距新郑国际机场<span style=\"padding: 0px; list-style: none; border: 0px;\">100公里。兰南、宁洛、二广、郑尧、焦桐5条高速公路穿境而过。正在建设的郑万高铁从我市通过，并在城乡一体化示范区和郏县设站。拥有高等院校5所、省级以上各类研发平台147家，其中国家级企业技术中心4家、重点实验室1家、国家级科技企业孵化器1家，国家级星创天地1家，是全国科技进步先进市、国家知识产权试点市。</span></span></p><p><br/></p>',0,0,50,'1561606166','',21,12,-1,12),(16,'平顶山市宝丰县','/uploads/20190704/0786283ef8dbeed8ada135855a8aa930.jpg','<p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">宝丰县位于河南省中西部，隶属平顶山市，总面积722平方公里，总人口52万人。宝丰县是“中国曲艺之乡”、“魔术之乡”和“民间文化艺术之乡”，为河南省八大文化改革发展试验区之一，同时还是“国家卫生县城”、“国家园林县城”和“省级文明县城”。</p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">一、历史沿革</p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">1、商周时为<span style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\">应国</span>属地，春秋初属郑，后属楚，战国初期属韩。秦置父城县，汉因之。隋、唐时先后为汝南县、滍阳县、武兴县、龙兴县。</p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">2、武周证圣元年(695年)置武兴县(治今宝丰县城)，属汝州。唐神龙元年(705年)改名中兴县，寻改称龙兴县。北宋熙宁五年(1072年)废龙兴县，元祐元年(1086年)复置。宣和二年(1120年)，蒙古至元三年(1266年)废。明成化十一年(1475年)复置宝丰县，属汝州。清因之。</p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">2、1913年属豫西道，1914年属河洛道，1927年直属河南省。1936年与郏县合并，改辅城县，同年复宝丰县。1949年属许昌专区，1959年属许昌地区。1960年废入平顶山市，1961年再置宝丰县，仍属许昌专区。1969年属许昌地区。1983年属平顶山市。</p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">二、地名来历</p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">宋徽宗宣和二年（1120年），因当时县境内有白酒酿造、汝官瓷烧制，冶铁工场等，物宝源丰，宝货兴发，奉敕赐名“宝丰县”。《读史方舆纪要》汝州宝丰县：“以县有冶铸场，改曰宝丰。”</p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">三、风景名胜</p><p><img src=\"http://p9.pstatp.com/large/pgc-image/cc327de6be1e44959dc7c19c96f49e1b\" img_width=\"553\" img_height=\"370\" alt=\"河南省宝丰县，北宋置县，因制造业发达得名，国家园林县城\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">1、香山寺。为全国重点文物保护单位。位于宝丰县闹店镇南，建于东汉末期，大约在汉灵帝光和年间，比白马寺晚约百余年，历经汉、魏、晋、南北朝、隋、唐、宋、元、明、清，迄今已有1800多年的历史`。是现在已知的中国早期佛寺之一，在中国佛教史和观音文化史上具有重要的不可替代的地位，其宗教根文化特征非常突出。</p><p><img src=\"http://p3.pstatp.com/large/pgc-image/0e08dd8f17014fea8e12b9cb40a0593c\" img_width=\"640\" img_height=\"394\" alt=\"河南省宝丰县，北宋置县，因制造业发达得名，国家园林县城\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">2、白雀寺。位于宝丰县李庄乡古城村西父城遗址之上。白雀寺始建于后秦姚苌白雀年间（公元384—385年）。古人以白雀为瑞应的象征，白雀集而吉祥至。相传白雀年间白雀群集于父城故址——古槐之上，人们以为祥瑞而建起寺庙称白雀寺。</p><p><img src=\"http://p1.pstatp.com/large/pgc-image/c09f16ec12b6450eb7a3ebbf0ed2324f\" img_width=\"440\" img_height=\"330\" alt=\"河南省宝丰县，北宋置县，因制造业发达得名，国家园林县城\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">3、文笔峰。位于县城南2.5公里的文笔山上，内有文笔峰塔又称文峰塔，明代，第二批省级文物保护单位。该塔于明万历四十七年建造为六棱实心砖塔，下有六角青石塔座，上部六棱尖顶，无层、无门窗，略似华表，造型别致，风格独特，为河南省塔列中之孤例。</p><p><img src=\"http://p3.pstatp.com/large/pgc-image/c25b33bfcc784772becb08581f8605c6\" img_width=\"600\" img_height=\"378\" alt=\"河南省宝丰县，北宋置县，因制造业发达得名，国家园林县城\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">4、高皇庙。位于县城北10公里石桥镇高皇庙村。高皇庙因刘邦曾在此地驻扎而得名。</p><p><img src=\"http://p9.pstatp.com/large/pgc-image/2d7fb919d1784bcd9dfe5fefbd4de136\" img_width=\"640\" img_height=\"426\" alt=\"河南省宝丰县，北宋置县，因制造业发达得名，国家园林县城\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">5、文庙大成殿。明代，第五批省级文物保护单位，位于宝丰县城东大街县一中院内。文庙大成殿始创于宋，坐北向南。明成化十二年（1476年）重建，崇祯十年（1637年）扩建。</p><p><br/></p>',0,0,50,'1562208297','旅途掠影',21,12,-1,0),(17,'宝丰马街书会','/uploads/20190707/f30bd68a3c269834b2391a7abb241967.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">宝丰马街书会，是我国曲艺界的盛会，每年农历正月十一至十三，在宝丰县城南7公里处的杨庄镇马街村举行，俗称“十三马街书会”。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">马街书会历史久远。据马街村广严寺和火神庙碑刻记载及考证，书会起源于元代延祐年间，距今已有700多年的历史。传说其时马街村有一位叫马德平的说书艺人，技艺精湛，人品高尚，桃李满天下，农历正月十三这天，老艺人仙逝，徒弟们都来悼念他，为他说书献艺。这天恰逢村东北火神庙会，此会时值上元之前，而上元买书说唱为当地民俗，马街又地处交通要道，故其说唱艺人被征聘一空。这样年复一年，相沿成习，书会随庙会应运而生，遂成传统的马街书会。</span></a></p><p><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">每年的正月十三，来自山东、安徽、河北、湖北、四川、陕西及河南各地的成百上千的民间曲艺艺人，负鼓携琴，汇集于宝丰马街，以天作幕，以地为台，书会期间，周围百里村民，数十万群众从四面八方络绎不绝前来赶会、听书、写书。千座书棚，吹拉弹唱，人头攒动，摩肩接踵，热闹非凡，规模壮观，其宏大场面被称为中国文化史上的一大奇观。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562493487911787.png\" alt=\"\" title=\"\" align=\"center\" width=\"483\" height=\"345\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; width: 644px; height: 291px;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562493488999861.png\" alt=\"\" title=\"\" width=\"775\" height=\"300\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; width: 652px; height: 218px;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">马街书会聚集了不同曲种、不同流派、不同地区的艺人，演唱曲目繁多，书目浩瀚，被称为“书山曲海”。据统计曲艺种类有40余种，传统及现代书（曲）目有千余个，唱腔曲牌有500多个。曲种主要有河南坠子、三弦书、大调曲子、大鼓书、山东琴书、凤阳花鼓、湖北渔鼓、四川清音、道情、越调乱弹、山东柳琴、徐州琴书、西河大鼓、相声、快板书、评书、评词等；书目有《杨家将》《呼家将》《包公案》《大红袍》《响马传》等。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">马街书会是在固定时间、固定场所以曲艺演唱、艺人亮艺卖书为核心，融酬神娱人、祭祀宗教、经贸活动为一体的文化空间，具有独特民风民俗的、传统的文化活动场所，也是民间艺人进行艺术贸易和交流展示的文化空间，是一个典型的曲艺“超市”。艺人在书会上亮艺卖书，写书者从中挑选，以演唱水平确定演唱三天书的价格，在这个过程中，每年产生一名书价最高的“书状元”。这就使得马街书会不仅是艺人以书会友的舞台，也是切磋技艺的擂台，因此，“争当书状元、写出最高价”也是马街书会吸引全国各地艺人不远万里朝圣般前来赶会的重要精神动力之一。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">马街书会具有规模浩大、历史悠久、形式独特、内涵丰富等特点。改革开放以来，马街书会的发展又注人了新的活力，中央及省、市领导的关怀与支持及地方财政对书会的加大投入，使书会得到了健康发展，全国曲协名流宿将纷纷到会助兴，也缘由马街书会，第二届、第六届中国曲艺节在平顶山市举办。国家、省、市级新闻媒体多次对书会进行了相关报道，使书会影响深远，倍放异彩。马街书会以其悠久的历史、深厚的文化底蕴、强大的社会影响力成为河南省的一个文化符号。1996年，马街书会被国务院侨办列人“中国十大民俗”之一，并被制成专题录像片送往我国各驻外使馆作为国际民俗文化交流，宝丰县也因此被国家有关部门命名为“曲艺之乡”“中国民间艺术之乡”。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">2006年，该项目被列入第一批国家级非物质文化遗产名录。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562493588458399.jpg\" title=\"1562493588458399.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p>',1,1,50,'1562493618','旅途掠影',22,12,25,127),(18,'郏县大铜器','/uploads/20190707/4125e32e3e284a09525b54c4aa00eeb4.png','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">郏县大铜器是中国商周文化的遗产，是中原民间艺术之珍品，始于东汉晚期，距今已有1700多年的历史。据《三国志·武帝纪》记载，建安二十五年（公元219年）5月，曹操与刘备争夺汉中，襄樊大战获胜后，曹操在郏县长桥乡青龙寨（摩坡）给将士庆功授奖，命郏县人和宫中乐师打铜器助兴，以鼓士气，激励再战。从此，打铜器在郏县境内广为流传，代代不绝。到明清时，打铜器已演变为驱邪消灾、祭天、唤雨、婚姻贺喜、庙会结社的吉祥之兆。新中国成立后，打铜器成为欢度节日、庆贺丰收、迎送佳宾、开业庆典、增进友谊的欢庆礼仪。</span><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><img src=\"/ueditor/php/upload/image/20190707/1562493974785899.png\" alt=\"图片1\" title=\"图片1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; width: 705px; height: 295px;\" width=\"705\" height=\"295\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a><br/><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">郏县大铜器主要分布于河南省郏县，辐射到平顶山市、新乡市、驻马店市等周边市县，是我国打击乐器的典型代表，也是中国最响亮的打击乐器。它的古谱历史悠久，曲牌丰富，曲中较多运用了1/4、2/4、3/4、4/4、6/4拍，广泛使用了连续切分音及小切分音，它用鼓、铙、镲、锣等乐器合奏，曲牌多以时代背景而命名。有“五虎下西川”“曹操得胜鼓”“马面川”“呼雷炮”“大上桥”“长兴鼓”“狮子滚绣球”“撞倒墙”“三出头”“三百六十捶”“二起板”“锣鼓套”“倒上桥”“一连三”“大开板”“小虫闹竹竿”“千样果”、“花点长兴”、“三不照”、“武头”、“歇拍”等36个传统曲牌（今称曲谱)。表演风格粗犷、酣畅、真挚，时而如蛟龙滔海，时而如大鹏展翅，时而如行云流水，时而高叠狂欢，显示出中华民族积极向上勇于拼搏的精神。上世纪八九十年代，郏县铜器达到发展巅峰，音乐、舞蹈得到挖掘，推陈出新，获得了诸多荣誉，为郏县、平顶山市乃至河南省增光添彩。</span></p><p></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562493976917967.png\" alt=\"图片2\" title=\"图片2\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; width: 722px; height: 451px;\" width=\"722\" height=\"451\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">1988年，在河南省首届艺术节大赛中，荣获一等奖。1992年代表河南省参加“中国沈阳国际秧歌舞蹈节暨民间舞蹈节”大赛荣获金榜第一名。同年12月，参加文化部在北京举办的“群星杯”评奖，又获广场舞蹈金奖第一名。1993年1月，参加北京“龙潭杯”民间舞蹈大赛，再次夺魁，荣获三连冠。1994年代表河南省到深圳市“中国民俗文化村”表演，获一等奖。先后有中央电视台、人民日报、光明日报、河南日报等新闻媒体报道郏县铜器舞，并被译成多种文字发往我驻使领馆。外国专家称郏县铜器舞为“中国农民的迪斯科”。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">2008年，该项目被列入第二批国家级非物质文化遗产名录。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562494085779622.jpg\" title=\"1562494085779622.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\"><br/></span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,1,50,'1562494108','旅途掠影',16,12,20,94),(19,'舞钢市冶铁遗址群','/uploads/20190707/7608c2e14aadfcbca8a220946eb98589.png','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">舞钢冶铁遗址群（石门郭冶铁遗址、许沟冶铁遗址、沟头赵冶铁遗址、翟庄冶铁遗址、圪垱赵冶铁遗址、尖山采矿遗址）分布于平顶山市舞钢市的五个乡、镇、办事处内，战国至汉。&nbsp;2013年，被国务院公布为第七批全国重点文物保护单位。<br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">石门郭冶铁遗址位于垭口办事处石门郭村北土岗上，面积4800平方米。发现两个土槽，槽壁已被火烧结。地表散布有碎铁矿石、熔铁块、炼渣等。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562494391481876.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">许沟冶铁遗址位于尹集镇梁庄村许沟村南台地上，西1500米邻田岗水库，面积约为1.2万平方米。遗址内分布有铁矿石、炉壁残块、泥范和石范等，遗址是一个从冶炼到铸造的综合性冶炼场所。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562494392562084.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">沟头赵冶铁遗址位于武功乡田岗村沟头赵组内，面积约1.2万平方米，发现有多处炼渣、烧结凝结物、炉盘及战国至汉代的陶片等。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">翟庄冶铁遗址位于杨庄乡翟庄村南，面积约8000平方米，发现大量炼渣、炉壁残块和一座炉壁，还见战国时代的瓦片。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">圪垱赵冶铁遗址位于尚店镇马庄村圪垱赵西侧，面积约1.1万平方米，发现有炉址、琉璃体炼渣。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">尖山采矿遗址位于杨庄乡柏庄村西范庄尖山脚下，面积1225平方米。遗址内一处深坑周围有很多矿渣堆积，收集有铁矿石及铁渣凝固体。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">舞钢冶铁遗址群分布比较集中，规模大，延续时间长，在我国古代冶炼遗址中占据重要的位置。冶炼遗址接近原料、燃料产地，对于研究战国至汉代冶金技术、经济的发展具有重要的价值。2013年被国务院公布为第七批全国重要文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190707/1562494486346604.jpg\" title=\"1562494486346604.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,0,50,'1562494507','旅途掠影',8,12,26,0),(20,'宝丰县清凉寺汝官窑遗址','/uploads/20190707/c101e1220fffcf047b2ca523bfe91ba8.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">清凉寺汝官窑遗址位于平顶山市宝丰县大营镇清凉寺、韩庄村，遗址保护范围共计133.2万平方米。汝窑位居“汝、官、哥、钧、定”五大名窑之首，有“汝窑为魁”之称。从20世纪50年代至今，为寻访汝窑遗址，我国几代文博工作者付出了巨大努力。自1987年至2016年，河南省文物考古研究院先后对宝丰清凉寺汝官窑遗址进行了十四次考古发掘，获得了大量瓷器、窑具、标本，出土了不少传世品中未见的新器形，发现了20多座烧制御用汝瓷的窑炉及作坊遗迹。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190707/1562494666201361.jpg\" title=\"1562494666201361.jpg\" alt=\"微信图片_20190707180826.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a><br/><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">中国宝丰清凉寺汝官窑遗址展示馆于2012年9月开工建设，2017年1月16日竣工开馆。展示馆为单层框架及钢网架结构，建筑面积5160平方米，占地面积30000多平方米。展示内容分寻觅之路、揭开面纱、再现辉煌、不负耕耘四部分，重点对第六至第十四次考古发掘的主要遗迹进行保护和展示。馆名由我国著名古陶瓷专家叶喆民先生题写。</span></p></a></p><p style=\"text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190707/1562494692975491.jpg\" title=\"1562494692975491.jpg\" alt=\"微信图片_20190707180641.jpg\"/></span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">宝丰汝窑博物馆于2014年7月开工建设，2017年10月19日建成开馆。博物馆为地下一层、地上二层框架结构，建筑面积6300平方米。博物馆陈列以“汝窑为魁”为主题，总展品1100余件。展示内容分为汝瓷之源、青瓷典范、汝窑遗绪三个部分。通过宝丰清凉寺汝官窑遗址出土文物与时代背景相结合，分别从釉色、造型、工艺等角度，展示汝窑自源起、兴盛、至臻、续燃、推崇等发展过程。临时展厅以“物宝源丰”为主题，展示宝丰悠久的历史文化。馆名由我国文物鉴定专家委员会副主任委员、古陶瓷研究专家耿宝昌先生题写。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562494709403344.jpg\" title=\"1562494709403344.jpg\" alt=\"微信图片_20190707180803.jpg\"/></span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">该遗址曾被评为七五期间全国十大考古新发现、二十世纪河南十项重大考古发现、2000年度全国十大考古新发现；2001年，被国务院公布为第五批全国重点文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190707/1562494733854787.jpg\" title=\"1562494733854787.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">长按识别关注 让旅行变得更简单</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><br/></span></p>',1,0,50,'1562494777','旅途掠影',6,12,25,77),(21,'郏县山陕会馆','/uploads/20190707/4aa32d996c36a646eb2dc395f7f558c7.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">郏县山陕会馆又称郏县山陕庙，位于平顶山市郏县城关镇西关大街西段，北侧紧邻洛（阳）界（首）公路，西南2公里处有广阔天地知青园景区，东南1.5公里处是郏县文庙景区，北500米有青龙湖，西北18公里处是三苏园文物景区和道教中顶莲花山。</span><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562495079107917.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">郏县山陕会馆创建于清康熙三十二年（1673年），是清代山陕商贾往来西东南北的驿站和洽谈生意、祀神招财、迎宾宴客的商会场所，是全国目前保存较好的会馆之一。座北面南，占地面积6300平方米，门临河道，其主要建筑有镇河照壁、庙门和戏楼迭建一体的门楼、钟楼、鼓楼、中殿（关公殿）、后殿（春秋楼）、堂楼等古建筑组成，气势恢宏，布局严整，木、石、砖雕精美，充分体现了山、陕、豫古代建筑综合风格，具有较高的历史、科学、艺术价值。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562495079994215.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">照壁，宽8米，高7米，厚1米。基座用质地坚硬、加工平整的红石大块砌筑，灰泥抹缝，密不透水。上部用青砖砌造，青瓦覆顶。壁阴正中砖雕砌筑圆池一个，池内镶嵌砖雕团龙，生动活泼，被时人称之为“镇河大将军”。门楼三间，中门供人出入，两次间为门卫及接待之所。二楼三间敞通，面北为古戏楼，砖雕楼脊，脊之正中，以砖雕麒麟驮宝瓶之造型，筑成高大的脊刹。戏楼两侧有钟楼、鼓楼。楼基高达4米，钟鼓楼周围各用12根圆木立柱将楼顶十字架梁承起。双层飞檐，斗拱端庄，浑厚朴实。山陕会馆的大门的门饰、砖雕、垂花结构精巧，与二楼的古戏楼构建一体，配以左右之钟鼓楼，使庙门更显得小巧玲珑，典雅幽静，古色古香。院内东西廊房各11间，中央空地阔大，可容观众千人观戏。北为中殿（关公殿）面阔5间。殿东侧尚有堂楼5间及附属栈舍。再往北去，为后大殿（春秋楼）。其建筑群具有鲜明的地域特征和时代特征，其木雕、砖雕、石刻技艺高超，具有很高的历史价值和文化艺术价值。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190707/1562495080220086.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">郏县山陕会馆建筑功能完备，围墙至今高大端正，中殿（关公殿）、后殿（春秋楼）、客栈楼馆，以及仓储马厩等一应俱全，尽显古代晋陕商人的聪明才智和经商理念，是山陕会馆中的典型建筑。2013年，被国务院公布为第七批全国重点文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190707/1562495153610141.jpg\" title=\"1562495153610141.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">长按识别关注 让旅行变得更简单</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><br/></span></p></a></p>',1,0,50,'1562495169','旅途掠影',9,12,20,128),(22,'宝丰酒传统酿造技艺','/uploads/20190708/503343e4a5b86f613e68bb00dfd8f356.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">宝丰酒传统酿造技艺于2008年6月，列入第二批国家级非物质文化遗产名录。类别是“传统技艺”，</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">宝丰酒已有4100多年的历史，它以其得天独厚的天然条件——优质粮食和泉水，开辟了酿酒业的先河，被列为中国17大名酒之一。其特征是以优质高粱为原料，大麦、小麦、豌豆混合制曲，采用“清蒸二次清”工艺，地缸发酵，陶坛贮陈，具有清香纯正、甘润爽口、回味悠长的独特风味，是我国清香型白酒的典型代表。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span><img src=\"/ueditor/php/upload/image/20190708/1562572078731774.png\" alt=\"\" title=\"\" width=\"534\" height=\"283\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; width: 534px; height: 262px;\"/></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">宝丰县春、夏、秋、冬四季分明，雨量均匀适度，既得天赐沃土，又有黍谷菁华，特产高粱、大麦、豌豆，皆为醇酒佳料。“名酒必清泉”，宝丰地处尧山东麓，滍、汝二水上流，泉水地下上涌，清澈透明，净爽甘冽，堪为酿酒源泉。这些丰厚的自然资源为宝丰酒的酿造提供了宝贵的物质基础。</span></a><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">宝丰酒酿造工艺从选料、粉碎、制曲，到酒的制成有近百道工序，主要分为选料、粉碎、制曲、酿造四大部分。</span></a><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">1.选料。精选颗粒饱满、里实皮薄，无杂质、无霉变、无虫蛀的高粱、大麦、小麦、豌豆。</span></a><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">2.粉碎。用石磨将高粱粉碎成4、6、8瓣，多糁少面为宜。大麦、小麦、豌豆分别粉碎，能通过1.2毫米标准筛孔的占55%~60%，不能通过的占40%~45%。</span></a><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">3.制曲。曲是酿酒的原动力，在酿酒过程中起着发酵、生香的关键作用。主要包括人工踩曲、入房养曲两个步骤。</span></a><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">4.酿造。主要包括堆集润料、入甄蒸粮、人池发酵、装甄蒸馏、看花截酒、贮陈老熟等环节。老熟后，经勾兑、品评和调味，口感达到甘润爽口、清香芬芳、回味悠长的特征后，即可上市出售。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><img src=\"/ueditor/php/upload/image/20190708/1562572079446024.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">宝丰有“千年酒都”之称，历史上一直是中国酿酒业的中心。宝丰境内千村飘香，万家立灶，“烟囱林立，酒旗似蓑”，有“过街醉人之说”。在古代酒税成为官府收入的大宗，</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">在酒坊集中之地专设酒务机关，并派高级官员任作酒监。当时汝州四县共设十酒务，宝丰独占七处。著名的理学创始人程颢曾任商酒务酒监多年，并为宝丰留下“酒务春风”的佳话。大诗人元好问就以“春风着人不觉醉，快卷更须三百杯”的诗句称颂宝丰名酒佳酿。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">2008年，该项目被列入第二批国家级非物质文化遗产名录。</span></a><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562572518707711.jpg\" title=\"1562572518707711.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">长按识别关注 让旅行变得更简单</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br/></span></a></p><p><br/></p>',1,1,50,'1562572606','旅途掠影',11,12,25,0),(23,'汝瓷烧制技艺','/uploads/20190708/b53eb905fd6def5c480e4a8a52d98a22.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">汝瓷因产于汝州而得名，汝州因汝瓷而名扬四海。汝瓷是五大名瓷（汝、官、钧、哥、定)之首，历史悠久，盛名于世，它是皇家镇宫珍宝，也是民间钟情的吉祥物。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">汝瓷制作从唐代中期开始，到北宋时最为鼎盛，宋代汝官瓷名闻全国，成为宋宫御用珍品，后因战乱其技艺流传于民间，延续至今。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562572692109109.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">据史料记载，汝瓷的传统烧造工艺要经过拣选、粉碎、制浆、成型、素烧、施釉和烧制等7道生产工序。&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: left;\"><span style=\"font-family: 宋体, SimSun;\">1.拣选。汝瓷生产主要用可塑性原料和非可塑性原料，除去杂质，拣选漂洗，烘干备用。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">2.粉碎。用人力、水力石碾、椎捣方法对拣选的原料进行加工粉碎。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">3.制浆。把粉碎好的原料制成泥浆。使用泥浆自然干燥排水法，使多余水分蒸发出去，达到泥料要求的适应水分。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">4.成型。用手动辘钟或机轧法拉坯成型。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">5.素烧。把阴干后的成形胎坯放入窑炉内，经过若干小时的除湿，封闭窑炉，让炉内温度至900℃左右，素烧完成。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">6.施釉。一般用浸釉法，某些厚釉色泽需二次施釉。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">7.烧制。烧制过程是汝瓷制造的核心技术.根据器形所需，将胎体用支钉、垫饼等支妥放稳，放入匣钵内，然后入窑烧制。传统的烧制主要有柴烧、煤烧两种方法。经过30多个小时的预热除湿、升温氧化和拉火还原等程序，使窑温至1200度左右，停火，直到温度适宜，即可出窑。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562572693612917.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">和其他名瓷相比，汝瓷在长期的演变和创新过程中，形成了鲜明的特色与特征。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">特征一，从现存的汝瓷精品来看，汝瓷制作具有四大特色，艺人称之为“制作技艺四绝”：一是配料绝，二是变色绝，三是裂纹绝，四是应光绝。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">特征二，汝瓷以釉色取胜，难见花纹装饰。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">特征三，汝瓷的造型精美别致。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">特征四，汝瓷因配料独特，具有很强的着色能力。即使釉层很薄，其色也会随光变幻。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">特征五，汝瓷重釉色，贵釉质，美质感；其内在的品质特征是胎质细腻、匀净。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">特色六，汝官窑是皇室直接控制并建立的瓷窑，十分保密，禁止民窑仿制；其造型、装饰、釉色等，均按宫廷要求设计烧制，带有浓厚的皇权色彩。这种特征，在其他瓷艺中也是绝无仅有的。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">2010年，该项目被列入第三批国家非物质文化遗产名录。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562572837504086.jpg\" title=\"1562572837504086.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,1,50,'1562572863','旅途掠影',1,12,27,0),(24,'妙善观音传说','/uploads/20190708/3fa7fbc681816f132e7bd00d2b18c353.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">妙善观音传说是世代相传的民间文学，它以位于宝丰县与新华区交界处的香山寺为中心，以周边的白雀寺、古父城遗址、三皇姑坟等为依据，形成了比较完整的传说体系，并在当地群众中广泛流传。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562572944839507.png\" alt=\"图片4\" title=\"图片4\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">相传春秋时期，宝丰古父城有个国君妙庄王，生有妙颜、妙音、妙善三个女儿，因为妙善为王后梦中孕育而生，所以被视为掌上明珠。妙善幼年就能吟诗作赋，不慕荣华，喜悦佛法，她厌恶王室的暴虐，同情宫女的遭遇，被宫女们尊称为“三皇姑”。三皇姑13岁时出落得亭亭玉立，庄王为了讨好邻国的国王，就将三女儿许配其太子，三皇姑宁死不从。庄王无奈，挑选了一个媒婆前去劝说，媒婆使出浑身解数都无济于事。庄王一怒之下把三皇姑囚禁于后宫，她一连几天水米不进，容颜憔悴，王后心疼女儿，悄悄放她出去逃生。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562572945842863.png\" alt=\"图片5\" title=\"图片5\" width=\"481\" height=\"264\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; width: 481px; height: 264px;\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">三皇姑先到白雀寺里侍佛，不久被庄王知晓，派人三番五次逼她回宫，她执意不回，庄王气极之下将寺院放火焚烧，王后因此哭得死去活来。其实三皇姑并未被烧死，她被尼姑们救出，藏到了火珠山（即香山）上。庄王焚烧白雀寺后，三皇姑活不见人，死不见尸，他忧虑成疾。八仙中的铁拐李化作凡人，装扮成郎中，给庄王诊病后说：“圣王的病必须用亲骨肉的一只手、一只眼做药引，方能治愈，否则即使神医，也无可奈何。”庄王的大女儿和二女儿不肯舍己救父就在庄王病入膏肓之时，门官通报：“三皇姑回来了，请求献眼、手为父治病。”后来庄王的病好了，他高兴地问妙善需要什么，她说：“我不要江山也不要财富，只求父王在火珠山上修座寺庙，女儿要终身侍佛。”庄王满足了女儿的要求，大兴土木，建起了香山寺。寺院落成后，庄王要为三皇姑在大殿塑像，雕塑匠请示庄王：“塑成什么样?”庄王道：“全手全眼。”雕塑匠却误听为“千手千眼”，于是就雕塑成一尊千手千眼的三皇姑观世音雕塑，这即是香山寺和干手千眼观音菩萨的来历。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">妙善传说融合了儒家和佛教思想，集中体现了“孝”和“善”的道德思想，成为优秀的民族传统文化，深受民众崇拜，得到了深远而广泛的传播。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">2011年，该项目被列入第三批河南省省级非物质文化遗产名录<span style=\"font-family: 宋体, SimSun;\">。</span></span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562573087955875.jpg\" title=\"1562573087955875.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,0,50,'1562573112','旅途掠影',0,12,25,0),(25,'牛郎织女传说','/uploads/20190708/1199ca09b8f8fa2fe74544a201a04dc5.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">“牛郎织女”作为我国四大经典神话爱情传说之一，在民间代代相传，妇孺皆知，是中华民族独特传统文化中一朵耀眼的奇葩，在中华民族爱情文化宝库中占有十分重要的位置。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">牛郎织女的传说最早见于《诗经·大东》：“跂彼织女”“睆彼牵牛”的记载。《古诗十九首·迢迢牵牛星》已称牛郎织女为夫妻。应劭《风俗通》逸文：“织女七夕当渡河，使鹊为桥，相传七日鹊首无故皆髡，因为梁以渡织女也。”而牛郎织女传说在鲁山县辛集乡孙义村更是老少皆知，广为流传，那里至今还保存有牛郎洞和织女潭。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562573158684709.png\" alt=\"牛\" title=\"牛\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; width: 600px; height: 509px;\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">“牛郎织女”神话传说遗存丰富，有大量牛郎织女传说的民俗和民风，其中有民谣、民歌，及其祭祀遗物、牛郎洞、九女潭沟、织女潭、天河、九女庙、天爷庙等遗址和七夕会址。鲁山至今保留着牛崇拜的习俗，人们把牛看作家庭一员。鲁山有40余处带“牛”字的地名，鲁山的牛市也十分有名。一年一度的七夕庙会，会址就在牛郎故里孙义村（现移至乡政府所在地辛集街），这既是鲁山夏季最热闹的一次大会，也是牛郎织女传说起源于鲁山的最好佐证。鲁山有植柞养蚕的历史，传鲁山的织绸技术就是织女下凡后传授的，鲁山绸被誉为“织女织”“仙女织”。鲁山人还喜种“九姑娘花”。九姑娘花即油菜花，传为织女从天上带下来让人间度春荒的。鲁山民众还多在院落里种植葡萄，传牛郎织女七夕鹊桥相会时，人们躲在葡萄架下可以偷听到他们的喁喁私语。形成了一个相对比较完整的具有地方特色的文化体系。鲁山县作为牛郎故里和牛郎织女文化传说的起源地，被中国民间文艺家协会命名为“牛郎织女文化之乡”。牛郎织女文化，是鲁山历史长河中一块璀璨瑰宝，是中华民族一笔丰厚的文化遗产。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">2011年，该项目被列入第三批河南省省级非物质文化遗产名录。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562573159990502.png\" alt=\"图片6\" title=\"图片6\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562573277151068.jpg\" title=\"1562573277151068.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,0,50,'1562573328','旅途掠影',1,12,22,42),(26,'白朗起义传说','/uploads/20190708/9ca539a2dcc15c237d1d38f37f24f2d6.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">白朗起义传说于2015年9月，列入第四批河南省非物质文化遗产名录。类别是“民间文学”，</span></a></p><p><span style=\"font-family: 宋体, SimSun;\">白朗（1873-1914），字明心，宝丰县张八桥镇大刘村人，汉族。1910年，豫西遭受灾荒，阶级矛盾激化，民变四起。白朗被地主诬陷入狱，几经波折，走投无路，拉起杆子，响应武昌起义，占据姚店铺，打开刘集，开仓放粮，设伏交马岭，劫击宝丰县官张礼堂。1913年，白朗与牛天祥、秦椒红南下，进入母猪峡活动。与革命党的反袁力量汇合，攻克新安店、光州，以“中原抚汉讨袁军”的名义发布文告。“二次革命”失败，白朗跳出包围，攻占老河口，得到孙中山、黄兴等人的支持，义军人数有3万多人。在革命党人的指导下，义军先后攻克荆紫关、商南具、乾县、凤翔、天水、洮州等，因种种原因，义军入川不成，只得东归，冲破干难万险，回到宝丰。1914年7月，各路军云集豫西，总统府悬赏10万元捉拿白朗。困守虎狼爬岭的义军弹尽粮绝，白朗中流弹而死，终年41岁。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">作为一位民间英雄，白朗的传说在宝丰民间广为流传，它反映了豫西底层人民反帝反封建的浩然之气，展示的是一幅民国年间中原英雄辈出的画卷。百余年来，白朗起义以故事。歌谣、隐语等多种形态呈现，以打“莲花落”、说书、戏曲段子为常见，早已渗透到人们的言行里，其精神核心正是被中国人视为最高道德理想和精神情感的“义”字，这种大义大举影响了一代又一代人，丰富了民国社会史、政治史、军事史、文化史。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562573387339954.png\" alt=\"白狼\" title=\"白狼\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">2014年，该项目被列入第四批河南省省级非物质文化遗产名录。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190708/1562573497632812.jpg\" title=\"1562573497632812.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,0,50,'1562573530','旅途掠影',3,12,25,76),(27,'轧琴','/uploads/20190708/9697c4040fd334029aaaec5aa0c2bc2b.jpg','<p><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">轧琴，又名“亚筝”“轧筝”，俗名“滚琴”，据1988年全国民族民间器乐普查中证实，轧琴是我国最早出现的拉弦乐器之一，《旧唐书·音乐志》中有关于它的史料记载，它的形成时间应在隋唐初期，距今至少有1500多年的历史。</span></p><p><span style=\"font-family: 宋体, SimSun;\">轧琴演奏技法是马尾弓和琴身协调滚动和翻动，演奏弓法有平弓子、击弓子、擞弓子等。轧琴是以五声音阶定弦。它的音色属于哑乐，古朴大方。演奏者左手托琴身之持琴孔，右手持弓，不用指法，来回翻动或滚动，演奏出美妙动听的乐曲。演出过程中，演奏者可站、可坐，还可在行进中演奏。&nbsp;&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562573588733961.png\" alt=\"轧琴\" title=\"轧琴\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; width: 671px; height: 272px;\"/><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">轧琴由琴底、琴梆、琴头等10个部分组成。马尾制弓，蚕丝制弦。琴身长66.5厘米，琴头宽13.5厘米，琴尾宽9.5厘米。琴箱是半管状，张弦9根。弓长40厘米。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">轧琴演奏的主要曲目有古曲、民间器乐曲和河南地方的戏曲、曲牌，如大调曲子和小调曲子等。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">舞钢市杨庄乡袁门村郭氏家族传下来的轧琴，则是郭九洲的先祖经300多年代代相传至今的，历史悠久。其先祖为路歧艺人（即流动的艺人），故在此办起了家庭乐坊，使用曲胡、轧琴、古筝、三弦、四弦、二弯等乐器进行演出活动。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span><img src=\"/ueditor/php/upload/image/20190708/1562573590295507.png\" alt=\"轧琴1\" title=\"轧琴1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">郭氏家族组建的家庭乐坊明末清初时由家族成员8人组成，在其演变过程中，最多时可达20人，最少时也有6人。新中国成立后，经郭梅欣老艺人的多年潜心研究，艺术上得到了很大发展。1958年，他有幸参加了河南省许昌地区民间音乐调演，得到了专家们的一致好评。“文革”期间，此种表演方式曾经中断过一段时间，至党的十一届三中全会后，轧琴技艺又迎来了艺术上的春天。1984年3月，他们参加河南省第五届民间音乐舞蹈调演，获得民间挖掘奖和优胜奖。郭氏轧琴传人郭九洲根据当前形势在原有的曲目基础上，自编自演了许多时兴小戏及反映时代特征的新曲目，在演出中深入人心，良好的演出效果，也为发扬轧琴这一古老的民间乐器做出了贡献。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">2007年，该项目被列入第一批河南省省级非物质文化遗产名录。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562573746814439.jpg\" title=\"1562573746814439.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,0,50,'1562573766','旅途掠影',1,12,26,103),(28,'霸王鞭','/uploads/20190708/fdc1eca970751975b749cf7c2ca9af71.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">霸王鞭是河南省叶县农村广泛流传的民间舞蹈，因使用的道具“鞭”形似楚霸王使用的兵器—“鞭”而得名。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">据叶县仙台镇董寨村的老艺人讲，在当地农民又叫它为“响杆”，因为在表演时它能发出清脆的响声而得名。相传秦末楚汉相争，项羽与刘邦击掌相约，先人咸阳为王，后项羽一路过关斩将，所向披靡，每攻下一座城池，项羽便站在马上挥舞马鞭，高歌竞舞。舞至酣时，命士卒折木为鞭再舞，共同欢庆胜利，其歌舞的欢乐之情，吸引和感染了当地的百姓，人们纷纷效仿，结果这种欢庆胜利的即兴舞蹈形式就由军营传播到民间，逐步演变为一种传统舞蹈节目。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span><img src=\"/ueditor/php/upload/image/20190708/1562573818451152.png\" alt=\"图片3\" title=\"图片3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">每逢节日及农闲时节，当地农民们便自发组织起来耍动起霸王鞭以自娱自乐。霸王鞭的伴奏以大起板为基本素材，吸收融和了曲剧音乐的精华，使伴奏曲更加规范、完整、丰富。1958年以前，它还基本保留着传统的表演形式，隶属于故事戏（高跷上的曲子戏），人员由老生、老旦、青衣、两个小旦、丑、和尚等七八个人组成，一般戏曲化妆，和尚穿黄马褂，鼻子上面白蝉，负责打鞭，一个小旦耍盘，所有人均脚踩高跷，队形走“过街乐”“剪子股”等。后来改为平地舞，霸王鞭的舞蹈者右手执霸王鞭，左手拍击，并且有节奏地打击身体的各个部位，由竹竿碰击铜钱发出清脆的声音；另一舞蹈者右手拿扇，左手端果盘，伴随着音乐有节奏地扭起秧歌，随着节奏变换队形，显得庄重活泼，变换自如，形成了音乐节奏感强、动作幅度大、舞蹈热烈奔放、铿锵有力的独特风格，反映出人民群众喜庆丰收的欢乐情景。现在霸王鞭表演可分为平地舞和高晓舞两种。由于舞蹈取材于农村，贴近群众生活，能形象地反映出农民庆丰收自娱自乐的场面，故能在广大人民群众中深深扎根。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">2011年，该项目被列入第三批河南省省级非物质文化遗产名录。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562573883918915.jpg\" title=\"1562573883918915.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>',1,0,50,'1562573915','旅途掠影',1,12,29,58),(29,'民间剪纸（李福才剪纸）','/uploads/20190708/8bdc0801aa8e080d7698592f357b6316.png','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">李福才是鲁山县下汤街一位农民，虽然他目不识丁，但靠自学学习剪纸艺术，对剪纸技法有很深的造诣与天赋，不用构思，随心所欲，刀法稳、准、巧，整个剪纸过程皆为手工制作，作品形象鲜明夸张，简洁优美，线条粗细相宜，图案栩栩如生，且每个作品皆独一无二，展示出农村质朴的生活场景，深受群众的喜爱，被当地老百姓称为“神剪”。1993年，中央电视台来鲁山专题摄制李福才剪纸作品400余幅，在《神州风采》栏目播出，同年，他的《百鸟朝凤》《十二生肖》《老鼠嫁妮》等作品在中国首届民族民间剪纸大赛中榜上有名，《中国文化报》专题介绍李福才剪纸。李福才被中国民间艺术家协会聘为会员，河南省民间艺术家协会授予他“神剪李福才”称号。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span><img src=\"/ueditor/php/upload/image/20190708/1562573972257783.png\" alt=\"剪纸\" title=\"剪纸\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span><img src=\"/ueditor/php/upload/image/20190708/1562573973747720.png\" alt=\"剪纸1\" title=\"剪纸1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a><br/><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">李福才剪纸的艺术价值主要表现在以下四个方面：1.创作风格古朴，乡土气息浓厚；2.不用构图，搭纸即剪，结构布局恰如其分；3.同一题材表现手法各异，无同一模式；4.临场创作思路极其敏捷，题一叫出，顺手成章，题意确切，形象逼真。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">2007年，该项目被列入第一批河南省省级非物质文化遗产名录。</span><span style=\"font-family: 宋体, SimSun;\"><br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190708/1562574043937662.jpg\" title=\"1562574043937662.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,0,50,'1562574068','旅途掠影',3,12,22,35);
INSERT INTO `ddsc_culture` VALUES (30,'父城遗址','/uploads/20190708/73835e1cd3586d5e2429798146999219.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">父城遗址位于平顶山市宝丰县城东20公里李庄乡古城、马王庄、杨庄村等，东周、汉，父城商周时为应国地，春秋归楚为“城父邑”，是楚国北方之边陲重镇，楚太子建曾镇守于此。秦为父城县。东汉著名军事家“大树将军”冯异生于城内，葬于父城东南之龙山脚下。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562574209451549.jpg\" title=\"1562574209451549.jpg\" alt=\"微信图片_20190708161242.jpg\"/>&nbsp;&nbsp;<br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a><br/><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">父城遗址系商周至南北朝时期古城址，城址分内外二城。外城呈长方形、四面城垣痕迹隐约可见，东西长1750米，南北宽1250米，全城周长6公里，面积约220万平方米，城墙基宽10余米，残高1米。内城俗称“紫禁城”，位于外城内西北隅，为长方形，城台高3米，内城面积约40万平方米。城址内遗物颇丰，春秋绳纹陶片及汉代板瓦俯拾皆是。城址内马庄、古城等村曾先后出土春秋及战国时期青铜壶、铜鉴等。附近之南牛庄、小谢庄发现春秋战国墓葬。</span></p></a></p><p style=\"text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562574233492050.jpg\" title=\"1562574233492050.jpg\" alt=\"微信图片_20190708161324.jpg\"/></span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">父城遗址形制规整，分布面积较大，遗迹丰富，是一座布局清晰、保存完好的古城址，为研究楚国的疆域、城防、社会、人文以及观音文化起源和商周、秦汉至南北朝时期的城池设立及城市发展等，都具有极其重要的意义。2013年，被国务院公布为第七批全国重点文物保护单位。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562574253195677.jpg\" title=\"1562574253195677.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>',1,0,50,'1562574275','旅途掠影',1,12,25,80),(31,'小李庄遗址','/uploads/20190708/0c267ac57d505350b124b33742c06a4e.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">小李庄遗址位于平顶山市宝丰县杨庄镇小李庄村，新石器时代至南北朝。该遗址呈东西走向，西高东低，应河由西向东自遗址北部穿过。遗址东西长约1250米，南北宽约800米，面积约100万平方米。文化遗迹从早到晚可分为仰韶、二里头、商、西周、汉代、南北朝等六个时期，尤以仰韶与商代最为丰富。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562574316634997.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">2008年6月—12月，河南省文物考古研究所、平顶山市文物管理局和宝丰县文物管理局联合对该遗址南水北调工程渠线进行了文物考古发掘。发现两处以石块为柱础的较大型地面式建筑的长方形房基，以及房基附近集中埋藏在一起而且排列有序的泥质红陶钵、夹砂红陶罐及陶瓮，出土遗物除陶器外，另有瓷、铜、铁、石、骨、角、蚌器及铜钱（货币）、骨笄（装饰品）等。更为重要的是，在商代文化层内还发现了一片被凿而灼烧过的卜骨。</span></a></p><p></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562574317576823.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">小李庄遗址属于仰韶文化、二里头文化及商周聚落遗址。发掘出土的大量遗迹、遗物，在一定程度上反映了当时的政治、经济、文化状况，为研究该区域的历史文化提供了宝贵的实物资料。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562574317330231.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">小李庄遗址2008年在第三次全国文物普查工作中被发现；2013年，被国务院公布为第七批全国重点文物保护单位。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562574439699940.jpg\" title=\"1562574439699940.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>',1,0,50,'1562574454','旅途掠影',1,12,25,127),(32,'临沣寨','/uploads/20190708/e130388dbd88c511ca3f9841afa7b163.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">临沣寨位于平顶山市郏县东南13公里处的堂街镇朱洼行政村。创建于明代，重建于清代。清朝同治元年（公元1862年）由议叙监知县事朱紫峰（本村人）倡建，占地7公顷，筑寨石料全部为紫红色石材，寨墙平面呈椭圆形，周长1100米，高6.6米，上有哨楼五座，垛子800个。按八卦的乾、坤、巽三个方位，分别设西北门“临沣”，西南门“来曛”，东南门“溥滨”。临沣寨因西北濒临沣溪而得名。三个寨门均装有两扇10厘米厚铁皮镶裹的榆木大门，现在尚能自由开关。西北门两扇木板门铁皮上錾刻的“同治元年”，“嵗在壬戌”字样清晰可识。寨外有13米宽的护寨河绕寨一周，河内终年绿水长流。护寨河外围是千亩芦苇，百亩竹园。每到盛夏，清清的流水，碧绿的苇竹，五彩的水鸟和雄伟壮观的红石古寨相互映衬。有“中原第一红石古寨”之称。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562574485395710.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">临沣寨内寨内主要街道两横三纵五道，呈“井”字形交叉，寨内两横大街在这里由于历史原因分别叫做北大街和南大街，实为“北横大街”、“南横大街”。北横大街长300米，宽7—9米。南横大街长250米，宽8—10米。其余街道5—8米宽。民居多为砖木脊坡式瓦房或楼房，现有明代民居三间一院，清代建筑100余栋，400多间。五脊六兽硬山顶，青砖高浮雕花脊，房坡覆盖灰色小板瓦，属于典型的中原民居风格。这些建筑规模宏大，布局严谨，类型齐全，从明代至今，在时代链上没有缺环，且各个时期均有绝对纪年的规范建筑。尤其是明清建筑，集中地存在于一个村庄之中，这在全国也是极为罕见的，是研究明代建筑向清代建筑过度提供了极好的实物资料，具有较高的建筑史学价值。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562574485593185.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">清道光年间，朱氏三兄弟宅院，朱紫贵宅，建于道光十五年（公元1835年），占地面积1322平方米；朱振南宅，建于道光十一年（公元1831年），占地面积1762平方米；朱紫锋宅，建于道光二十九年（公元1849年）占地面积2516平方米。朱紫贵宅院门临南大街，座北朝南，在寨内西部；朱紫贵宅居中，东邻是朱紫锋宅院，座北朝南，门临南大街；朱镇南宅院在寨内东部，门临北大街，座南朝北。又以朱紫峰私宅为最，称为“汝河南岸第一府”。国家文物专家罗哲文说：“临沣寨填补了古建筑在村寨方面的空白。”这里有古寨墙、古寨河以及古建筑朱紫峰老宅、朱紫贵老宅、朱振南老宅、关帝庙、朱家祠堂遗址、清代枪炮局、朱家酒院等125处400多间古建筑。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">这些古建筑是典型的明清四合院，保存相当完整，所有的古建筑都以中轴线建造。虽经近二百年的风雨沧桑仍坚固如初，令专家称绝，具有较高的科学、艺术价值。2005年公布为“中国历史文化名村”。2013年，被国务院公布为第七批全国重点文物保护单位。</span></p></a></p><p style=\"text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562574570410481.jpg\" title=\"1562574570410481.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p></a></p><p style=\"text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p></a></p>',1,1,50,'1562574584','旅途掠影',8,12,20,88),(33,'望城岗冶铁遗址','/uploads/20190708/f85c6e96f350bcd586863173ded4b3ed.png','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">望城岗冶铁遗址位于鲁山县城南与张店乡望城岗村北之间。现地表为耕地，遗址保存完好，地表散存着大量的矿石粉、青石、木炭、砂子、炉渣等，器物残范、炉底等冶铁、铸铁遗物随处可见，东西长约1200米，南北宽400米。该遗址自西汉至东汉时期，历时300余年，面积达36万平方米，文化层堆积厚2～5米，是河南省目前已发现的最大的汉代冶铁遗址。2006年，被国务院公布为第六批全国重点文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562574637406955.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">该遗址考古发掘，遗址中部及偏西部位，发现有成排和单一约一间房基大小的烧结炉底，出土有炉渣、炉壁、鼓风残块、泥模范块、矿石块、矿石粉、铁块、煤渣、木炭渣、残砖、陶范及陶器，五铢钱等汉代遗物。另有汉代墓葬、陶窑、水井、水渠、贮水池等遗迹。并发现刻有篆文“阳一”、“河口”、“六年”等陶范。2000年11月至2001年元月，河南省文物考古研究所在当地文物部门的协助下，对其进行考古发掘工作，揭露面积2000多平方米，发现完整宋代烧瓦窑一座，汉代完整窑二座，汉代水井五座，汉代冶铁高炉一座。还发现一条长40米、宽2米、深3米的人工水渠，冶铁风管、铁叉、铁犁铧等大量遗物。整个遗址年限是从西汉中期到东汉末期200多年间从未中断过冶铁。发掘出一巨型冶铁炉，炉缸内径东西长4米，南北宽3米，有出铁、排渣、鼓风、填料、供水、冷却等系统。冶铁炉可连续冶炼，同时出铁、出渣，需150人连续轮换作业。此炉是我国迄今为止所发现规模最大的汉代冶铁炉，它代表了汉代冶铁技术的最高水平。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562574642520890.png\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">这座汉代冶铁高炉目前占有四项全国第一：一是国内也是世界上发现的最大、最完整、最特别的椭圆高炉。二是出铁口与出渣口分开，可以大大提高工作效率。三是利用自然河流作动力向高炉内鼓风，可节省大量劳力。四是首次发现了填料的工作台，这种填料工作台科学、合理、以前从未发现。</span></p></a></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562574764344083.jpg\" title=\"1562574764344083.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\">长按识别关注 让旅行变得更简单</p>',1,0,50,'1562574783','旅途掠影',4,12,22,41),(34,'元次山碑','/uploads/20190708/509012901b796c9921fc3db1cae433be.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">元次山碑位于平顶山市鲁山县高中院内。为唐代刺史、诗人元结（719～772年）墓碑。碑铭原立于鲁山县城北青条岭泉坡元结墓地，明万历四十二年（1614年）将碑移于此并建保护亭。碑亭为方形攒尖顶灰筒瓦顶建筑，元次山碑位于亭内。元次山碑系唐代大书法家颜真卿于大历七年（公元772年）63岁时为好友元结亲手撰写并书丹的悼文。全文以寸半见方的楷书写就，字迹浑厚雄健，遒劲秀拔，气势磅礴，充溢着宁折不弯的浩然正气。是颜体的代表作品之一。2006年，被国务院公布为全国重点文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562574908909475.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">元次山碑为青石雕刻而成，碑额缺失。碑身为长方体，高1.9米，宽0.95，厚0.28米，右下角残缺。碑文四面环刻，也称四面碑，正背两面各17行，两侧4行，行字数33—35不等，全文1383字。首行题： &nbsp;&nbsp;唐故容州都督兼御史中丞本管经略元君表墓铭并序”。记述了元结的世系、生平事迹及死葬年月，多处表达了颜真卿对元结的敬慕之情。碑文已收入《颜鲁公论文集》。目前碑文大部分完好，缺203字。碑文全篇气势贯通，纵横飞动，是一件难得的书法珍品。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562574915465387.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">颜真卿是我国唐代中期的书法大师。他承袭了东晋王羲之的书法传统，吸收了唐初欧阳询、虞世南、褚遂良、薛稷四大书法家的艺术精华，在楷书和行书上，创造出自己独特的书法艺术。后人之所以把颜体作为学习的书体，主要是他突破了前人的规范，在笔法、结构、布局、墨法等方面，超尘脱俗，独树一帜，创造出出类拔萃的独特风格。元结碑的书法特点、字体结构为横细竖粗，多用圆笔，转笔不折而转，字多用弧形，左右的竖画，粗重外弓，向内回抱，上密下粗，蚕头燕尾，独具风格的颜体已经形成。元次山碑碑文是颜书达到炉火纯青地步的作品之一，向来被视为临习颜书之范本。&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562574974873688.jpg\" title=\"1562574974873688.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单&nbsp;<br/></span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,0,50,'1562574993','旅途掠影',6,12,22,0),(35,'文集遗址','/uploads/20190708/8f8eea02dfea5c58d8d52b91dc65ea5c.jpg','<p></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">文集遗址位于平顶山市叶县常村乡文集自然村西南。遗址大体处于澧河北岸的二级台地上，总面积约为60万平方米。2006年5月至2008年10月，河南省文物考古研究所会同平顶山市文物局、叶县文化局组建考古队，对叶县文集遗址进行了较长时间与较大规模的考古发掘。发掘面积达11300平方米。共发现各类遗迹1470多个，有建筑基址、房基、灰坑、窖藏坑、灰沟、道路、地灶、火池、砖池、水井、地道、墓葬。其中，最为重要的遗迹绝大多数都位于金代至元代初年的文化层之间。文集遗址是一处自唐代延续至元代的，在金代发展为较大规模的民间商品流通、交易场所——集镇一类的遗存。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562575033727496.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">遗址中较为清晰而明确的地层关系和丰富的遗迹、遗物，必将增进我们对中原地区金代瓷器的认识和了解，为研究这一地区唐代至明代的历史文化面貌，尤其是金、元时期民间的经济贸易往来、文化交流、社会生活诸方面的发展状况提供一批珍贵资料。另外，在铜钱币窖藏坑内发现的南宋钱币，充分反映了当时南宋王朝与金朝在政治上相互对峙，在经济文化方面相互交流的社会关系史。2013年，被国务院公布为第七批全国重点文物保护单位。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562575097311466.jpg\" title=\"1562575097311466.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></a></p><p></p>',1,0,50,'1562575111','旅途掠影',4,12,29,62),(36,'叶邑故城','/uploads/20190708/3c6b9767a64e57265f95c5474d687299.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">叶邑故城位于平顶山市叶县旧县乡旧县村。北依澧水，东邻烧车河。城内街道、城门、城墙、寺庙等遗址大部分尚保存。城墙周长约9公里。最高约5.5米。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562575154733871.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">叶邑故城春秋时曾为楚国政治家、军事家叶公沈诸梁的封地，叶公，姓沈，名诸梁，字子高，鲁昭公十八年（公元前524年）， 被封为叶县尹，号叶公。叶公被尊为叶姓始祖。叶公尹叶期间修水利、劝农桑。使叶邑地丰民强，成为楚国四座大城之一。今叶县旧县乡尚存东、西陂遗址和叶公墓。</span></a></p><p><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562575154542947.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">叶邑故城地上、地下文化遗存丰富，在古城周围，尚有古冢30-40座，近的临近城池，远的达 10余公里；现存部分有的封土较厚，大的高约15米，占地超过10亩。根据已发掘的Ｍ1－Ｍ3出土的器物包括青铜器、木器、陶器、玉器、铅器等，其中不乏国内精品，墓主级别较高，均为楚国墓。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562575155216288.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">叶邑故城曾于鲁成公十五年(公元前576年) 至鲁昭公十八年（公元前524年）作为许国的国都，对叶县旧县四号墓的发掘证实这一点，四号墓为春秋时期许国国君许灵公之墓。出土了大量精美的青铜乐器和兵器。其中青铜编钟的“九件钮钟八件鎛钟二十件甬钟”的组合形式为全国唯一的一套。这套编钟一反古乐的五音规范,包含了七个音阶,这对研究我国古代音乐发展史有着重要价值。填补了我国东周时期考古学的空白。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562575155754783.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">叶县地处南北文化的结合部，叶邑古城对研究春秋战国时期的历史、政治、经济、文化、农田水利建设和民间风俗习惯等各个方面都具有较高的价值。2006年，被国务院公布为第六批全国重点文物保护单位。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562575320253500.jpg\" title=\"1562575320253500.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p>',1,0,50,'1562575339','旅途掠影',7,12,29,129),(37,'蒲城店遗址','/uploads/20190709/23dc04f4feae9d733b657211297dad81.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">蒲城店遗址位于平顶山市卫东区蒲城街道办事处蒲城店村北部，南北长约500米，东西宽约1040米，遗址面积20多万平方米，文化遗存十分丰富。蒲城店遗址是一处包括从新石器时代龙山文化、夏代二里头文化早期、西周、春秋、战国、汉、宋、明各个时期连续使用的大型遗址，以新石器时代龙山文化和夏代二里头文化早期遗址为主，又有两周、汉、宋、明各个时期的遗存，发现有房基、壕沟、灰坑、墓葬、水井灶等遗迹。它对于研究新石器时代河南龙山文化和夏代二里头文化具有非常重要的历史文化价值和学术价值。2006年，被国务院公布为第六批全国重点文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562640681395542.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">新石器时代晚期龙山文化。主要发现的遗迹以龙山文化的城墙和灰坑为主，龙山城址位于遗址的东北部岗地上，大致呈东西向长方形，现存东、西、南三面城墙，北面是湛河故道(北墙可能被河水冲毁)，西墙(含城壕)残长124米，南墙中东部向北转折后又向东延伸，南墙全长246米。城墙底部宽7—9米，残高约3米，夯层厚约0.2米，现存城墙由主体墙和两面护坡组成，墙外紧接宽而深的城壕。该城址的时代为龙山文化晚期。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562640681373748.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">夏代二里头文化早期。主要发现的遗迹为城墙和房基，城址位于遗址西南部，保存较好，略呈东西向长方形。城址(含城壕)东西长约260米、南北宽204米。筑城时特意先把拟建城墙之处的原有杂土挖出，再填以较纯净的土并经夯打以使城墙地基坚实。房基遗址十分集中，在不到4000平方米的区域内发现了二十多处的房基，有一间、两间、三间和多间，最多为一排七间，每间的长度大约在2—3米之间。从一处房子的房间的数量看，这一时期正处在一个从群居到一家一户生活的过渡时期。该城址的年代大致为二里头文化早期，是夏代一座军事重镇。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562640681106189.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">蒲城店遗址龙山文化晚期城址在地理位置上是中原地区最靠南的城址之一。两座古城的形制、城垣与城壕的结构与修筑方法不尽相同，为研究中国早期城址发展史及古代文明起源提供了新的资料<span style=\"font-family: 宋体, SimSun;\">。</span></span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190709/1562640785998466.jpg\" title=\"1562640785998466.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,0,50,'1562640813','旅途掠影',0,12,30,0),(38,'应国墓地','/uploads/20190709/cdf55d0e24288bf164b7e191e6873615.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">应国墓地位于平顶山市城乡一体化示范区西部北滍村西边的一道南北向的土岭——滍阳岭上。应国是两周时期中原地区的一个姬姓诸侯国，其地望大致在今沙河以北至汝河之南的大部分地区，其中心区域在今河南省平顶山市一带，包括平顶山市区、宝丰县、郏县、襄城县、叶县等。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562640951167203.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">应国墓地是一处以西周早期至春秋早期应国贵族的墓葬区，同时也包括春秋中期至战国中晚期楚国贵族的墓地，其上还公布有西汉与东汉时期的一些平民墓葬。应国墓地自西周早期开始使用，历经西周、春秋、战国、西汉，直至东汉初期，几乎是不间断地前后纵跨上千年。1986年—2007年，河南省文物考古研究所与平顶山市文物局联合对应国墓地进行了长达二十多年的考古发掘工作。共发掘墓葬400余座，出土铜、铁、铅、锡、金、陶、骨、玉石、贝蚌、木等各类文物达万余件。尤以铜器、玉器为大宗。其中一号墓中出土的玉鹰，成为平顶山市的市徽标志，而鹰城成为平顶山市的别名。2006年，被国务院公布为第六批全国重点文物保护单位。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562640951968244.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">应国墓地的墓葬大致可分为两大部分，即两周时期的贵族墓和两汉时期的平民墓。其中两周时期的墓葬，自南向北依次排列，其入葬年代也相应地从早到晚逐渐递进。墓葬之间秩序井然，极少有打破关系。墓地南半部分是应国墓葬区，北半部分是楚国墓葬区，两个区域之间相隔有一定的距离。单就应国墓葬区来看，显然应是由冢人进行统一管理的公墓区。墓葬大都是长方形竖穴土坑墓，只有少数大型的应国墓是带有墓道的甲字形墓。葬具一般是木质单棺单椁。随葬器物中大多数是青铜器和玉石器，部分铜礼器与个别铜兵器上铸有铭文，对研究应国与楚国的历史文化以及我国古代礼制的发展和演变大有裨益。据随葬器物分折，这些墓葬的墓主人大都是高级贵族或低级贵族。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562640951239666.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">至于两汉时期的墓葬，则是遍布于整道土岭之上，而且经常发生打破两周时期墓葬的现象。多半为砖室墓。有单室墓和多室墓之分。空心砖上大都有模印几何形图案，个别砖上或有模印有“亭长”等人物形象或车马出行图象。少数小砖券顶墓设有石门，石门及其门框与门楣上刻有青龙、白虎、朱雀或龙食鱼等图象。随葬器物一般都是日常生活所用陶质器皿和一些模型明器。据随葬器物分折，这批墓葬的墓主人一般都是平民。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190709/1562641086442514.jpg\" title=\"1562641086442514.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>',1,1,50,'1562640874','旅途掠影',7,12,131,0),(39,'虎头山新四军烈士陵园','/uploads/20190709/ef9ad085a4d9ef8845451d2a10974db1.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">虎头山新四军烈士陵园位于平顶山市舞钢市尹集镇苇子园村虎头山上，陵园南北长350米，东西宽300米。由舞钢市人民政府创建于1992年。主要建筑有时任军委副主席张万年题字“虎头山革命烈士陵园”。陵园内有十七座新四军烈士墓和其他烈士墓碑，战斗机一架，大炮两门。广场前有十五米高花岗岩雕塑一组。雕塑为军旗飘扬下的新四军战士手持钢枪，手捧军号正在奏响战斗号角。战士神采飞扬气势雄壮，威震敌胆，喊杀震天，响彻山谷。它记载了1945年2月新四军黄林司令员率领的河南挺进兵团38团一营300多名官兵在嵖岈山西部虎头山地区与日寇展开激烈的阻击战。战斗中我军迅速抢占制高点虎头山，打退敌人七次进攻，阵地上的白雪被鲜血染红。在敌人第八次冲锋时终因寡不敌众我军在击毙、击伤日军180多人的情况下，壮烈牺牲20多人，其余官兵迅速撤退进入嵖岈山腹地。其中有十七名烈士被埋葬在虎头山上。该陵园保存较好，对于研究新四军在中原嵖岈山地区的战斗历史提供了实物资料具有重要的历史价值和军事价值。&nbsp;虎头山新四军烈士陵园作为革命文物，它承载着革命先辈们许多可歌可泣的动人事迹，承载着他们英勇奋斗，不怕牺牲、百折不挠的革命传统、革命精神，是我们党和国家、人民永远的宝贵财富。所以保护好研究好、利用好这些革命文物，对于我们坚定文化自信，培育和弘扬社会主义主义核心价值观，不断夯实中国特色社会主义的思想道德基础，具有重要意义。2016年，被省民政府公布为第七批省级文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641173756049.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">虎头山新四军烈士陵园由舞钢市人民政府承建。第一期工程始建于1992年，完成于2002年。虎头山新四军烈士陵园自建成以来先后被舞钢市人民政府、平顶山市人民政府和团省委、河南省人民政府命名为各级爱国主义教育基地，青少年爱国主义教育基地和国防教育基地，是目前平顶山地区唯一一处省级国防教育基地。近年来，周边地区党团组织、解放军和社会各界前来凭吊、瞻仰和接受教育。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641216623755.jpg\" title=\"1562641216623755.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">长按识别关注 让旅行变得更简单</span></p></a></p>',1,0,50,'1562641228','旅途掠影',3,12,26,101),(40,'石漫滩水库大坝旧址','/uploads/20190709/38488fc82e13863c25ec6b436e1ff3b6.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">石漫滩水库大坝旧址位于平顶山市舞钢市武功乡罗湾村，建于1951年。是我国解放后在淮河流域建造的第一座大型水利工程，水库于1975年8月特大洪水溃堤，造成重大人员伤亡。为纪念该工程造成的重大影响，警示后人，于1995年复建大坝时，专门保留一部分坝址作为纪念。2008年，被省政府公布为第五批省级文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190709/1562641267564750.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190709/1562641350522054.jpg\" title=\"1562641350522054.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">长按识别关注 让旅行变得更简单</span></p></a></p>',1,0,50,'1562641372','旅途掠影',1,12,26,105),(41,'舞钢市楚长城遗址','/uploads/20190709/414df94c279a4600af94a58240bdac02.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">舞钢市楚长城遗址西起河南省方城、叶县交界处的老寨山，向东经舞钢市杨庄乡与庙街乡交界处的横梁山、四头脑山、蜘蛛山、平岭、马头崖、金钗崖到铁山的马鞍山、经舞钢市垭口办、寺坡办的鹁鸽楼山、刘山、大石门口、火山、旗杆山、铁山乡的罗山，过石漫滩大坝到尹集镇的鏊山折向南经尖山、孤山寨东西螃背山、大包寨、叶小岭山寨、尚店镇的五峰山后出舞钢境进入泌阳县象河关。全长81公里，共分为33段，走向呈“7”字形。该遗址保存较好，对研究我国战国时期的边界划分，军事防御等提供了依据，具有重要历史、军事价值。2009年，国家文物局组织成立专业调查机构，专题对楚长城进行调查、考古、认定，于2012年5月最终确认。2013年，被省政府公布为第六批省级文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190709/1562641418882688.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">楚长城是中国最早的长城，被称为长城之父。</span></p></a></p><p style=\"text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641505616867.jpg\" title=\"1562641505616867.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p></a></p><p style=\"text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">长按识别关注 让旅行变得更简单</span></p></a></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br/></span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p>',1,0,50,'1562641537','旅途掠影',3,12,26,103),(42,'平顶山博物馆','/uploads/20190709/4e0d5ab61341b06ae0d34fc513b57ab2.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">平顶山博物馆位于长安大道与怀仁路交叉口的平安广场西侧，依地势而立，交通便利；望烟波而居，景色宜人。占地面积54亩，建筑面积3万平方米，是我市投资最多，建筑面积最大的公益性文化设施。该建筑由清华大学建筑设计研究院设计，外形理念源自我国古书竹简的形象，墙体由一片片抽象的竹简构成，造型雄浑大气，古韵盎然，同时也寄寓着博物馆作为文明使者的传播功能。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190709/1562641589436390.jpg\" alt=\"677541705572236085\" title=\"677541705572236085\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">馆舍周围绿树成荫，四季常青，三季有花，两季有果；22亩的牡丹园里种有一万七千余株牡丹，包含九大色系，一百六十六个品种。平顶山博物馆隶属于平顶山市文化广电和旅游局，是公益性文化事业单位，内设办公室、安保部、社教部、陈展部、保管部、文物科技保护中心6个部室，现有中心文物库房、“鹰城古韵--平顶山历史与文化陈列和城市规划陈列三大功能，馆藏文物3万余件，3级品以上珍贵文物2000余件。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190709/1562641589309796.jpg\" alt=\"346498807066480860\" title=\"346498807066480860\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190709/1562641590182158.png\" alt=\"17\" title=\"17\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">自2012年5月15日开馆以来，接待社会各界观众400余万人次。先后荣获第十一届（2013年度）全国博物馆十大陈列展览 精品奖”、“国家AAAA级旅游景区”、“加拿大蒙特利尔室内设计金奖”（在35个获奖设计中，这是唯一以博物馆内容为主题的设计）、“省级文明单位”、“河南省爱国主义教育基地”、“河南省社会科学普及基地”、“省级卫生先进单位”等国家、省、市级荣誉20余项。2018年5月18日，平顶山博物馆与河南博物院签署战略合作框架协议，成立河南博物院平顶山分院。平顶山博物馆是观众领略深厚文化底蕴、触及悠久历史脉络、感受辉煌建设成就、享受文化发展成果的人文殿堂。在收藏、研究、宣传、推介，利用平顶山文化遗产方面发挥作用；为塑造平顶山形象、展示平顶山魅力、提升平顶山品位方面发挥独特作用。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"></a><img src=\"/ueditor/php/upload/image/20190709/1562641669822049.jpg\" title=\"1562641669822049.jpg\" alt=\"微信图片_20190707093953.jpg\"/><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\">长按识别关注 让旅行变得更简单</p>',1,1,50,'1562641685','旅途掠影',6,12,131,0),(43,'中国岩盐博物馆','/uploads/20190709/7962847b7f07fa8ba795579d5d87e725.png','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">一、博物馆简介</strong><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"></strong></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">中国岩盐博物馆位于河南省平顶山市叶县“中国岩盐之都”，总投资2000余万元，占地面积26亩，2016年8月正式破土动工，2017年12月11日建成并对外开放。该馆展览主题“文明结晶”，由《岩盐之都》、《盐的生产》、《盐与社会》、《生命之源》四部分组成。馆内陈列以大量珍贵的文物和历史照片为基础，采用声、光、电等多媒体技术，辅助以喷绘、灯箱、图片、蜡像、雕塑、场景、沙盘模型等演示手法，突出展现了中国盐业的辉煌成就和叶县岩盐的开发进程。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641763974776.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">二、建馆缘由</strong></span></p><p></p><p><span style=\"font-family: 宋体, SimSun;\">河南在历史上不产盐，属于纯销区，长期依赖外省供给，直到改革开放后，国家为了全面推进基础工业建设，在叶县勘探石油时，偶然发现在叶县境内约1100米的地下，蕴藏着一个特大型优质盐矿床——“叶县盐田”。该盐田展布面积400余平方公里，储量3300亿吨，品味居全国井矿盐之首，储量居全国第二。2007年9月29日，叶县被中国矿业联合会命名为“中国岩盐之都”。为了传承古都岩盐文化，推介叶县乃至平顶山市崭新名片，进一步带动地方旅游休闲、观光服务等相关产业发展，叶县政府和中国平煤神马集团共同出资建设了中国岩盐博物馆，可以说，该馆是应运而生，它让盐业文明和古城历史在这片土地上焕发出蓬勃生机和异样光彩。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">三、展览主题</strong><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"></strong></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">（一）岩盐之都</strong><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"></strong></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">叶县位于中原腹地，历史悠久，文化底蕴深厚，矿产资源丰富，岩盐储量位居全国前列。中国平煤神马集团作为一家国有特大型企业集团，立足本业、着眼全局，大力发展盐化工产业，特别是在叶县盐业开发利用方面做出了突出贡献，有力推动了地方经济蓬勃发展。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641763340897.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">（二）盐的生产</strong><br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">盐的生产方法，可简括为煮海为盐、湖池晒盐、凿井取盐。不同种类的盐在不同的历史时期有其独特的生产工艺，与当时的科学技术发展相适应。海盐的提炼技术包括煮盐和晒盐。湖盐的生产工艺经历了由集工捞采法、垦畦浇晒法到机械化开采的过程。井矿盐的生产工艺也由古代凿井法演变为现代化的旋转钻井。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641764761536.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">（三）盐与社会</strong><br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">盐与社会有着极为密切的联系。一方面，在日常生活中，盐是一种不可缺少的物质。另一方面，从古到今，盐已渗透到政治、经济、文化、军事等社会各个领域，并发挥着重要的作用，推动人类社会文明的演变。比如，历代盐税制度、典型盐商建筑、特色盐风民俗，都是盐与社会关系密切的有力例证。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641764125063.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">（四）生命之源</strong><br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">盐，不仅翻腾在浩瀚的大海上，隐藏在静谧的湖池里，埋藏在深厚的岩层中，而且还从我们每天生活都会接触到的锅碗瓢盆中转变成涌动不息的生命之源。盐中所含氯、钠、钾、碘等化学元素无时无刻不在调节身体的健康状态。盐从诞生的那一刻，就伴随着人生的酸、甜、苦、辣、咸之滋味，随着文明的车轮滚滚向前，永不停息。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641765322179.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641903448909.jpg\" title=\"1562641903448909.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">长按识别关注 让旅行变得更简单</span></p>',1,0,50,'1562641926','旅途掠影',3,12,29,0),(44,'叶县县衙博物馆','/uploads/20190709/33b50983865cf6622c0dd4479652139f.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">叶县县衙博物馆是国家AAA旅游景区，全国重点文物保护单位，始建于明代洪武二年（公元1369年），座落于县城中心街北侧，占地面积16848平方米是目前我国现存三座古县衙中唯一的明代县衙。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641984505646.jpg\" alt=\"县衙博物馆夜景\" title=\"县衙博物馆夜景\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">整座建筑布局共有41个单元，153间房屋组成，具有重要的历史价值、艺术价值和科学价值。叶县县衙群落布局合理，规模宏大，是我国古代县级衙署中为数不多的幸存者。其建筑形式融南北之风格，对研究我国古代建筑的风格、流派特点及变化规律等都具有重要的价值。同时也是研究我国古代县级政权机构的重要实物依据。</span></a></p><p></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641984309838.jpg\" alt=\"县衙博物馆2\" title=\"县衙博物馆2\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">叶县县衙整体布局由中轴线和东、西侧副线上的建筑群体组成。位于中轴线上的建筑主要有大门、仪门、卷棚、大堂、宅门、二堂、三堂及所附属的东西班房、六科房和东西厢房等组成；东侧副线分别为厨院、知县宅及狱房等建筑；西侧副线依次为西群房、虚受堂、思补斋等建筑；三堂后为大仙祠和后花园；大门东侧有编钟演奏厅和文物展厅。文物库房及展厅建筑面积473平方米，分文物库房、精品文物展厅、监控室、值班室等部分，从1000多件库存文物中选出300多件精品进行展览，其中有70多件文物为全国仅存的稀世珍宝。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641985976999.jpg\" alt=\"县衙博物馆5\" title=\"县衙博物馆5\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641985757906.jpg\" alt=\"县衙博物馆4\" title=\"县衙博物馆4\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562641986641660.jpg\" alt=\"县衙博物馆3\" title=\"县衙博物馆3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">叶县县衙自对外开放以来先后荣获：“国家AAA级旅游景区”、“全国重点文物保护单位”、“市级廉政教育基地”、“市级党性教育基地教学点”、“平顶山市爱国主义教育示范基地”“河南中小学专项性社会实践教育基地”、“河南省科普教育基地” 和“平顶山市科普教育基地”等荣誉。目前，叶县县衙共接待中外游客300余万人次，是叶县对外开放的重要窗口。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190709/1562642064669518.jpg\" title=\"1562642064669518.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>',1,1,50,'1562642088','旅途掠影',16,12,29,129),(45,'平顶山市文化艺术中心','/uploads/20190709/cb04053ee18f9d20c45e3085ab50b53f.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">平顶山市文化艺术中心隶属于平顶山市文化广电和旅游局，位于长安大道与平安广场东侧，场馆占地约54亩，建筑面积28403平方米。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562642143790064.jpg\" alt=\"中心全景2017有牌子\" title=\"中心全景2017有牌子\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562642144214072.jpg\" alt=\"大厅1\" title=\"大厅1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">市文化艺术中心为免费开放多功能综合性公共文化服务场馆。功能设置为：公益文化演出、图书借阅阅览、爱国主义教育电影展映，文化艺术作品展览，非物质文化遗产展示传承，公共文化艺术教育传播、文艺创作等。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562642144685291.jpg\" alt=\"平顶山市文化艺术中心音乐厅\" title=\"平顶山市文化艺术中心音乐厅\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">自2011年12月31日试运营以来，共接待社会各界观众300余万人次。先后荣获国家一级文化馆、省级公共文化服务示范项目、省级文明单位、省级社会主义核心价值观示范点、河南省爱国主义教育基地、省级卫生先进单位等国家、省、市级荣誉20余项。2018年平顶山市文化艺术中心成功申报省级公共文化服务艺术基金。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562642145347668.jpg\" alt=\"中心北长廊\" title=\"中心北长廊\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562642234614107.jpg\" title=\"1562642234614107.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p>',1,1,50,'1562642282','旅途掠影',17,12,125,0),(46,'平顶山市群众艺术馆','/uploads/20190709/3274ecd098fb2ea1541f56dc5dc9c0d6.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">平顶山市群众艺术馆成立于1955年，属于公益性文化事业单位，现位于市文化艺术中心二楼北侧，场馆面积19000㎡，是国家一级文化馆，现有副研究馆员5人、市级拔尖人才2人，内设办公室、培训部、活动部、非遗部、美展部、少儿部、信息技术部、理论调研部等8个部室，承担组织、指导、开展全市群众文化艺术活动，研究群众文化艺术理论，培训业余文艺骨干，实施免费开放，推进群众文化数字化信息化建设，组织文化志愿者开展文化惠民服务，保护和传承非物质文化遗产等职能。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562642327575665.jpg\" alt=\"平顶山市文化艺术中心器乐教室\" title=\"平顶山市文化艺术中心器乐教室\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562642328336718.jpg\" alt=\"大排练厅\" title=\"大排练厅\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">2013年、2014年、2016年度河南省先进文化馆；2014年荣获文化部公共项目类“群星奖”；“文化客厅”公益课堂先后荣获：河南省首届“群星奖”优秀公共文化服务项目、河南省公共文化服务体系示范项目、第三批国家公共文化服务体系示范项目等，“文化客厅”公益课堂共计授课2.3万多节（次），成果展演展示120多场（次），设立基层培训点360多个，受益群众100多万人（次）。我市现有国家级非遗项目4项，国家级代表性传承人1人；省级非遗项目28项，省级代表性传承人27人；市级非遗项目138项，市级代表性传承人81人。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><img src=\"/ueditor/php/upload/image/20190709/1562642328402117.jpg\" alt=\"平顶山市文化艺术中心小排练厅\" title=\"平顶山市文化艺术中心小排练厅\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190709/1562642382794561.jpg\" title=\"1562642382794561.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\">长按识别关注 让旅行变得更简单</p>',1,1,50,'1562642409','旅途掠影',15,12,125,0),(47,'平顶山市美术馆','/uploads/20190710/68f834dfc1dd307b10368a7233dccb4c.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">平顶山市美术馆隶属于平顶山市文化广电和旅游局，位于新城区清风路与顺德路交叉口东南角文化艺术中心。平顶山市美术馆陈列展厅面积1200平方米，临时展厅面积800平方米。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562718174631338.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">平顶山市美术馆集收藏、陈列、展览、研究、教育、服务、创作、交流为一体。重点宣传推介平顶山市优秀美术作品，拓展公共文化教育功能，提升大众审美品味。在陈列展览和管理运作上吸纳新理念、新方法，为本市艺术家打造交流平台，为广大市民提供艺术殿堂，同时肩负着记录当地文化历史发展和文化延续传承的使命，在对外文化交流中起着重要作用。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">从开馆截止目前藏品情况：接受捐赠257人次，中国画117幅，油画55幅，水彩、粉画7幅，速写373幅，书法567幅，摄影14幅，工艺美术66件，其他美术作品26幅/件。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><strong style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">特色文化</strong></span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">特色文化活动，“煤海星光”全国美术作品展览，平顶山市艺术人才培养工程，“关爱乡村儿童助力脱贫攻坚”平顶山市少儿美术捐赠展览</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">日常文化活动，平顶山市美术馆捐赠成果展，其他临时展览，张柳松捐赠瓷板画展览</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190710/1562718300793700.jpg\" title=\"1562718300793700.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p>',1,0,50,'1562718337','旅途掠影',3,12,125,0);
INSERT INTO `ddsc_culture` VALUES (48,'平顶山市图书馆','/uploads/20190710/89e9ea31904e4ed89089b54489c45e3a.jpg','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">平顶山市图书馆成立于1962年，是国家二级图书馆。总馆位于湛河区湛南路中段，分馆位于新城区市文化艺术中心，总面积11330平方米。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562719229103229.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">藏书量达69万余册，电子图书8万余册，购买数字资源36&nbsp;T，以家谱和地方文献为特藏，拥有《古今图书集成》《四库全书》《四库全书存目丛书》《四库全书存目丛书补编》等大型工具书，已成为全市地方文献中心和文化信息资源共享中心。目前有自助借还等各种设备30余台、累计办理借阅证近5万张,年借阅量73万余册，年平均开展读者活动参与人数为216658人次。以“读者第一·服务至上”为宗旨，实施免费开放、敞开办证、开架借阅、阅藏一体、预约借书、送书上门等主动式服务方式，并积极开展各种阅读推广活动，建立起了自助办证、自助借阅等现代智能服务体系，24小时城市书房全天开放。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562719229495545.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">先后荣获全国十大有声阅读示范基地、国家级全民阅读先进单位、在全国少年儿童“故事达人”大赛中，被中国图书馆学会等部门授予“优秀组织奖，在2018年中国图书馆学会主办的“依法办馆，创新发展——新时代公共图书馆建设与服务”知识竞赛活动中，代表河南赛区参加决赛，并被授予“最佳组织奖”，拥有省级卫生先进单位、河南省先进公共图书馆、巾帼文明岗、五一劳动奖状、省级“三八红旗集体”、市级文明单位、平顶山市社会科学普及基地、平顶山市爱国主义教育“示范基地”等国家、省、市级荣誉118项。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190710/1562719285701509.jpg\" title=\"1562719285701509.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>',1,0,50,'1562719310','旅途掠影',7,12,32,0),(49,'宝丰县刘兰芳艺术馆','/uploads/20190710/da726bf0d529868567d8e171c0ee1795.png','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">宝丰县刘兰芳艺术馆总占地面积19771平方米，总建筑面积12229平方米。其中艺术馆建筑面积5530平方米，户外广场面积6699平方米。场馆距县城6.5公里，距平西火车站3.5公里，交通便利，区位优越。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562719558586410.png\" alt=\"5\" title=\"5\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">艺术馆总体布局是典型中国北方的四合院落格式，仿明清风格古建筑群，外观古朴、典雅、内饰高贵、精美。主体建筑包括入口门楼、主体艺术馆、艺术人家馆、多功能厅等，同步配套内部设施有活动广场、停车场、观景绿化、道路及环卫设施</span><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">等。</span></p></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562719559577378.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190710/1562719559450530.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word