DROP TABLE IF EXISTS `ddsc_culture_city`;
CREATE TABLE `ddsc_culture_city` (
  `cid` int(11) NOT NULL AUTO_INCREMENT,
  `c_name` varchar(255) DEFAULT NULL COMMENT '模块名称',
  `pid` int(11) DEFAULT NULL COMMENT '上级id',
  `c_sort` int(11) NOT NULL DEFAULT '50' COMMENT '排序',
  `sid` int(11) NOT NULL DEFAULT '0' COMMENT '省份id',
  `phone` varchar(255) DEFAULT NULL COMMENT '投诉热线',
  PRIMARY KEY (`cid`)
) ENGINE=InnoDB AUTO_INCREMENT=132 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='城市列表';

