#
# Structure for table "ddsc_dd"
#

DROP TABLE IF EXISTS `ddsc_dd`;
CREATE TABLE `ddsc_dd` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `gid` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',
  `num` int(11) NOT NULL DEFAULT '0' COMMENT '数量',
  `sid` int(11) NOT NULL DEFAULT '0' COMMENT '商品规格',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 COMMENT='订单前信息';

