#
# Structure for table "ddsc_food"
#

DROP TABLE IF EXISTS `ddsc_food`;
CREATE TABLE `ddsc_food` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cid` int(11) NOT NULL DEFAULT '0' COMMENT '城市id',
  `tid` int(11) NOT NULL DEFAULT '0' COMMENT '分类id',
  `name` varchar(255) DEFAULT NULL COMMENT '酒店名称',
  `marray` varchar(255) DEFAULT NULL COMMENT '简介',
  `addr` varchar(255) DEFAULT NULL COMMENT '地址',
  `phone` varchar(255) DEFAULT NULL COMMENT '联系电话',
  `times` varchar(255) DEFAULT NULL COMMENT '营业时间',
  `price` int(11) NOT NULL DEFAULT '0' COMMENT '人均消费',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `status` tinyint(3) NOT NULL DEFAULT '1' COMMENT '状态 0关闭 1开启',
  `sort` int(11) NOT NULL DEFAULT '50' COMMENT '排序',
  `time` varchar(255) DEFAULT NULL COMMENT '发布时间',
  `yprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '原价',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=53 DEFAULT CHARSET=utf8 COMMENT='美食列表';

