#
# Structure for table "ddsc_food_hot"
#

DROP TABLE IF EXISTS `ddsc_food_hot`;
CREATE TABLE `ddsc_food_hot` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `fid` int(11) NOT NULL DEFAULT '0' COMMENT '美食id',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '价格',
  `ad` varchar(255) DEFAULT NULL COMMENT '关键词',
  `content` longtext COMMENT '详情',
  `yprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '原价',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8 COMMENT='热门菜品';

#
# Data for table "ddsc_food_hot"
#

INSERT INTO `ddsc_food_hot` VALUES (3,32,'鲁山揽锅菜','/uploads/20190711/8600349708271d6297df7ce744db1e5c.jpg',20.00,'平顶山市“十大名吃”','<p>鲁山揽锅菜是河南省著名小吃，每逢节假日必是场场爆满。揽锅菜用料及其考究，首先上面那层肉要先用各种大料配的汤卤到肉质稀烂入口既化，每份揽锅菜下层的菜都是新鲜炒制的，包扣小白菜，拳菜，炸的豆面丸子。用郏县豆瓣酱再炒制卤好的肉，吃上一口真是满嘴生香。</p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 18.72px; white-space: normal; background-color: rgb(255, 255, 255);\"><img src=\"/ueditor/php/upload/image/20190711/1562833170124989.jpg\" alt=\"833f63f8e1bf4b1485ebfd782f3b9971\" title=\"833f63f8e1bf4b1485ebfd782f3b9971\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline;\"/></p><p style=\"text-align:center\"><img src=\"/ueditor/php/upload/image/20190711/1562835280188290.jpg\" title=\"1562835280188290.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p><span style=\"font-family: 宋体, SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 长按识别关注 让旅行变得更简单</span></p>',0.00),(4,18,'郏县豆腐菜','/uploads/20190711/dacd9c1a1e21186f9a6f310d6bff5ab1.jpg',20.00,'平顶山市“十大名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px;\">郏县豆腐菜是河南省平顶山市郏县的特色美食。郏县豆腐粉条菜用料考究、独具特色，吃起来不仅味道鲜美，香而不腻，而且有暖胃去寒，滋阴壮阳，保健防病的功能。荣获“中华名小吃”称号。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px;\">郏县豆腐菜源于河南省郏县乡镇的一种特色小吃，是当地民间一种盛行的小吃品种，经过郏县人 赵听选 也就是当地有名的【听选豆腐粉条菜】的创始人不断的探索与改进，如今的郏县豆腐菜不仅在做法上有区别，而且在味道上独具特色，别具一格。听选豆腐粉条菜是一道用料考究、独具特色，已有千年历史的风味小吃，全称为豆腐粉条菜。辅以茴香、八角、辣椒、胡椒、枸杞、肉桂、葱花、等十余种佐料，吃起来不仅味道鲜美，香而不腻，而且有暖胃去寒，滋阴壮阳，保健防病的功能。</span></a><br/></p><p><br/></p>',0.00),(6,32,'郏县饸饹面','/uploads/20190711/10881a826ee63a1dc9220fa723b7ad11.jpg',20.00,'平顶山市“十大名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">郏县饸饹面是河南郏县地区一道用料考究、独具特色，已有千年历史的传统特色名小吃，简称郏县饸饹。郏县饸饹是一种圆形条状面制食品，初以荞麦面为面料，其加入用纯羊油熬制的辣椒和百年老锅汤及新鲜味美的羊肉，辅以八角、茴香、辣椒、胡椒、肉桂、葱花、枸杞等十余种佐料，吃起来不仅味道鲜美，香而不腻，而且有暖胃去寒，滋阴壮阳，保健防病的功能。清末，始以小麦面替代荞麦面，口感营养更胜一筹。《中华风味饮食总》一书称郏县饸饹&quot;舒脾健胃，养生至宝&quot;。<br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"text-align:center\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190711/1562835461187279.jpg\" title=\"1562835461187279.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;长按识别关注 让旅行变得更简单</span></p><p><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br/></span></p></a></p>',0.00),(7,35,'郏县豆腐菜','/uploads/20190715/3a872de19f025e1c42194f852c707379.jpg',0.00,'十大名吃','<p><a></a></p><p><a>郏县豆腐菜是河南省平顶山市郏县的特色美食。郏县豆腐粉条菜用料考究、独具特色，吃起来不仅味道鲜美，香而不腻，而且有暖胃去寒，滋阴壮阳，保健防病的功能。荣获“中华名小吃”称号。</a></p><p><a><br/></a></p><p><a>郏县豆腐菜源于河南省郏县乡镇的一种特色小吃，是当地民间一种盛行的小吃品种，经过郏县人 赵听选 也就是当地有名的【听选豆腐粉条菜】的创始人不断的探索与改进，如今的郏县豆腐菜不仅在做法上有区别，而且在味道上独具特色，别具一格。听选豆腐粉条菜是一道用料考究、独具特色，已有千年历史的风味小吃，全称为豆腐粉条菜。辅以茴香、八角、辣椒、胡椒、枸杞、肉桂、葱花、等十余种佐料，吃起来不仅味道鲜美，香而不腻，而且有暖胃去寒，滋阴壮阳，保健防病的功能。</a></p><p></p>',0.00),(8,43,'平顶山市十大名吃','/uploads/20190716/050022c965fcfcfc662ce49709a51d9d.jpg',20.00,'“十大名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">羊肉冲汤制作过程中，有着十几道工序的铺垫，从选羊、熬汤、佐料配比，到切肉、配料、热汤冲滗均很考究。羊肉以本地山、绵羊为主。汤为羊骨头、羊肉羊杂精心熬制，即将熬制成的滚烫的汤浇入盛有熟羊肉及羊杂碎的碗中，再用汤勺滗住碗里的熟羊肉或羊杂碎，将汤倒回锅里。反复几次后一直把羊肉冲得冒着热气，再浇上羊肉汤，放入葱、香菜、盐、味精、五料及红艳的辣椒油。就这样，一碗香气氤氲的羊肉冲汤便做好了，碧绿的葱花、浓白的汤头、片得厚薄适中的羊肉静静地浸在一片氤氲的香气中，足以让人“闻香下马，知味停车”。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">羊肉冲汤的主要特点是:色泽光亮、呈乳白色、汤质优美、不腥不腻、味道鲜美。</span></p></a></p>',0.00),(9,34,'鲁山揽锅菜','/uploads/20190716/afd877226dce5e9b163ad5becc87e8c2.jpg',20.00,'“十大名吃”','<p><span style=\"color: rgb(102, 102, 102); background-color: rgb(255, 255, 255); font-family: 宋体, SimSun;\">鲁山揽锅菜是河南省著名小吃，每逢节假日必是场场爆满。揽锅菜用料及其考究，首先上面那层肉要先用各种大料配的汤卤到肉质稀烂入口既化，每份揽锅菜下层的菜都是新鲜炒制的，包扣小白菜，拳菜，炸的豆面丸子。用郏县豆瓣酱再炒制卤好的肉，吃上一口真是满嘴生香。</span></p>',0.00),(10,36,'郏县饸饹面','/uploads/20190716/de68d6c93d925194c59c4cf104008d6a.jpg',15.00,'平顶山市“十大名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px;\">郏县饸饹面是河南郏县地区一道用料考究、独具特色，已有千年历史的传统特色名小吃，简称郏县饸饹。郏县饸饹是一种圆形条状面制食品，初以荞麦面为面料，其加入用纯羊油熬制的辣椒和百年老锅汤及新鲜味美的羊肉，辅以八角、茴香、辣椒、胡椒、肉桂、葱花、枸杞等十余种佐料，吃起来不仅味道鲜美，香而不腻，而且有暖胃去寒，滋阴壮阳，保健防病的功能。清末，始以小麦面替代荞麦面，口感营养更胜一筹。《中华风味饮食总》一书称郏县饸饹&quot;舒脾健胃，养生至宝&quot;。</span></p></a></p>',0.00),(11,37,'宝丰炝锅面','/uploads/20190716/1c47b98d181ad99841c8332d224bc03d.jpg',10.00,'平顶山市“十大名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">炝锅面是一道以羊肉、绿豆面条、千芝麻叶、黄豆芽作为主要食材,通过羊肉炝锅,炝出羊肉的香味,然后加入羊肉汤、干菜、黄豆芽和绿豆面条加以烹制而成,其主要特点就在炝的过程,目前已经广泛流传成为我市的一道特色面类小吃。</span></p></a></p>',0.00),(12,38,'热豆腐','/uploads/20190716/8bfbec598ea06917ecdf582011e8d1bc.jpg',10.00,'平顶山市“十大名吃”','<p><span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">舞钢热豆腐又称辣子热豆腐，是舞钢市的一道特色小吃，细嫩爽口的豆腐，浇上辣子汁、蒜泥汁、香椿汁等调料，色香味俱佳！</span></p>',0.00),(13,39,'懒豆','/uploads/20190716/fa9dce11de532eb647e4232df3669969.jpg',10.00,'平顶山市“十大名吃”','<p><span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">懒豆是舞钢独有的一道农家特色菜，其原材料为黄豆、青菜，利用黄豆磨成豆腐之原理，其中间省一道滤渣工序，故得名懒豆。其做法先把黄豆磨成浆，浆与渣不滤开，混合在一起，然后将青菜(或萝卜樱等)煮至将熟时，放入浆与渣的混合体与青菜一起煮，放入盐、香油，直至煮熟。因原料为黄豆、蔬菜粗纤维、胡萝卜等天然农家食材，所以具备：天然素食、无公害；风味独特、口感鲜嫩；营养均衡、提升免疫力；助消化、治病、保健、美容价值高等多方面的功效和优势，实为中老年保健的绝佳食材。</span></p>',0.00),(14,40,'买根烧鸡','/uploads/20190716/61d9e42e1d48b6dcfe62304ff82ed09c.jpg',50.00,'平顶山市“十大名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">买红卫，生于一九六六年九月，系买根烧鸡的传承人，买家烧鸡到宝丰已有五代人，清末至今已有一百五十余年历史，清末民初已颇负盛名，工艺制作独特，五味俱佳，酥香软烂、咸淡适口，肥而不腻，骨里透香，世代相传久盛不衰，一九八八年传至本人时，改名为“买根烧鸡”，一九九一年获得平顶山市优质食品奖证书，2000年为了纪念父亲买根，就以买根的名字申请注册了国家商标。买根烧鸡，宝丰县城乡居民津津乐道，它以风味独特，味道咸鲜醇厚著称，是宝丰县特色风味传统食品，更是该县十大文化名吃之首，2015年元月被宝丰县民间文艺家协会授予“宝丰十大文化名吃”，同时还接受央视财经频道《消费主张》栏目组来我县拍摄传统年货——买根烧鸡。2015年8月又被平顶山市美食节组委会授予“地方特色名吃”等荣誉称号，2016年8月份河南电视台“香香美食”栏目组慕名而来做专访。</span></p></a></p>',0.00),(15,40,'买根烧鸡','/uploads/20190716/c7bdc2724876d9d215cd4905b8b10e13.jpg',50.00,'平顶山市“十大名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">买红卫，生于一九六六年九月，系买根烧鸡的传承人，买家烧鸡到宝丰已有五代人，清末至今已有一百五十余年历史，清末民初已颇负盛名，工艺制作独特，五味俱佳，酥香软烂、咸淡适口，肥而不腻，骨里透香，世代相传久盛不衰，一九八八年传至本人时，改名为“买根烧鸡”，一九九一年获得平顶山市优质食品奖证书，2000年为了纪念父亲买根，就以买根的名字申请注册了国家商标。买根烧鸡，宝丰县城乡居民津津乐道，它以风味独特，味道咸鲜醇厚著称，是宝丰县特色风味传统食品，更是该县十大文化名吃之首，2015年元月被宝丰县民间文艺家协会授予“宝丰十大文化名吃”，同时还接受央视财经频道《消费主张》栏目组来我县拍摄传统年货——买根烧鸡。2015年8月又被平顶山市美食节组委会授予“地方特色名吃”等荣誉称号，2016年8月份河南电视台“香香美食”栏目组慕名而来做专访。</span></p></a></p>',0.00),(16,41,'瘸子烩面','/uploads/20190716/e31bad619870e75cfabdb9565cf22c26.jpg',10.00,'平顶山市“十大名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">瘸子烩面为叶县风味特色小吃之一,因其创始人赵瑞民右腿残疾人们慢慢就把他作的炝锅烩面称之为“瘸子烩面”。其最大的特点在于“炝”,它把鲜羊肉丝放入锅里炒,羊肉的味道彻底被炝了出来,烩面采用优质面粉做出烩面胚备用,然后以新鲜的生羊肉,加入葱、姜、蒜、红辣椒等烹炒入味,待七八分熟后再添入高汤,煮沸后再拉面入锅,加入青菜,盛面时再撒上点香菜提味,根据需要添加秘制的辣椒油,吃起来汤香肉美、鲜而不膻,美味至极。</span></p>',0.00),(17,42,'红牛牛肉','/uploads/20190716/f2c33d95910afe3919cd75ab7cf39aee.jpg',30.00,'平顶山市“十大名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">郏县红牛牛肉制作工艺，始于宋、盛于明清，是康熙年间钦点的老字号贡品牛肉。色红肉嫩，味道爽口，香而不腻，老少皆宜，是上乘绿色保健食品，被誉为“牛肉之王”。郏县牛肉由于遵古泡制，严守工艺，制作规范，皮干肉湿，盐分适中，所以可久放、可远距离运送，不变质，不逊色味。而且富含人体需要的多种元素成分和热量，食后祛胃寒，增体力，焕精神，英姿勃发。</span></p></a></p>',0.00),(18,44,'舞钢沫糊','/uploads/20190716/453bf25ecea512f196fb2ba31497f29a.jpg',10.00,'平顶山市“十优名吃”','<p><span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">沫糊是地道的农家饭。先将黄豆、花生、玉米、芝麻炒熟，然后放在石磨里磨，磨成糊状，然后倒进锅里烧开。这时，里面可以放入粉条和豆腐丝、海带丝、胡萝卜丝等，待快熟时再放些青菜，先用旺火，再用温火，待熬到汤浓菜烂即可食用。沫糊喝起来比油茶、糊辣汤爽口，且余香悠长。</span></p>',0.00),(19,45,'曹镇卤肉','/uploads/20190716/a4274d465eee55a9e5c477e01bf3a8e2.jpg',20.00,'平顶山市“十优名吃”','<p><span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">曹镇卤肉以百年老汤、猪肉、香辛料为主要原料加工制成,颜色红润悦目,香糯浓醇,咸甜适度,肥而不腻,老少皆宜。主要集中在湛河区曹镇乡曹东村,辐射周边地区。</span></p>',0.00),(20,46,'叶县张集硬面馍','/uploads/20190716/8ce1371087e55b12884ef9c512a967b3.jpg',10.00,'平顶山市“十优名吃”','<p><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">张集硬面馍始于清朝乾隆年间,由民间传入宫廷被封为“龙卷馍”。传承数百年,历经兴替。其主要特色是:精选特制面粉,采用发酵面和未发酵面掺杂反复杠压的方法,先武火后文火,下蒸外炕,出锅的馍头洁白如雪,层层叠叠,底焦面嫩,香酥可口,麦香纯正,长期存放不霉不裂。</span></p>',0.00),(21,47,'郏县李军正麻辣鸡','/uploads/20190716/7cb1a9db98f399aa0f4d68b0645a5cc9.jpg',50.00,'平顶山市“十优名吃”','<p><span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">李军政麻辣鸡(原李家烧鸡)始创于光绪二十九年(1903)是郏县一个传统老店。其工艺流程复杂配料十分讲究。在使用老汤的基础上精选一些名贵调料使“李家烧鸡”升华到色泽光亮,气味芳香,咸淡透骨,深受人们的欢迎。</span></p>',0.00),(22,48,'银记烩面','/uploads/20190716/8e8e65136790b71e6c9e1cb54f8206eb.jpg',15.00,'平顶山市“十优名吃”','<p><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-family: 宋体, SimSun;\">自2000年以来,一切“从严,从细,从实,从小,求精”,专心打造鹰城银记烩面。无论从银氏传承,还是众采河南地方烩面特色,在行业中摸爬滚打、探索、挖掘、吃遍学遍了平顶山及周边县市众多烩面,从而打造形成了具有“地方特色的、老百姓喜爱的、社会认可的鹰城银记羊汤烩面”。所用原材料均选用绿色优质原材料。</span></p>',0.00),(23,49,'矿工烤火烧','/uploads/20190716/57e17893d7b4edfe3d6de5d5a9737f32.jpg',10.00,'平顶山市“十优名吃”','<p><span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">矿工烤火烧是平顶山地区特有的面点小吃,源于平顶山一矿食堂,起初做为煤矿工人班中餐食用,距今已经有50余年的历史,因外表色泽金黄似牛皮,又名为牛皮火烧。采用精制面粉,选取精肉,经过含有丁香肉等二十余种有益的中草药材的特质老汤卤制加工后,制成卤肉馅,经过烤箱烤制而成。火烧外表松脆,火烧内五花肉肥而不腻,令人回味无穷。</span></p>',0.00),(24,50,'鲁山槲坠','/uploads/20190716/15a9cd9034cd4e01884b921863d7e364.jpg',10.00,'平顶山市“十优名吃”','<p><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0); font-family: 宋体, SimSun;\">﻿槲坠是鲁山县西部、西南部山区的特色风味小吃之一。是端午节的必备食品,类似棕子,形状却与棕子不同,棕子是三棱锥形的;槲坠是圆柱形的,二者的馅子大体相同,都是糯米、枣、花生米之类。传统制作上以甜味为主,现代人制作时则根据个人品味而定。鲁山槲坠的独特风味在包裏物槲叶上,槲叶是山毛榉植物槲栎之叶片。</span></p>',0.00),(25,51,'舞钢罐饺子','/uploads/20190716/57a7ed4d9be4db87dda145d71c74a6f2.jpg',20.00,'平顶山市“十优名吃”','<p><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">﻿舞钢罐饺子是舞钢周边乡镇的地域性特色小吃。旧时,多岀现于集市、庙会,为方便于贩卖与保温,盛于大尺寸的敞口瓦罐,故此得名。舞钢罐饺子里面有饺子、丸子和酸汤,有喜庆团圆之意,成为舞钢地区餐桌上必不可少的美食。</span></p>',0.00),(26,52,'鲁山下汤厚馍','/uploads/20190716/9e6e5134f243b9c971de105eb2f7743a.jpg',10.00,'平顶山市“十优名吃”','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); max-width: 100%; font-size: 16px;\">将面添水和酵母揉成光滑的面团、静置发酵约一个小时以上。取出放在案板上摊平,洒上干粉,然后揉搓面团,直至揉搓成较硬的面团,然后放入锅中,两面金黄即可。</span></a></p><p></p>',12.00);

#
# Structure for table "ddsc_food_type"
#

DROP TABLE IF EXISTS `ddsc_food_type`;
CREATE TABLE `ddsc_food_type` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COMMENT='美食分类';

#
# Data for table "ddsc_food_type"
#

INSERT INTO `ddsc_food_type` VALUES (2,'豫菜'),(5,'平顶山市本地美食'),(6,'平顶山市本地特产');

#
# Structure for table "ddsc_goods"
#

DROP TABLE IF EXISTS `ddsc_goods`;
CREATE TABLE `ddsc_goods` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '商品id',
  `name` varchar(255) DEFAULT NULL COMMENT '商品名称',
  `xprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品现价',
  `sales` int(11) NOT NULL DEFAULT '0' COMMENT '销量',
  `kc` int(11) DEFAULT NULL COMMENT '库存',
  `content` longtext COMMENT '商品详情',
  `up` tinyint(1) NOT NULL DEFAULT '0' COMMENT '商品状态 0下架 1上架',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '首页显示 0否 1是',
  `sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序',
  `image` varchar(255) DEFAULT NULL COMMENT '商品图片',
  `fid` int(11) DEFAULT NULL COMMENT '所属分类id',
  `tag` varchar(255) DEFAULT NULL COMMENT '商品标签',
  `city` varchar(255) DEFAULT NULL COMMENT '产地',
  `shop_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id',
  `yprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '原价',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

