#
# Structure for table "ddsc_hotel"
#

DROP TABLE IF EXISTS `ddsc_hotel`;
CREATE TABLE `ddsc_hotel` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL COMMENT '酒店名称',
  `score` varchar(255) DEFAULT NULL COMMENT '评分',
  `money` int(11) NOT NULL DEFAULT '0' COMMENT '人均消费',
  `addr` varchar(255) DEFAULT NULL COMMENT '地址',
  `phone` varchar(255) DEFAULT NULL COMMENT '联系方式',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `severs` varchar(255) DEFAULT NULL COMMENT '设施详情',
  `content` text COMMENT '温馨提示',
  `status` tinyint(3) NOT NULL DEFAULT '1' COMMENT '状态 0关闭 1开启',
  `type` tinyint(3) NOT NULL DEFAULT '1' COMMENT '类型 1酒店 2民宿',
  `recome` tinyint(3) NOT NULL DEFAULT '0' COMMENT '推荐 0否 1是',
  `sort` int(11) NOT NULL DEFAULT '50' COMMENT '排序',
  `yprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '原价',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=33 DEFAULT CHARSET=utf8 COMMENT='酒店民宿';

