INSERT INTO `ddsc_hotel` VALUES (7,'皇宫大酒店1','9.8',66,'河南','15093103977','/uploads/20190605/b9f3c48e70ab6e4b53c8f71c796e07e9.png','1,2,3,4','<p>12312</p>',1,1,1,50,0.00),(8,'酒店','10',90,'郑州恒华大厦','13721484116','/uploads/20190615/994d043b0570f8a9f9dc0a43871eb4b5.jpg','1,2,3,4,5,6','<p><span style=\"color: rgb(57, 57, 57); font-family: \" open=\"\" font-size:=\"\" background-color:=\"\">温馨提示</span><span style=\"color: rgb(57, 57, 57); font-family: \" open=\"\" font-size:=\"\" background-color:=\"\">温馨提示</span><span style=\"color: rgb(57, 57, 57); font-family: \" open=\"\" font-size:=\"\" background-color:=\"\">温馨提示</span><span style=\"color: rgb(57, 57, 57); font-family: \" open=\"\" font-size:=\"\" background-color:=\"\">温馨提示</span><span style=\"color: rgb(57, 57, 57); font-family: \" open=\"\" font-size:=\"\" background-color:=\"\">温馨提示</span><span style=\"color: rgb(57, 57, 57); font-family: \" open=\"\" font-size:=\"\" background-color:=\"\">温馨提示</span></p>',1,1,1,50,0.00),(9,'蕴海锦园大酒店','五星级饭店',0,'平顶山市长安大道怀仁路',' 0375-2638888','/uploads/20190707/7a6c9fd82d54e7038ae2b8f3ac85e92c.jpg','1,3,4','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">蕴海锦园大酒店地处河南省平顶山市新城区的行政区域，坐落于白龟湖国家湿地公园内，占地面积120余亩，建筑面积3.87万平方米。前身是由中国平顶山市蕴海投资管理有限公司于2009年4月投资兴建的蕴海建国饭店。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562467674659150.jpg\" alt=\"11\" title=\"11\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" vertical-align:=\"\"/><span style=\"font-family: 微软雅黑, \" microsoft=\"\">&nbsp;</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">酒店于2010年9月18日试营业，于2012年6月12日被国家旅游局评为五星级饭店，并于2013年12月9日被中国旅游饭店协会评为5A级绿色金叶级饭店。酒店于2015年2月14日更名为中信国安大酒店；于2017年9月25日正式更名为蕴海锦园大酒店。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\"><img src=\"/ueditor/php/upload/image/20190707/1562467675474798.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" vertical-align:=\"\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">酒店拥有100多亩地的园林，600多种不同的植物，2100多棵花草树木，汇集了全国各地的名贵稀有树种，造型迥异，婀娜多姿，具有极高的自身价值、研究价值和观赏价值。高低起伏、错落有致地环绕在酒店建筑群的四周，与平西湖岸及湖滨生态公园融为一体，荡漾的碧波、欧式的庭院，小桥流水、古树风韵、满眼绿意、水帘瀑布、构成一幅壮美的画卷，体现园林式的商务酒店的风格，把园林与建筑相融至极致，是酒店的重要特色。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"><span style=\"font-size: 16px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">客房类型</span></strong></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, \" microsoft=\"\"></span></strong></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; color: rgb(46, 52, 59); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 宋体, SimSun;\">酒店由主体大楼、会议配楼和6栋别墅组成错落有致的建筑群体，拥有客房256间/套。其中标准间147间，单间75间，豪华套房26间，四房以上豪华套房6间（每栋别墅以一间计算），残疾人客房2间。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562467675522276.jpg\" alt=\"3\" title=\"3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562467676201025.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle; font-size: 16px;\"/><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><h4 class=\"tit-bar\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-size: 0.373333rem; font-weight: normal;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">服务项目</span></strong></h4><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; color: rgb(46, 52, 59); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\">蕴海食府中餐厅位于主体楼东侧，设零点餐厅，18个豪华包房，一个大宴会厅（会所）；塞纳河咖啡厅位于主体楼大堂北侧；威尼斯餐厅位于行政楼二楼；各种不同的餐厅可提供餐位数共870个；主楼地下一层的部分区域为康体娱乐设施，游泳馆、KTV包房、SPA、健身中心、美容美发、桌球、乒乓球、洗浴等；独立的国际会议中心拥有多功能厅、会见厅、接待室等大小不同的会议室11个；大堂吧、茶室、商场、宾客服务中心等高档酒店的设施一应俱全，能满足不同客人的需求。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; color: rgb(46, 52, 59); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\"><br/></span></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%;\"><strong><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; color: rgb(46, 52, 59); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">交通指南</span></span></strong><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; color: rgb(46, 52, 59); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\"></span><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; font-size: 17.28px; white-space: normal;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; color: rgb(46, 52, 59); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\">酒店交通便利，比邻市主干道长安大道，距离市区及火车站只需30分钟车程，距离新城区高速公路路口不到3公里，到郑州新郑机场不足2小时。国家规划中的郑万（郑州到万州）高铁即将在新城区与宝丰直接设站，届时平顶山至郑州行程缩短在1小时以内，到重庆行程缩短在3小时以内。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; font-size: 17.28px; white-space: normal; text-align: center;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; color: rgb(46, 52, 59); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-atta