#
# Structure for table "ddsc_hotel_banner"
#

DROP TABLE IF EXISTS `ddsc_hotel_banner`;
CREATE TABLE `ddsc_hotel_banner` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `hid` int(11) NOT NULL DEFAULT '0' COMMENT '酒店id',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='酒店banner';

#
