#
# Structure for table "ddsc_hotel_coupon"
#

DROP TABLE IF EXISTS `ddsc_hotel_coupon`;
CREATE TABLE `ddsc_hotel_coupon` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `hid` int(11) NOT NULL DEFAULT '0' COMMENT '酒店id',
  `money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单金额',
  `coupon` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '优惠券',
  `status` tinyint(3) NOT NULL DEFAULT '1' COMMENT '状态 0关闭 1开启',
  `sort` int(11) NOT NULL DEFAULT '50',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COMMENT='酒店专属优惠券';

