#
# Structure for table "ddsc_hotel_order"
#

DROP TABLE IF EXISTS `ddsc_hotel_order`;
CREATE TABLE `ddsc_hotel_order` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `rid` int(11) NOT NULL DEFAULT '0' COMMENT '房间id',
  `hid` int(11) NOT NULL DEFAULT '0' COMMENT '酒店id',
  `start` varchar(255) DEFAULT NULL COMMENT '入住时间',
  `end` varchar(255) DEFAULT NULL COMMENT '离开时间',
  `days` int(11) NOT NULL DEFAULT '0' COMMENT '入住天数',
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8 COMMENT='酒店下单前信息';

