DROP TABLE IF EXISTS `ddsc_hotel_room`;
CREATE TABLE `ddsc_hotel_room` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `hid` int(11) NOT NULL DEFAULT '0' COMMENT '酒店id',
  `name` varchar(255) DEFAULT NULL COMMENT '房间名称',
  `ad` varchar(255) DEFAULT NULL COMMENT '放假标签',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '房间价格',
  `status` tinyint(3) NOT NULL DEFAULT '1' COMMENT '状态 0关闭 1开启',
