DROP TABLE IF EXISTS `ddsc_order`;
CREATE TABLE `ddsc_order` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `gid` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '价格',
  `num` int(11) NOT NULL DEFAULT '0' COMMENT '数量',
  `username` varchar(255) DEFAULT NULL COMMENT '联系人',
  `phone` varchar(255) DEFAULT NULL COMMENT '联系方式',
  `name` varchar(255) DEFAULT NULL COMMENT '商品名称',
  `code` varchar(255) DEFAULT NULL COMMENT '订单号码',
  `status` tinyint(3) NOT NULL DEFAULT '0' COMMENT '订单状态 0待付款 1已付款 2已核销',
  `time` varchar(255) DEFAULT NULL COMMENT '下单时间',
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `type` int(11) NOT NULL DEFAULT '1' COMMENT '1 团游订单 2景区门票订单 3酒店民宿',
  `start_time` varchar(255) DEFAULT NULL COMMENT '出发日期',
  `end_time` varchar(255) DEFAULT NULL COMMENT '返回日期',
  `old_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '原价',
  `coupon` int(11) NOT NULL DEFAULT '0' COMMENT '优惠券价格',
  `cid` int(11) NOT NULL DEFAULT '0' COMMENT '优惠券id',
  `hotel_name` varchar(255) DEFAULT NULL COMMENT '酒店名称',
  `days` int(11) NOT NULL DEFAULT '0' COMMENT '入住天数',
  `only_coupon` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '专用优惠券',
  `oid` int(11) NOT NULL DEFAULT '0' COMMENT '专用优惠券id',
  `shop_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8 COMMENT='订单列表';

