DROP TABLE IF EXISTS `ddsc_package`;
CREATE TABLE `ddsc_package` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `qprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '起价',
  `marray` text COMMENT '简介',
  `meal` varchar(255) DEFAULT NULL COMMENT '套餐名称',
  `cprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '成人价格',
  `eprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '儿童价格',
  `trip` text COMMENT '行程特色',
  `plan` text COMMENT '行程安排',
  `cost` text COMMENT '费用说明',
  `tips` text COMMENT '温馨提示',
  `ticket` varchar(255) DEFAULT NULL COMMENT '门票名称',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '价格',
  `status` tinyint(3) NOT NULL DEFAULT '1' COMMENT '状态 0关闭 1开启',
  `sort` int(11) NOT NULL DEFAULT '50' COMMENT '排序',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `time` varchar(255) DEFAULT NULL COMMENT '发布时间',
  `fid` int(11) NOT NULL DEFAULT '0' COMMENT '旅行社id',
  `yprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '原价',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8 COMMENT='团游详情';

