DROP TABLE IF EXISTS `ddsc_rural`;
CREATE TABLE `ddsc_rural` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `tid` int(11) NOT NULL DEFAULT '0' COMMENT '分类id',
  `addr` varchar(255) DEFAULT NULL COMMENT '拍摄地点',
  `dates` varchar(255) DEFAULT NULL COMMENT '拍摄时间',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `content` text COMMENT '内容',
  `image` varchar(255) DEFAULT NULL COMMENT '封面图',
  `images` text COMMENT '组图',
  `time` varchar(255) DEFAULT NULL COMMENT '发布时间',
  `status` tinyint(3) NOT NULL DEFAULT '0' COMMENT '审核状态 0未审核 1已审核',
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `looks` int(11) NOT NULL DEFAULT '0' COMMENT '浏览数量',
  `recom` tinyint(3) NOT NULL DEFAULT '0' COMMENT '首页推荐 0否 1是',
  `author` varchar(255) DEFAULT NULL COMMENT '作者',
  `longs` varchar(255) DEFAULT NULL COMMENT '经度',
  `lats` varchar(255) DEFAULT NULL COMMENT '纬度',
  `sort` int(11) NOT NULL DEFAULT '50' COMMENT '排序',
