INSERT INTO `ddsc_rural` VALUES (64,6,'北京','2019-05-22','bnbn','bmnvbn','/uploads/20190522/017b0e2d9fa1111497196f42e02d19ce.jpg','/uploads/20190522/017b0e2d9fa1111497196f42e02d19ce.jpg,/uploads/20190522/9cda9ea60242899f1e230974b544d96b.jpg','1558508466',0,1,0,0,NULL,NULL,NULL,50,0,0,0,NULL,NULL,NULL,NULL),(65,7,'北京','2019-05-22','zdxfcsd','sdfsdfds','/uploads/20190522/60b81c8bd295eea7c88450fbe26fb986.png','/uploads/20190522/60b81c8bd295eea7c88450fbe26fb986.png','1558508501',0,1,0,0,NULL,NULL,NULL,50,0,0,0,NULL,NULL,NULL,NULL),(70,6,'河南省 郑州市 金水区','2019-06-16','护理记录螺母','哈哈哈哈','/uploads/20190615/35795c50746cc99f565c63ea32293a18.jpeg','/uploads/20190615/35795c50746cc99f565c63ea32293a18.jpeg','1560580496',0,1,0,0,NULL,NULL,NULL,50,0,0,0,NULL,NULL,NULL,NULL),(80,6,'叶县','2019-06-26 11:28:40','谁说叶县没有好景色，这么美的地方，你们知道吗？','<p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">进入夏季之后，烈日炎炎，我们可能会自驾或者跟着旅游团去一些山水名胜乘凉避暑，但在叶县本地就有这样一个有山有水的好去处，您知道吗？</span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><span style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; font-weight: 700;\"><img src=\"http://p3.pstatp.com/large/7a60006c8bdfa01eee6\" img_width=\"1080\" img_height=\"1920\" alt=\"谁说叶县没有好景色，这么美的地方，你们知道吗？\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">常村乡刘东华村附近有一处原生态景点“一线天”，位于叶县常村乡南部山区，与南阳方城县独树镇搭界，属于伏牛山的余脉。</span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><img src=\"http://p3.pstatp.com/large/7a60006c8be9fda11ab\" img_width=\"1080\" img_height=\"1920\" alt=\"谁说叶县没有好景色，这么美的地方，你们知道吗？\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); text-align: center;\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">这里绿植繁茂，郁郁葱葱。</span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><img src=\"http://p3.pstatp.com/large/7b60001c2b58b67a7a3\" img_width=\"1080\" img_height=\"1920\" alt=\"谁说叶县没有好景色，这么美的地方，你们知道吗？\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); text-align: center;\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">这里山溪潺潺，清澈流淌。</span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><span style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; font-weight: 700;\"><img src=\"http://p1.pstatp.com/large/7b60001c2b6f5853941\" img_width=\"1080\" img_height=\"1920\" alt=\"谁说叶县没有好景色，这么美的地方，你们知道吗？\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); text-align: center;\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">这里层层叠叠，劈立千仞！</span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><img src=\"http://p1.pstatp.com/large/7a60006c8bf9a61165e\" img_width=\"1080\" img_height=\"1920\" alt=\"谁说叶县没有好景色，这么美的地方，你们知道吗？\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">其特殊的地质地貌，完美的呈显了山与山间的断层，巧妙的形成了一条巨大的断裂带，与山谷完美璧合，恍似处于仙境之中，其景色妙不可言。</span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><img src=\"http://p9.pstatp.com/large/7a60006c8c5f5e430bd\" img_width=\"1080\" img_height=\"1920\" alt=\"谁说叶县没有好景色，这么美的地方，你们知道吗？\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">山谷中大石无序的散落，原生态的地貌构成了一幅美丽的景观。</span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">由于此地还没有开发，完全保留了原生态景象，其山路较为陡峭，正因为山路的陡峭，让整个旅途显得更为美妙。</span></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><img src=\"http://p3.pstatp.com/large/7a60006c8c29fc11482\" img_width=\"1080\" img_height=\"1920\" alt=\"谁说叶县没有好景色，这么美的地方，你们知道吗？\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\">时而低身穿越，时而侧身小步行进，或双手双脚拼力攀爬，或大步向前迈进。</p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><img src=\"http://p3.pstatp.com/large/7b60001c2b87da7d4e1\" img_width=\"1080\" img_height=\"1920\" alt=\"谁说叶县没有好景色，这么美的地方，你们知道吗？\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\">这里是消暑佳处，这里是休闲乐园！</p><p><br/></p>','/uploads/20190626/8d09174ed082e41995add70512077c4d.jpg','/uploads/20190626/1b9a723a330e532c64de8cd43c001b4b.jpg,/uploads/20190626/7f722b99d40d66a2c8b9e5a8dfa9b40a.jpg','1561522663',1,0,133,1,'旅途掠影','113.3575','33.62255',50,12,29,62,'<p><span style=\"color: rgb(34, 34, 34); font-family: 宋体, SimSun;\">平顶山市——叶县——常村乡刘东华村</span></p>','','<p><span style=\"font-family: 宋体, SimSun;\">春夏为佳</span></p>',''),(81,6,'叶县','2019-06-26 11:28:40','叶县有座始建于明代的古建筑“望娘楼”','<p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\"><span style=\"color: rgb(34, 34, 34);\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" background-color:=\"\">望娘楼位于</span>叶县龚店常李村，沿叶邓路到水牛杜村北转到<span style=\"color: rgb(34, 34, 34);\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" background-color:=\"\">神马盐化工</span>开发区一直向东到开发区东边缘。到一加油站北行到向东的大路向东大约500米路北。不过现在已经拆迁啦，只有望娘楼遗世独立。</span></p><p><img src=\"http://p1.pstatp.com/large/pgc-image/1c300637d26d4b7384f78ae3aef123ec\" img_width=\"2976\" img_height=\"3968\" alt=\"叶县龚店乡常李古建筑：望娘楼\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">该楼始建于明代，砖木石结构，灰瓦，三间五层，楼高18.44米，塔形。1985年8月，被列为县级文物保护单位。</span></p><p><img src=\"http://p1.pstatp.com/large/pgc-image/8d6a4fb6528b4f3091cbeaa8c886e588\" img_width=\"2976\" img_height=\"3968\" alt=\"叶县龚店乡常李古建筑：望娘楼\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p><img src=\"http://p1.pstatp.com/large/pgc-image/ef7138f615d44ff48ca3271b288d74b0\" img_width=\"3968\" img_height=\"2976\" alt=\"叶县龚店乡常李古建筑：望娘楼\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">望娘楼的来历：传说叶县邓李乡孙寨村有一个姑娘嫁到了这里。初来乍到，她常常在月明星稀的夜晚思念家乡和母亲，无尽的乡愁无法排遣。因受当时的封建习俗所限，她不可能随意返乡探亲。后来，孙姑娘的婆家人便依照她娘家的一座堂楼模样，建筑了望娘楼。望娘楼建成后，孙姑娘思念母亲时，就登楼眺望娘家，以排遣她的思母恋乡之情。同时，她的娘家与婆家还共同约定，一旦一家有什么急事，即在自家楼顶处悬挂红灯笼一只，对方一见红灯笼，即可采取应急对策；若双方均无红灯笼出现，则表明两家一切平安无事。</span></p><p><img src=\"http://p3.pstatp.com/large/pgc-image/90ab96ef5a66465d8d7f4ae1bad7c631\" img_width=\"2976\" img_height=\"3968\" alt=\"叶县龚店乡常李古建筑：望娘楼\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p><img src=\"http://p3.pstatp.com/large/pgc-image/70701daa6f364036b3a9a9f2e2ccd295\" img_width=\"3968\" img_height=\"2976\" alt=\"叶县龚店乡常李古建筑：望娘楼\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><h1 label=\"标题居中\" style=\"font-size: 32px; font-weight: bold; border-bottom: 2px solid rgb(204, 204, 204); padding: 0px 4px 0px 0px; text-align: center; margin: 0px 0px 20px;\"><br/></h1>','/uploads/20190626/fe855001e2445fde2ab567420e8c28cc.jpg',',,','1561523761',1,0,231,1,'旅途掠影','113.3575','33.62255',50,12,29,70,'<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——叶县——<span style=\"color: rgb(34, 34, 34);\">龚店——常李村</span></span></p>','','<p>一年四季皆可</p>',''),(82,6,'鲁山县','','河南珍藏的最美瀑布，就在鲁山','<p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">河南地处中国中部，不像南方那样水资源丰富，所以河南人对水的感情也更亲切，对瀑布也更是情有独钟。在河南很多的山地景区中都有或大或小的瀑布，有的气吞山河，有的细若游丝，有的如悬空中，有的曲折蜿蜒。在这些众多的瀑布中有挂瀑布，在美景的映衬下显得低调了许多。这就是尧山景区的黑龙潭瀑布。</p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">尧山黑龙潭瀑布又称九曲瀑布，是尧山国家重点风景名胜区主要瀑布之一，尧山山峰奇特，瀑布众多，森林茂密，大山壁立，异峰如塑，怪石纷呈，是尧山自然风光的一大特色。站立主峰玉皇顶上，千岩万壑，飞龙走凤，远近高低，景色迥异，构成了一幅幅美不尽收的图画。黑龙潭瀑布作为景区的重要景观供游客观赏。</p><p><img src=\"http://p3.pstatp.com/large/pgc-image/4ba72daba2504d32b2409348e29ba98e\" img_width=\"640\" img_height=\"457\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p><img src=\"http://p1.pstatp.com/large/pgc-image/8daf329491154049933e04d896b1f01a\" img_width=\"452\" img_height=\"652\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">尧山黑龙潭瀑布高70余米，呈三弯九曲之状，水面根据枯水与丰水期分别宽1-6米不等，由于水流下切磨蚀与岩石承受侵蚀能力不同等因素，断崖上形成数个深度0.3-1.5米不等的凹窝，呈之字形自上而下分布，使壁面凹凸不平，水流形成三弯九曲之状，故也称之为九曲瀑布。</p><p><img src=\"http://p3.pstatp.com/large/pgc-image/d10138ea10db4f34b4198b9573d41358\" img_width=\"640\" img_height=\"373\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p><img src=\"http://p3.pstatp.com/large/pgc-image/89536adf986644159b0e030207f5891c\" img_width=\"600\" img_height=\"401\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">瀑布两边的崖壁上，秀木扶疏，青苔密布，各色野花撒落其间，与雪白的瀑布交相辉映，织成一幅锦绣图案。由于石人山降水丰沛，上游植被繁茂，水源涵养好，瀑水常年不断。瀑布注入下端的黑龙潭，訇响声传出半里之外。</p><p><img src=\"http://p1.pstatp.com/large/pgc-image/8083c3aedaf74729babc2639afcc2b59\" img_width=\"640\" img_height=\"1138\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p><img src=\"http://p1.pstatp.com/large/pgc-image/4afe01c35b5d48f897291c61fbb6c480\" img_width=\"640\" img_height=\"424\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">每到夏季，山水如兽直扑潭中，九穴则全然不见，留下击鼓撞钟般的吼叫，崩花溅玉，一派雷霆万钧、大气磅礴的壮观景象。平日里，潺潺飞流从穴缘泻下，瀑白穴幽，交相映衬。瀑下潭水清澈透底，涟漪阵阵，碧光闪耀，令人唏嘘。</p><p><img src=\"http://p3.pstatp.com/large/pgc-image/7bbadb6e1cac4eeab7928e9b0edef4e8\" img_width=\"640\" img_height=\"427\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p><br/></p>','/uploads/20190627/6ba12da0be6db3679f0c473315d4073a.jpg','/uploads/20190627/6ac3390bc5fd6e681f7bf52a27b00dc0.jpg,/uploads/20190627/cfbdfe087629a97c776f914517a6ccd2.jpg','1561624539',1,0,88,1,'旅途掠影','112.90915','33.73634',50,12,22,39,'<p>第一天：飞机到昆明，然后接机到昆明酒店；</p><p>第二天：昆明—石林—楚雄彝乡恋歌—楚雄酒店；</p><p>第三天：楚雄—洱海游船登金梭岛—天龙八部影视城赠送古装拍照—海滨花卉园—大理酒店；</p><p>第四天：大理古城——丽江—拉市海国家湿地公园—丽水金沙—丽江古城—丽江酒店；</p><p>第五天：束河古镇——玉龙雪山云杉坪索道—丽江酒店第六天：泸沽湖猪槽船—环湖游篝火晚会—住泸沽湖酒店</p><p>第七天：观泸沽湖日出—摩梭村寨—楚雄酒店/火车</p><p>第八天：鲜花市场游客集散中心—送机</p><p><br/></p>','<p>公交线路:20路，全程约4.5公里</p><p>1、从天安门步行约460米,到达天安门东站</p><p>2、乘坐20路,经过6站, 到达天坛西门站(也可乘坐2、120)</p><p>3、步行约30米,到达天坛</p><p><br/></p>','<p>每年3月春分时节，新密市尖山风景区10万余株野生杏树竞相吐蕊，漫山遍野，如云似霞的杏花已经成为当地一道靓丽的风景线。今年的杏花开得更是格外美丽，多数花枝招展、笑脸迎客;个别含苞待放，正忙于梳妆打扮。满山满坡的“粉红色”争奇斗艳。微风拂过，花海荡漾，引来四面八方的游客和摄影爱好者踏青赏花，亲身体验“伏羲故里，魅力尖山;养生之地，长寿之乡”的无限风情。</p><p><br/></p>',''),(83,6,'鲁山县','2019-06-28 00:00:00','清凉夏日去哪玩？鲁山美食节攻略别错过！','<p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34);\"><span style=\"font-family: 宋体, SimSun;\">品美食，望星空，住民宿，6月28日——6月30日，鲁山县团城乡清水河旅游党总支成立揭牌暨2019“宝丰杯”花园沟美食节将盛大开幕！吃货老饕们快来鲁山花园沟村清水源山居享受美食节的饕餮盛宴吧！</span></p><p style=\"white-space: normal;\"><img src=\"http://p9.pstatp.com/large/pgc-image/f8000a1c23604e9cb8d44c329abdafd9\" img_width=\"800\" img_height=\"600\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/6a4e12fe5daa45a1b9fefe2745ebe13f\" img_width=\"466\" img_height=\"573\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\">中华老字号买宝太羊肉杂可</p><p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34);\">为期3天的美食节将以美食为载体、以文化为核心，依托鲁山团城的楚长城遗址、千年辛夷王、万年榆树群等历史文化底蕴和乡村旅游发展优势，围绕开幕仪式、美食和美食器皿（展）、美食文化之旅、夜景灯光秀、乡村文化演出等五大版块，策划推出一系列丰富精彩的活动。</p><p style=\"white-space: normal;\"><br/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"white-space: normal;\"><img src=\"http://p9.pstatp.com/large/pgc-image/7091a62f534245caba7862c1d1986f31\" img_width=\"640\" img_height=\"400\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\">特色串串香</p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/f6bfa54212534fefba1c450256a98c8e\" img_width=\"900\" img_height=\"614\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34);\"><span style=\"font-family: 宋体, SimSun;\">为方便游客体验，美食节共设置6个特色饮食区和4个游玩景点。6个特色饮食区分别是鲁山特色饮食区、平顶山特色名吃名点区、信阳特色饮食区、酒水区、烧烤区、土特产区。4个游玩景点分别是楚长城遗址、千年辛夷王、万年榆树群、南界岭（冷风崖）。不出鲁山，就能让你山水避暑狂欢，吃遍中原美味！</span></p><h1 pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; font-size: 24px; margin: 36px 0px 22px; padding: 0px; line-height: 32px; color: rgb(34, 34, 34);\">【民宿项目】</h1><p style=\"white-space: normal;\"><img src=\"http://p9.pstatp.com/large/pgc-image/60c617c47e004d5fa0175709b16bc3dd\" img_width=\"800\" img_height=\"600\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/af710c1c66f2464b8c4477ceaa51699e\" img_width=\"800\" img_height=\"600\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34);\"><span style=\"font-family: 宋体, SimSun;\">花园沟村位于鲁山县团城乡西南部，清水河源头，属深山区，与南召县一山之隔。花园沟村是深度贫困村。花园沟村旅游资源丰富，历史古迹楚长城，原始风情化石山、铁瓦庙，千年辛夷王，万年榆，珍珠三道瀑等旅游资源如诗如画。充分利用自然景观优美、旅游资源丰富的优势，以民宿旅游项目带动当地群众发展农家乐，走出一条独具特色的脱贫致富新路子。</span></p><p style=\"white-space: normal;\"><img src=\"http://p3.pstatp.com/large/pgc-image/addd0ad6c0684adbab2b2f1ec797281c\" img_width=\"800\" img_height=\"599\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"white-space: normal;\"><img src=\"http://p3.pstatp.com/large/pgc-image/a17b778331cc4e7794dfc20677baf426\" img_width=\"800\" img_height=\"634\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><h1 pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; font-size: 24px; margin: 36px 0px 22px; padding: 0px; line-height: 32px; color: rgb(34, 34, 34);\">【文化展示】</h1><p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34);\"><span style=\"font-family: 宋体, SimSun;\">美食节期间，可以欣赏到或买到鲁山肉石、鲁山花瓷、聚京全蝎，状元驴生态驴肉、买宝太羊肉杂可、李记老字号胡辣汤、西关栅子门老李胡辣汤、德秀红葡萄酒、蒲公英茶、昭平湖银鱼等鲁山特色。</span></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/cd375bbb41cd41cc8fbf9607ee6a9a48\" img_width=\"640\" img_height=\"518\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34);\"><span style=\"font-family: 宋体, SimSun;\">鲁山肉石，是天然石种，属硅质岩石，有玉髓和石英组成，产自河南省伏牛山区。是在地质运动过程中硅质岩石与各种矿物质接触色化而成。在构造麽擦热力和地下水长期作用下，形成不同深浅的红，橙，紫，白的方解石环带状沉淀，各色方解石包绕或充填于巨大的石英岩类构造角砾之间，并交结固一体，就形成了这种形似猪肉状的石。</span></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/7c99f9c6bde0430abd9860d6cfd8db30\" img_width=\"580\" img_height=\"421\" alt=\"清凉夏日去哪玩？鲁山美食节攻略别错过！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34);\"><span style=\"font-family: 宋体, SimSun;\">“蒲公英”茶自古以来都受到人们的广泛关注，在《本草纲目》、《唐本草》、《本草图经》、《纲目拾遗》等十几部中药典著中都有详细记载。</span></p><p><br/></p>','/uploads/20190630/36869539df937a1087c3f64847bec585.jpg','/uploads/20190630/4b04e9949b004c2020abcbd7f9929375.jpg,/uploads/20190630/7a9b4438941f190c47d029d19b343d0e.jpg','1561887468',1,0,104,1,'旅途掠影','112.90915','33.73634',50,12,22,55,'<p><span style=\"font-family: 宋体, SimSun;\">自驾（2天）</span></p>','<p><span style=\"color: rgb(34, 34, 34); font-family: 宋体, SimSun;\">平顶山——鲁山县——团城乡——<span style=\"color: rgb(34, 34, 34); font-family: 宋体, SimSun;\">花园沟村</span></span></p>','<p>一年四季皆可</p>',''),(84,6,'鲁山县','','鲁山藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩','<p style=\"white-space: normal;\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">响马河又叫想马河，位于河南省平顶山市鲁山县尧山镇想马河村境内，距平顶山市区100公里，距鲁山县城66公里。是一个还未完全开发好的一个景区。</span></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/d0fce7afdebb45a49053ff09e027ed2a\" img_width=\"640\" img_height=\"476\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px;\"><br/></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/616fb4fb3d7b4f88ae4dcd156b22641f\" img_width=\"800\" img_height=\"533\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"white-space: normal;\"><img src=\"http://p3.pstatp.com/large/pgc-image/952d0d4fa235492787f40f970122d337\" img_width=\"640\" img_height=\"427\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p><span style=\"font-family: 宋体, SimSun;\">这里生态原始，很少有人涉足，也就是近几年被驴友们发现，才逐渐的被人们所知，即便是这样，相比较其他景区，这已经是很难得了。</span></p><p style=\"white-space: normal;\"><img src=\"http://p3.pstatp.com/large/pgc-image/2c2c0db644044844b636998fe530d156\" img_width=\"508\" img_height=\"900\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px;\"><span style=\"font-family: 宋体, SimSun;\">想马河生态区里的山不是很高，有“横看成岭侧成峰”的意境。山上植被很好，绿树环绕，微风吹过，沙沙的声音宛如清渺的音乐。</span></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/538251c715c44310a1a076c6f3c07d1f\" img_width=\"800\" img_height=\"533\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px;\"><span style=\"font-family: 宋体, SimSun;\">一路上青山秀水，远离喧嚣远离雾霾，亲近自然，说是半开发，是因为这里不是完全的野线，还有一些基本的步道、栈道、过河吊桥。</span></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/061e8341625049b280bc8b66b2ba900a\" img_width=\"640\" img_height=\"407\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/8a8246485a124a8f8504d905167b8701\" img_width=\"700\" img_height=\"467\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px;\"><span style=\"font-family: 宋体, SimSun;\">这里比较适合亲子游玩，不陡不险线路简单，全程10公里左右，还有农家宾馆，可供食宿。清潭瀑布，细沙河石，小鱼、小螃蟹在溪水中自由嬉戏，最是小朋友喜欢的了。</span></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/f80367e3d1f448d08a88e9c76d8aa31c\" img_width=\"640\" img_height=\"426\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p><span style=\"font-family: 宋体, SimSun;\">这里的水很清很绿，没有被污染，小鱼、蝌蚪、螃蟹都悠闲自在。来到这里，好象到了世外桃源，一切烦忧都被这里的秀美洗涤，只留下清清爽爽的感觉，真的让人流连忘返！</span></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/eceb1317f6a5460189e2d6d4023af1ea\" img_width=\"640\" img_height=\"892\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"white-space: normal;\"><img src=\"http://p9.pstatp.com/large/pgc-image/a64aaba8ab5143b98e946257eb035ae4\" img_width=\"640\" img_height=\"892\" alt=\"免票！河南藏匿一奇景，山不高，水很绿，可捕鱼，适合亲子游玩\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px;\"><br/></p><p><br/></p>','/uploads/20190630/5c8366e5755bc015192702556de41b12.jpg','/uploads/20190630/950d91b60f4d5d72ae95fdbc61cb178d.jpg,/uploads/20190630/664ae84c37701ece55fff7794235e3c5.jpg','1561889000',1,0,218,1,'旅途掠影','112.90915','33.73634',50,12,22,39,'<p><span style=\"font-family: 宋体, SimSun;\">自驾</span><br/></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——尧山镇——想马河村</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">夏季为佳</span></p>','象眼旅游'),(85,6,'宝丰','2019-07-01 00:00:00','一个古老且有“味道”的千年历史传统古村落---李庄乡翟集村','<p style=\"margin-top:16px;margin-right:0;margin-bottom:40px;margin-left:0;padding:0 0 0 0 ;line-height:28px\"><span style=\"font-family: 微软雅黑;color: rgb(34, 34, 34);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\"></span></span></p><p style=\"margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px; margin-top: 5px; text-indent: 2em;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255); font-family: 宋体, SimSun;\">翟集村，位于河南省平顶山市宝丰县李庄乡西北部，东临运粮河，西临翟庄村，南临南石公路，北与郏县交界，历史悠久，文化底蕴丰厚，是一个有着一千多年发展历史的古村落。</span></p><p style=\"margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px; margin-top: 5px; text-indent: 2em; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><strong><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px;\">历史建筑</span></strong></span></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px;\"></span></strong></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036059312420.jpg\" title=\"1562036059312420.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036059441743.jpg\" title=\"1562036059441743.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036059658069.jpg\" title=\"1562036059658069.jpg\"/></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px; text-align: center;\"><strong><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">古朴民居</span></strong></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\"></span></strong></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036119289015.jpg\" title=\"1562036119289015.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036119305082.jpg\" title=\"1562036119305082.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036120156382.jpg\" title=\"1562036120156382.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036120626980.png\" title=\"1562036120626980.png\"/></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px; text-align: left; text-indent: 2em;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); letter-spacing: 0px; font-family: 宋体, SimSun;\">翟东王家大院门楼、翟集关帝庙，翟集村古建筑、古民居有着显著的地方特色。翟集村传统建筑规模宏大，类型齐全，这些建筑从明代晚期一直延续到现在。</span><strong><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span></strong></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><strong><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">单体建筑</span></strong></span></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px;\"><strong><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\"></span></strong></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036181100214.jpg\" title=\"1562036181100214.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036181545308.jpg\" title=\"1562036181545308.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036181738901.jpg\" title=\"1562036181738901.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036181487611.jpg\" title=\"1562036181487611.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036181978151.jpg\" title=\"1562036181978151.jpg\"/></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px; text-align: left;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); letter-spacing: 0px; font-family: 宋体, SimSun;\">&nbsp; &nbsp;建筑单体特色突出，群体布局严整，体现了民间朴素的审美价值，具有较高的民俗艺术价值，此外，密布于建筑各处的木雕、石雕、砖雕，手法多样、雕刻精湛、栩栩如生。</span><strong><span style=\"font-family: 微软雅黑; color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br/></span></strong></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><strong><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">陈醋飘香</span></strong></span></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px;\"><span style=\"color: rgb(34, 34, 34); font-family: 微软雅黑; letter-spacing: 0px;\">&nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 宋体, SimSun;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px;\"> 早在商代盘庚时期，翟集居民即借当地运粮河泉水打曲淋醋。</span><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); letter-spacing: 0px;\">2000多年来，翟集醋久盛不衰。 据了解，目前翟集村以户为作坊的加工坊就有百余家，已经形成了生产、加工、销售一条龙米醋产业链。</span></span></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); font-family: 微软雅黑; letter-spacing: 0px;\"></span></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036260257196.jpg\" title=\"1562036260257196.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036260153351.jpg\" title=\"1562036260153351.jpg\"/></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190702/1562036260522137.jpg\" title=\"1562036260522137.jpg\"/></p><p style=\"margin-top: 16px; margin-bottom: 40px; white-space: normal; padding: 0px; line-height: 28px;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); font-family: 微软雅黑; letter-spacing: 0px;\">酒香在宝丰，醋香在翟集。每个古村落都惊艳了时光，典型民居古建筑，翟集飘香冯异醋，一个古老且有“味道”的地方。</span></p>','/uploads/20190702/99b0053ef75279b002bd582c8919a788.jpg','/uploads/20190702/8f334a8414e77080f03c2a2648ce964d.jpg,/uploads/20190702/3cc91bfcb4143a2b129be8d562f0ad36.jpg','1562036513',1,0,106,1,'旅途掠影','114.25965','30.58015',50,12,25,80,'<p><span style=\"font-family: 宋体, SimSun;\">自驾</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——宝丰——<span style=\"color: rgb(34, 34, 34); text-indent: 32px; background-color: rgb(255, 255, 255);\">李庄乡——<span style=\"color: rgb(34, 34, 34); text-indent: 32px; background-color: rgb(255, 255, 255);\">翟集村</span></span></span></p>','<p>一年四季皆可</p>','');
INSERT INTO `ddsc_rural` VALUES (87,6,'叶县','','叶邑古镇主要景点和古迹','<p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">春秋古叶邑，留下了四十多处遗迹，已成为新的旅游景点，有的与叶公相关，有的与刘秀相关，有的与王乔相关，每个古迹景点都有一个美丽的故事。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; color: rgb(255, 41, 65); line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-family: 宋体, SimSun;\"></span><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; color: rgb(0, 0, 0);\"><span style=\"font-family: 宋体, SimSun;\">叶邑</span>主要古迹</span></span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"color: rgb(0, 0, 0); font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">一、古城墙</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">在叶邑故城的西、南、北部古城郭遗址尚存。现存古城墙多位于叶邑故城西南和西北方，总长约4.5公里，宽8—10米，最高约5.5米。城墙断面可见明显的夯层，土质细腻坚硬，包含有砂石、贝壳等物。据资料介绍，叶邑故城的周长约9公里。但从今天叶邑故城的古城郭遗址来考证，东城墙遗址位于1975年开挖的新河沟中，西、南、北城墙遗址尚存，南北长约2500米，东西宽约1000米，叶邑故城的实际周长约7000米。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207708310516.jpg\" title=\"1562207708310516.jpg\" alt=\"微信图片_20190704095112.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">叶邑故城的古城墙始筑于春秋时期许国在此建都期间，距今已有2500多年的历史了，城墙规模最终定型于叶公治叶时期，即距今2500年左右的春秋时期。此后至阜昌七年（南宋绍兴七年，公元1137年）县城北迁至今叶县县城止，其城墙规模被不断的修复和加固。县城迁走后，叶邑故城城墙的修复和加固情况也就明显不如以前了。清咸丰四年（1854年），旧县武举人陈璋、胡锡田、郭汉文三人曾率领旧县附近村民对叶邑故城的古城墙进行过一次修复。民国六年（1917年），因草寇出没，百姓受害，叶邑故城的城墙太长不易防守，旧县人张宝和、张心广、张懔一等三人率众在叶邑故城的东南角缩建成新寨。新寨的北边缩至中村与北村的交界处，西边缩至叶邑故城西城墙东边，南边和东边仍为叶邑故城的老城墙，新寨的面积不足叶邑故城的四分之一。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">建国初期，由于垦田开荒和挖土，叶邑故城的古城墙遭受到一定程度的破坏。在1975年开挖新河道时，东城墙遭到严重毁坏。所以，现存的古城墙多位于叶邑故城的西南和西北方。在平顶山市博物馆中展出的就有叶邑故城古城墙的图片。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; margin: 0px; padding: 0px; max-width: 100%; color: rgb(255, 41, 65); box-sizing: border-box !important; overflow-wrap: break-word !important;\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"color: rgb(0, 0, 0); font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\">二、老县衙</span></strong></span><span style=\"color: rgb(0, 0, 0); font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">叶邑故城的老县衙位于今叶邑镇中村与南村交界的堂门口西约60米路北的高地上（小土地庙北边上面）。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207675390960.jpg\" title=\"1562207675390960.jpg\" alt=\"微信图片_20190704095227.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">距离老县衙遗址东约60米的十字路口，今天人们仍叫它“堂门口”，其意即为距离县衙大堂不远的路口。从西周时期到阜昌七年（即南宋绍兴七年，公元1137年）县城北迁至今叶县县城止，其间2000多年的时间里，叶地的行政办公场所几乎都在叶邑故城的老县衙，曾在这里办过公的人员有：春秋时的许灵公等许国国君；春秋时的叶公沈诸梁；东汉时的县令王乔；北宋县令梅尧臣；北宋县尉黄庭坚。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">三、东公馆和西公馆</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">东公馆位于今叶邑镇中村村委会大门（老供销社大门）对面，即叶邑镇中村村民王成家的住宅处；西公馆位于东公馆斜对面稍偏南的老供销社门面房后院，即今叶邑镇中村村委会楼房东南侧前方。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207661824328.jpg\" title=\"1562207661824328.jpg\" alt=\"微信图片_20190704095302.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">东公馆和西公馆是专门用于接待各级官员用的官家馆舍，其始建年代可能是在春秋时期叶公沈诸梁治叶时，最迟也不会晚于东汉初年。光武帝刘秀、汉安帝刘祜及清朝道光帝等都曾在此下榻。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207626822559.jpg\" title=\"1562207626822559.jpg\" alt=\"微信图片_20190704095321.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">四、关羽大战夏侯惇处</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">关羽大战夏侯惇处位于今叶邑镇南村黑虎桥以南，平桐路以北的地段。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207614745224.jpg\" title=\"1562207614745224.jpg\" alt=\"微信图片_20190704095421.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">据《三国志》等史料记载，为了削弱刘备的力量，建安七年(202年)，刘表让刘备向北进攻曹操，曹操派夏侯惇、于禁、李典等领兵南下迎敌，双方在叶县（当时的叶县县城在叶邑故城）相遇并展开了激战。相传关羽曾大战夏侯惇，今叶邑镇南村黑虎桥以南、平桐路以北的地段，即为当年关羽大战夏侯惇之处。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); text-align: left;\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">五、黄公祠</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">黄公祠位于今叶邑镇北村清真寺东北方约100米处路东，黄公祠是叶地百姓为纪念北宋叶县县尉黄庭坚而修建的祠堂。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207601674602.jpg\" title=\"1562207601674602.jpg\" alt=\"微信图片_20190704095445.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">黄庭坚（1045—1105年）于宋英宗治平四年(1067年)中进士后，第二年即宋熙宁元年（1068年）被任命为叶县尉。黄庭坚是继叶公、王乔之后被叶地民众设祠供奉的第三位地方官员。2018年2月4日，据叶邑镇北村82岁的村民买文山老人回忆说，原北大坑西沿外立有纪念黄庭坚的石碑，后来石碑倒了，今石碑已不知何处。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">黄公祠是纪念黄庭坚的祠堂，始建年代在公元1105年黄庭坚去世后的北宋时期，距今已有900余年的历史了。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">六、澧水长桥</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">澧水长桥位于今叶邑镇北村刘秀庙东北方澧河北岸的河滩之中。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207586613341.jpg\" title=\"1562207586613341.jpg\" alt=\"微信图片_20190704095510.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">相传唐朝时有一个劫匪首领名叫李贵（有说叫李魁），有一次他带人劫获了一大笔财物，事后得知是送给杨贵妃的寿礼。这下李贵感到了事情的严重性，于是就和众人商议对策，最后他们决定以杨贵妃的名义用这笔巨款在澧河上修建一座石桥。他们立即组织工匠不分昼夜地加紧施工，等到朝廷派人来追查此事时，石桥已经基本建成了，桥头还立有一通石碑，上刻“杨贵妃舍金银修澧水长桥七十二孔”。来追查此事的官员见状，只好回去如实禀报了此事，杨贵妃见事已至此，便做了个顺水人情，也就不再追究此事。这就是澧水长桥的来历，至今附近的村民仍叫它“李贵桥”（也有人叫它李魁桥）。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">牛凤所著明嘉靖《叶县志》中记载，澧水长桥为叶县八景之一。在《重修澧水石梁记》中有嘉靖八年修澧水桥的记载。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">1983年叶县人民政府、叶县地名委员会地名办公室编著的《河南省叶县地名资料汇编》写道：“清乾隆三十二年（1767年）征缅甸之军就是从这里经过的。该桥民国初年毁于兵燹。”今其遗迹尚存，残留于澧河滩中。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">七、兴庆禅院</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">兴庆禅院位于今叶邑镇中村西南，叶邑镇中村与南村交界的堂门口西北方约150米处。叶邑镇中村村民80多岁的杨文超老人回忆说，这里原是他家的田地，过去他和父亲在这里干活时，时常会刨出很多的瓷碗和瓷盘碎片。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207549192867.jpg\" title=\"1562207549192867.jpg\" alt=\"微信图片_20190704095536.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">兴庆禅院始建于公元996年之前的北宋初年或唐朝至五代时期。兴庆禅院原有佛顶骨二粒，感应舍利子四十粒后增至一百粒。北宋至道二年丙申年（996年）四月初一，兴庆禅院僧众将一百粒感应舍利子和两粒佛顶骨，同葬于佛殿之内。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">1997年4月2日，中村七组村民张广林在吃土时挖出一个石函，外有鎏金铜佛像四尊，内有两瓶感应舍利（其中一瓶被毁坏），现藏于叶县博物馆内。石函分座、体、盖三部分，座为四方体，体、盖为八角柱形。盖顶有铭文曰：“有宋至道二年，岁次丙申，四月癸巳朔，汝州叶县兴庆禅院先有释迦佛感应舍利四十粒增至一百粒，佛顶骨二粒，同葬佛殿内，讫谨记。作持沙门智深、藏主僧惠满、堂中首座僧法瑍、院主小师遵仪、维那小师遵道、典座小师遵彭。”</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">感应舍利虽比不上释迦牟尼佛的真身舍利，但也是佛教中难得的圣物，而北宋至道二年兴庆禅院佛殿下葬感应舍利一百粒和佛顶骨二粒的事实，足以显示出兴庆禅院在北宋时的宏大规模和特殊地位。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">八、清真寺</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">清真寺位于今叶邑镇北村中段。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207529244831.jpg\" title=\"1562207529244831.jpg\" alt=\"微信图片_20190704095613.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">1986年8月所立石碑《重修清真寺垣墙碑记》中记载：“据传始建于元末明初。”这个时间与伊斯兰教在我国的发展历史是相符合的，距今已有700年左右的历史了。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">生于二十世纪四十年代的叶邑镇北村村民买喜亮，曾记得黄公祠的碑文中有“黄公祠坐落于县城北关，清真寺东北斜对过官道东”的记载，若据此来考证，叶邑镇北村清真寺在黄公祠创建时就已经存在，黄公祠已有900余年历史，叶邑镇北村清真寺的始建年代应在北宋时期，已有近千年的历史了，是河南省最早的一所清真寺。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">原清真寺内有正殿三楹，北讲堂三间，还有厢房若干间。正殿内有名人山水画，还有两幅对联。一幅为“主德深鸿造天地包藏万物，圣恩光大教感贤独尊一人”，另一幅为“明灯高照天堂路，圣经难劝迷人心”。正殿前首有卷棚三间，结构严谨，造型美观，周围木雕栩栩如生。清真寺大门建造也十分别致，门前石阶丈余高。寺内有古柏一株，遮天蔽日，十分壮观。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">相传清道光初年，云南发生地方叛乱。在平定云南叛乱时，曾受教于叶邑故城清真寺的叶、马二位将军立下了赫赫战功，得到了道光帝的大加赞赏。道光帝再次路经叶邑故城时，曾专门来到清真寺里致谢，并称赞伊斯兰教为“亘古至教”。原清真寺大门上方悬挂有道光年间“亘古至教”的匾额，现在此匾额依然被保存着。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">今清真寺内保存有清光绪九年（1883年）与民国六年（1917年）所立的石碑，还有一通古残碑，今已无法辨认其确切年代，有人说是清嘉庆年间的石碑。清真寺内还有一株千年古柏，这株古柏生的十分奇特，一半为刺柏，另一半为血柏。2006年6月，叶县县政府制牌，上面写有“国家一级古树，树龄1000年”等内容。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">九、城隍庙</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">城隍庙位于今叶邑镇北村清真寺西北方约200米处，北村小学东侧，原城隍庙遗址在今叶邑镇北村小学院内。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207505424411.jpg\" title=\"1562207505424411.jpg\" alt=\"微信图片_20190704095700.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\"><br style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"/></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">城隍庙的始建年代在南北朝时期，最迟也不会晚于唐朝初期,此后历代重修。从明朝万历十五年（1587年）到万历十六年（1588年），在当地百姓们的积极参与下，曾经对城隍庙进过一次较大规模的重修。明万历十五年（1587年）任叶县县令的高文登在《重修城隍庙乐楼记》和《重修旧县城隍庙记》中较详细的记载了这次重修的情况。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">在今叶邑故城的城隍庙中还保存有一对古石狮，其独特造型甚是少见。相传唐朝时，有一队江南的人马，要向京城长安运送一对石狮子，路经叶邑故城。当他们走到城隍庙附近时，不知为什么，马车走不动了，任凭车夫用鞭子怎么去抽打那拉车的马，车子还是走不了。领队的人来到城隍庙里上香叩头，还是不管用。最后无奈，那领队的人祷告说，如果是城隍爷看中了这对石狮子，想让它们在此侍奉城隍爷的话，那就让马车能走动。说来也奇怪，领队的人话音刚落，马车真的就能走动了，并径直向城隍庙走去。那领队的人看到这种情景就说，即然是这样，那就让这对石狮子留在这里侍奉城隍爷吧，我们回去如实向主人禀报。这就是叶邑故城城隍庙中那对古石狮的来历。在文化大革命期间，这对石狮子受到了一小部分损伤，北村村民将它们埋在了地下。文革过后，村民们把它们从地下挖了出来，重新立在了城隍庙门前。后来曾有人两次来偷这对石狮子，但是都没有得逞。现在出于保护文物的需要，村民们把这对石狮子安放在了城隍庙院内。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">另外，叶邑故城的城隍庙大殿前面有一道照壁，关于这道照壁的来历还有一个传说。相传明清时期，今叶县夏李乡五里河村有一张姓人家（今这家张姓后人仍居住在五里河村），其家有一儿子在叶邑故城的问政书院中读书，上学、放学都要从城隍庙前经过。有一天夜晚，在城隍庙中做工的一个泥水匠师傅做了一梦，梦见城隍爷对他说，有一个大人物经常从城隍庙门前经常，每当这时城隍爷就要出来迎送，这样太麻烦了，如果能够在大殿前修一道照壁遮挡，就谁也看不见谁了，城隍爷也不用那么麻烦了。这个泥水匠师傅就把自己做的梦告诉了大家，于是在叶邑故城的城隍庙大殿前面便有了一道照壁。五里河村张家的那个男孩长大之后，在朝中做了官，因攻破了一座非常坚固的敌城，被升至总兵（相当于今天的军分区司令员）。后来皇帝曾钦赐匾额，上书“大夫弟”三个金字，匾额四角与上方各有一条龙，一直保存到文革前。文革时期，张家怕因此而受到迫害，便用木工用的刨子刨平了匾额上的字，把匾额当作木板来用了。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">道书《诸神诞日玉匣记等集》以五月十一日为城隍圣诞日，该日城隍庙即举行祭祀。叶邑故城城隍庙的传统庙会时间是每年的农历三月十五日。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">十、神州圣母殿</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">神州圣母殿位于今叶邑镇中村与南村交界的堂门口西北方约150米处的兴庆禅院遗址北侧。据叶邑镇中村村民80多岁的杨文超老人回忆说，以前这里与他家的田地相邻，此处原有三通石碑。今叶邑镇北村刘秀庙内保存的明万历二十八年春所立《创修神州圣母殿併绘像记》石碑就是从这个遗址中找到的。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207465849973.jpg\" title=\"1562207465849973.jpg\" alt=\"微信图片_20190704095733.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">在初建时，这里是一座佛教的寺院，寺院始建于北宋乾德三年，即公元965年。该寺院从明万历二十一年（1593年）开始重修，至万历二十八年（1600年）重修告成，除重修了原有的佛殿外，又增建了神州圣母殿，但从总体来看这里仍是一座佛教的寺院。现在80岁左右的老人们还清楚的记得，原先人们都把这里叫做“尼姑庵”。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">明万历二十八年（1600年）春由汝州卫生员朱尧治撰文，住持僧尼喜安，徒传仓、传来仝立镌字囗李守连，男李存彦、张守金等立《创修神州圣母殿併绘像记》石碑一通。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\">十一、观音寺</span></strong><strong style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; font-family: 宋体; line-height: 17.6px; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></strong><span style=\"font-size: 16px; margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体; box-sizing: border-box !important; overflow-wrap: break-word !important;\"></span></span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">观音寺位于今叶邑镇北村小葛庄，2016年据叶邑镇中村村民74岁的张德老人回忆说，今叶邑镇北村刘秀庙内保存的“重修观音寺”石碑帽就是他与别人一起在这里找到的。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">观音寺始建于唐宋时期，确切年代有待进一步考证，明洪武十六年（1383年）僧普明曾率众进行过重修。在明嘉靖《南阳府志》和清朝吕柳文、崔赫所著康熙《叶县志》中均有关于观音寺的记载”。据今叶邑故城七八十岁的老人们回忆说，这是一座规模相当大的寺院，有的说是五进院，有的说是七进院。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207426974734.jpg\" title=\"1562207426974734.jpg\" alt=\"微信图片_20190704095810.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">在叶邑故城至今仍流传着一个关于观音寺僧人的故事。相传有一年，山东的一位官员来到观音寺看望住持，住持是他的朋友，据说住持是皇宫里一个太监的侄子。住持看到朋友远道而来，便亲自外出打酒款待这位官员。这位官员便自己在寺里参观，无意中他触动了寺里的暗道机关，打开了暗道，看到了暗道中有不少被囚禁的年轻女子。原来观音寺中挖有暗道，并设置了机关。近年来，有不少年轻貌美的女子来此进香后就莫名其妙的失踪了，其实是这里的僧人们打开了暗道机关，她们便掉入陷井内，然后被囚禁在暗道密室中，被这里的僧人们糟蹋伤害。这位山东的官员得知情况后，大吃一惊，决定进京上报此事。住持打酒回来得知这位官员已经知道了寺中暗道的秘密后，便在酒中下了毒，准备杀人灭口。这位机智的官员看出了其中的破绽，假装喝了毒酒，实际上是将毒酒倒进了衣袖中，然后装死，并趁机逃了出去。这位官员赶到京城后，将此事上报朝廷，朝廷立即派人调查，并查清了事情的真相。由于观音寺住持是皇宫里一个太监的侄子，于是负责查办这件事情的官员请示皇上应该怎么处理，由于涉及到皇宫里的太监，皇上便叹了口气说：“罢了。”那位负责查办此事的官员将“罢了”二字理解成了“耙了”，于是下令将观音寺中的僧人全部活埋，只把头露出地面，然后套上牲口像耙地似的将这些僧人的头给耙了。这些僧人们在观音寺中接受着四方大众的供养，不思弘扬佛法，度化众生，反而在佛门清净之地干出这些伤天害理的勾当，既违背了佛法，也违背了国法，受此活埋耙头之果报，亦是因果报应的体现，正如《太上感应篇》开篇说的那样：“祸福无门，唯人自召，善恶之报，如影随形。”</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562207396670960.jpg\" title=\"1562207396670960.jpg\" alt=\"微信图片_20190704095922.jpg\"/></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51);\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; line-height: 17.6px; font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0); box-sizing: border-box !important; overflow-wrap: break-word !important;\">今天在观音寺遗址处仍留有暗道塌陷的明显痕迹，原暗道及密室为砖砌内壁，向北延伸约60米后又分成两道，一道向西北延伸，另一道向东北延伸，其外形很象一条衣裤，所以人们把这里叫作 “裤子坑”。在今车夫庙西北约300米处，有一块两岗加一洼的地方，这里就是当年观音寺僧人被活埋耙头的地方，人们把这里叫做“耙耻沟”。</span></p><p helvetica=\"\" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" yahei=\"\" font-size:=\"\" letter-spacing:=\"\" text-align:=\"\" white-space:=\"\" background-color:=\"\" text-indent:=\"\" line-height:=\"\" box-sizing:=\"\" border-box=\"\" overflow-wrap:=\"\" break-word=\"\" style=\"margin-top: 0px; white-space: normal; padding: 0px; max-width: 100%; min-height: 1em; color: rgb(51, 51, 51);\"><br/></p>','/uploads/20190704/75406cdd821c4fd97c717e4e2b7e09e5.jpg','/uploads/20190704/a555039d1f35d97c4355e1458e4e776a.jpg,/uploads/20190704/87faab1609068958a00906cf17bbe635.jpg','1562207715',1,0,26,1,'旅途掠影','113.12527','33.52143',50,12,29,60,'<p>自驾<br/></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——叶县——<span style=\"color: rgb(51, 51, 51); letter-spacing: 0.544px; text-align: justify; text-indent: 36px; background-color: rgb(255, 255, 255);\">叶邑镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">春秋季节为佳</span></p>',''),(88,6,'鲁山县','','夏天，该避暑了，推荐个河南省内既好玩又免费的景区-鲁山珍珠潭','<p><span style=\"letter-spacing: 1px; text-indent: 36px; font-family: 宋体, SimSun; font-size: 16px;\">珍珠潭，位于河南省鲁山县四棵树乡东平沟，素有河南的&quot;九寨沟&quot;之称。传说古代有一人，名叫李桥，时运不济，采金钗石斛跌落湖中得一宝珠后时来运转，所以此谷得名转运谷，又称好运谷。里面集中了10至70米落差的瀑布10多条。瀑下有潭，瀑潭相连。</span></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><img src=\"http://p3.pstatp.com/large/pgc-image/1eecc6122a4d4068b93ca64d2b0745d0\" img_width=\"1080\" img_height=\"1920\" alt=\"夏天，该避暑了，推荐个河南省内既好玩又免费的景区-鲁山珍珠潭\" inline=\"0\" style=\"font-size: 16px; -webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; color: rgb(34, 34, 34);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">又到了漂流的季节，河南省内漂流首推鲁山尧山漂流，特别是尧山第一漂，漂流玩完以后，你可以到鲁山四棵树珍珠潭风景区看看。</span></p><p style=\"white-space: normal;\"><img src=\"http://p1.pstatp.com/large/pgc-image/8efdcca8e88c438f887e3ed53cfff559\" img_width=\"1080\" img_height=\"1920\" alt=\"夏天，该避暑了，推荐个河南省内既好玩又免费的景区-鲁山珍珠潭\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; color: rgb(34, 34, 34);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">珍珠潭位于平顶山鲁山县东平沟，这个地方距离尧山很近。目前景区还没有开发好，免费的，收十元停车费。到鲁山四棵树乡207国道与017县道交叉口，沿017县道一路上山，山路都是水泥路，路况可以，只不过这条路百转千回，急弯多，许多地方一面峭壁，一面悬崖，加上路窄，切记不可开车太快，注意力一定要高度集中。途中有军事漂流，快到山顶有个文殊寺，该寺何年始建，已不可考，有确切证据的历史则有600余年。院内最引人注目的，是这几株千年银杏树。秋天去看，少有的美景。翻过山顶，有个岔路口，一条路是通往尧山的，一条路是通往珍珠潭景区的。</span></p><p class=\"pgc-img-caption\" style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><img src=\"http://p3.pstatp.com/large/pgc-image/153e9b8c4242473784d4895ae0122536\" img_width=\"1080\" img_height=\"1920\" alt=\"夏天，该避暑了，推荐个河南省内既好玩又免费的景区-鲁山珍珠潭\" inline=\"0\" style=\"font-size: 16px; -webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" style=\"margin-top: 16px; margin-bottom: 16px; white-space: normal; -webkit-tap-highlight-color: transparent; box-sizing: border-box; padding: 0px; color: rgb(34, 34, 34);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">整个景区处在大山之中，非常的原始。青山、瀑布、乱石、水潭更是令人们无限向往。沿途从上到下大大小小二十多道瀑布和深潭，很多地方没有道路，只有栈道，仅能一个人通过，是一个真正充满探险的地方，这个景区一直被河南的驴友视为经典的户外穿越景区。风景迷人。山清水秀，原始自然，惬意舒心。</span></p><p><span style=\"font-family: 宋体, SimSun;\"><br/></span></p><p style=\"white-space: normal;\"><img src=\"http://p3.pstatp.com/large/pgc-image/2f1ded47f46c451a9942c81e60e3b18c\" img_width=\"1080\" img_height=\"1920\" alt=\"夏天，该避暑了，推荐个河南省内既好玩又免费的景区-鲁山珍珠潭\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p><span style=\"letter-spacing: 1px; text-indent: 36px; font-family: 宋体, SimSun; font-size: 16px;\"><br/></span><br/></p>','/uploads/20190704/12b49acba54f8ae3899f17e8405fc9fe.jpg','/uploads/20190704/c1382f302b9c38f86e0ef05f40717e55.jpg,/uploads/20190704/d207ac9b87b2368e6e616b8099283139.jpg','1562209128',1,0,48,1,'旅途掠影','112.90915','33.73634',50,12,22,53,'<p>自驾<br/></p>','<p><span style=\"font-size: 16px; color: rgb(0, 0, 0); font-family: 宋体, SimSun; text-decoration: none;\">平顶山市——鲁山县——四棵树乡——平沟村</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">夏季为佳</span></p>',''),(89,9,'叶县','','兴龙禅寺：大战昆阳城刘秀成真龙','<p><span style=\"text-align: justify; color: rgb(0, 0, 0); text-indent: 28px; font-family: 宋体, SimSun; font-size: 16px; background-color: rgb(255, 255, 255);\">叶县兴龙禅寺，坐落于河南省平顶山叶县叶邑镇旧县街南村。南面燕山水库，北面刘秀庙，叶公墓，叶县县衙，东临烧车河，交通方便，风景秀丽，地理位置独特；站在寺院向四周眺望，是一望无际的田野，可称得上是一个远离尘嚣的僻壤。更是一个适合修心静养的好地方。<span style=\"text-align: justify; color: rgb(0, 0, 0); text-indent: 28px; font-family: 宋体, SimSun; font-size: 16px; background-color: rgb(255, 255, 255);\"><br/></span></span></p><h2 style=\"background: rgb(255, 255, 255); margin: 0px; padding: 0px; text-align: center; line-height: 40px; text-indent: 0px;\"><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;<img title=\"1562210770438437.png\" alt=\"图片1.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562210770438437.png\"/></span><br/></h2><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">叶县兴龙禅寺、坐落于河南省叶县南</span>15<span style=\"font-family: 宋体;\">公里叶邑镇南村，依山傍水，景秀地灵。图为寺门前的观音和地藏王菩萨塑像。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562210786895435.png\" alt=\"图片2.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562210786895435.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">叶邑镇，原名旧县乡，是叶县的旧县城所在地，古昆阳城的原址，东汉帝国开国奠基之战</span>“<span style=\"font-family: 宋体;\">昆阳之战</span><span style=\"font-family: Arial;\">”</span><span style=\"font-family: 宋体;\">就发生在这里。经此一站，刘秀名声大振，奠定立国之基。图为寺门。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562210805615643.png\" alt=\"图片3.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562210805615643.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">王莽篡汉后因改革失败，搞得民生凋敝，民不聊生，农民纷纷揭竿而起。汉室后裔刘秀起兵南阳，后他领导的</span>8000<span style=\"font-family: 宋体;\">名义军在昆阳城被王莽军队</span><span style=\"font-family: Arial;\">30</span><span style=\"font-family: 宋体;\">万人重重包围。图为寺门前的通道和地藏王雕塑。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;<img title=\"1562210889555623.png\" alt=\"图片4.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562210889555623.png\"/></span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">刘秀突围求的援军后亲以</span>3000<span style=\"font-family: 宋体;\">骑精锐猛冲敌营，里应外合，大败王莽军缴获粮草辎重无数，将损坏的运粮草的车辆烧毁在昆阳城外一条小河旁，后人称之为</span><span style=\"font-family: Arial;\">“</span><span style=\"font-family: 宋体;\">烧车河</span><span style=\"font-family: Arial;\">”</span><span style=\"font-family: 宋体;\">。（寺内鼓楼）</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562210907843637.png\" alt=\"图片5.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562210907843637.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">后烧车河突发洪水，洪水退后出现三尊铁佛，光武帝为应天意，顺民心，特在小灵山上安抚建寺，命名为</span>“<span style=\"font-family: 宋体;\">铁弗寺</span><span style=\"font-family: Arial;\">”</span><span style=\"font-family: 宋体;\">，后为显示古城寺院之广，遂更名为南禅寺。后人为纪念刘秀，更名为</span><span style=\"font-family: Arial;\">“</span><span style=\"font-family: 宋体;\">兴龙禅寺</span><span style=\"font-family: Arial;\">”</span><span style=\"font-family: 宋体;\">。（图为寺内古碑）</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\"><img title=\"1562210938956474.png\" alt=\"图片6.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562210938956474.png\"/>&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">兴龙者，真龙天子兴起之意也，此寺是为纪念东汉开国皇帝刘秀所建。根据寺内所存残碑记载，兴龙禅寺始建于东汉光武帝年间，占地</span>40<span style=\"font-family: 宋体;\">亩， 常任僧人</span><span style=\"font-family: Arial;\">200</span><span style=\"font-family: 宋体;\">余单，碑词曰</span><span style=\"font-family: Arial;\">:“ </span><span style=\"font-family: 宋体;\">灵山东映南禅寺，昆水北流兴龙潭。</span><span style=\"font-family: Arial;\">”</span><span style=\"font-family: 宋体;\">（图为寺内大雄宝殿）</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562210954338831.png\" alt=\"图片7.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562210954338831.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">兴龙禅寺建成后香火鼎盛，但是后历经战乱，寺庙遭到破坏。</span> <span style=\"font-family: 宋体;\">据残碑记载，唐大太宗时期，</span> <span style=\"font-family: 宋体;\">公元</span>598<span style=\"font-family: 宋体;\">年重修兴龙禅寺。（图为法堂）</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562210971129430.png\" alt=\"图片16.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562210971129430.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">宋朝诗人黄庭坚曾到访兴龙禅寺，写下一首诗作《登南禅寺怀裴仲谋》：茅亭风入葛衣轻，坐见山河表里清。归燕略无三月事，残蝉犹占一枝鸣。天高秋树叶公邑，日暮碧云樊相城。别後寄诗能慰我，似逃空谷听人声。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562210985748843.png\" alt=\"图片17.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562210985748843.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">随着时间的流失，历来为军事重镇的昆阳城更是屡遭战火，寺院遭到严重破怀。</span>2014<span style=\"font-family: 宋体;\">年，在政府的大力支持和信众支持下，兴龙禅寺开始复修，如今基本完工。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562211003441834.png\" alt=\"图片18.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562211003441834.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">寺门前高高矗立着观世音菩萨和地藏王菩萨的塑像，连底座得有二十米高，远处就可望见，非常有气势。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562211020658033.png\" alt=\"图片19.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562211020658033.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">寺主体分成两个大院，院内有门连通。两个院，东侧的匾上题</span>“<span style=\"font-family: 宋体;\">南禅古寺</span><span style=\"font-family: Arial;\">”</span><span style=\"font-family: 宋体;\">，西侧的题</span><span style=\"font-family: Arial;\">“</span><span style=\"font-family: 宋体;\">兴龙禅寺</span><span style=\"font-family: Arial;\">”</span><span style=\"font-family: 宋体;\">。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562211060732517.png\" alt=\"图片20.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562211060732517.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">寺内殿宇错落有序，布局紧凑。每个殿都雕梁画栋，显得非常精美。角楼上风铃叮当，寺院里诵经声音不断。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562211079317048.png\" alt=\"图片21.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562211079317048.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">寺前有水库，周围在绿化，庙东不远处正在修建的一座高塔。叶县兴龙禅寺是一个佛教信众值得常去的地方，游客也不仿到此寻访散心，在此放松尘世间的杂念，寻找自己的初心，得到一份宁静。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562211099195171.png\" alt=\"图片22.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562211099195171.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">寺内精美的佛像。</span></span></p><p style=\"background: rgb(255, 255, 255); text-align: center; text-indent: 0px; margin-top: 30px; margin-left: 0px;\"><img title=\"1562211119386844.png\" alt=\"图片23.png\" src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190704/1562211119386844.png\"/><span style=\"letter-spacing: 0px; font-family: Arial; font-size: 12px;\">&nbsp;</span></p><p style=\"background: rgb(255, 255, 255); margin: 26px 0px 0px; padding: 0px; text-align: justify; line-height: 24px; text-indent: 0px; -ms-text-justify: inter-ideograph;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; font-family: Arial; font-size: 16px;\"><span style=\"font-family: 宋体;\">寺内精美的天王像。</span></span></p><p><span style=\"font-family: Calibri; font-size: 14px;\">&nbsp;</span></p><p><span style=\"text-align: justify; color: rgb(0, 0, 0); text-indent: 28px; font-family: 宋体, SimSun; font-size: 16px; background-color: rgb(255, 255, 255);\"><br/></span></p>','/uploads/20190704/f09759c002a8c9a903b64773c420504e.png','/uploads/20190704/af85b08ef54a6a63d95a93c6263abed2.png,/uploads/20190704/920f2d291c6fb67d6c57abce055bc372.png','1562211163',1,0,65,1,'旅途掠影','113.3575','33.62255',50,12,29,60,'<p><span style=\"font-family: 宋体, SimSun;\">自驾</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山——叶县——</span><span style=\"font-family: 宋体, SimSun; text-align: justify; text-indent: 28px; background-color: rgb(255, 255, 255);\">叶邑镇<span style=\"font-family: 宋体;\">——</span><span style=\"font-family: 宋体;\">旧县街——<span style=\"font-family: 宋体, SimSun;\"></span>南村</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">一年四季皆可</span></p>',''),(90,6,'鲁山县','','尧山北麓的自然风景区，汇集100多个景点，鸟语花香却人迹罕至！','<p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun;\">在尧山风景名胜区北麓，有一处自然风景区因其得天独厚的环境优势，吸引了众多画眉鸟栖息繁衍，故被称为“画眉谷”。</span></p><p><img src=\"http://p1.pstatp.com/large/pgc-image/1532766708023bd759fd31d\" img_width=\"1000\" img_height=\"700\" alt=\"尧山北麓的自然风景区，汇集100多个景点，鸟语花香却人迹罕至！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun;\">画眉谷属于国家4A级旅游景区，景区内各类景点100余处，植被覆盖率达95%，景区内有碧潭成串的盘龙谷；有碧波荡漾、拥有“袖珍三峡”美誉的杜鹃湖；有布满红色岩石的红石峡；有声势浩荡的红石瀑、六叠瀑；还有争奇斗艳的杜鹃花海等等，景区因秀美的自然山水风光又被称为“田园仙境”、“山水画廊”。</span></p><p><img src=\"http://p3.pstatp.com/large/pgc-image/15327667179127d59483966\" img_width=\"500\" img_height=\"375\" alt=\"尧山北麓的自然风景区，汇集100多个景点，鸟语花香却人迹罕至！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun;\">另外，还有省第一家回旋漂流景区——画眉谷碾盘极速回旋漂流景区，漂流河段全长6公里，落差209米，河水湍急，惊险刺激，设有回旋漂、森林漂和戏水区，漂流时间大约需要2小时。</span></p><p><img src=\"http://p1.pstatp.com/large/pgc-image/15327668103185e2e9a850f\" img_width=\"730\" img_height=\"355\" alt=\"尧山北麓的自然风景区，汇集100多个景点，鸟语花香却人迹罕至！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun;\">在画眉谷口，依山傍水散落着百余户农家，干净整洁的农家院落，灰墙蓝瓦古香古色。农家宾馆可供食宿，不失为一个休闲娱乐的好去处。</span></p><p><img src=\"http://p3.pstatp.com/large/pgc-image/1532766823743970f8040f2\" img_width=\"1056\" img_height=\"700\" alt=\"尧山北麓的自然风景区，汇集100多个景点，鸟语花香却人迹罕至！\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br/></p><p style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br/></p><p><br/></p>','/uploads/20190705/71dfd5d75b209e0be7711b09e15a4ca4.jpg','/uploads/20190705/dba5c5ed67b9fed05fa76e1c3b6d129f.jpg,/uploads/20190705/ce8972c1d9237929821a5334ff0d0f34.jpg','1562339222',1,0,41,1,'旅途掠影','112.90915','33.73634',50,12,22,39,'<p><span style=\"font-family: 宋体, SimSun;\">自驾（2天）</span></p>','<p><span style=\"color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" background-color:=\"\">平顶山市——鲁山县——尧山镇——四道河村境内</span></p>','<p>一年四季皆可，春夏为佳。</p>',''),(91,6,'郏县','','郏县茨芭镇','<p style=\"padding: 0px; border: 0px currentColor; border-image: none; text-align: left; margin-top: 5px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">在河南郏县西北部，有一座奇峰叠翠、云笼雾罩、绵延起伏的莲花山，莲花山脚下，一片充满生机的土地上，坐落着一方旖旎的家园——茨芭镇。这个豫中偏西的小镇，以其悠久的历史，丰厚的历史积淀和优秀的文化传承，向世人展示着她的博大和宽厚。</span><br/></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-size: 16px; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\"><img title=\"微信图片_201906201736086\" style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; vertical-align: middle; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" alt=\"微信图片_201906201736086\" src=\"/ueditor/php/upload/image/20190707/1562467244818161.jpg\"/></span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">茨芭镇位于郏县西北部，西接汝州市，东北临禹州市，省道洛界公路、前石公路穿境而过，是历史上古都洛阳通往开封、许昌、漯河、南阳等古城的交通要道，地理位置优越，交通便利。全镇地势北高南低，大部分地区属山地丘陵，总面积107平方公里，辖41个行政村，人口6.1万。<br/></span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">茨芭镇有着上千年的文明史，1956年在镇区东部建设清泉水库时，出土了属于仰韶文化类型的石斧、石锛和陶器碎片等。表明了数千年前，就有人类在这片土地上繁衍生息。据历史记载，茨芭镇春秋时属“夹邑”，战国归韩，宋时称钧台乡，明清属塔亭保。在清道光十八年的“公田碑”上，就已经有“茨芭镇”的称谓。1958年成立茨芭公社，1984年更名为茨芭乡，2008年9月，经省政府批准更名为茨芭镇。</span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><img title=\"微信图片_20190620181346\" style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; vertical-align: middle; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" alt=\"微信图片_20190620181346\" src=\"/ueditor/php/upload/image/20190707/1562467244642689.jpg\"/></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">由于历史悠久，且处于古道要冲，茨芭镇留下了丰富的历史文化遗存。<br/></span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">2、名扬中外三苏园：<br/></span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">三苏园位于镇东北的小峨眉山下，背依嵩山余脉莲花山，面对汝水旷川，黄帝钧天台在其前，左右有两条小岭逶迤而下，山明水秀，景色宜人。占地680余亩，是北宋大文学家苏轼、苏辙兄弟遗骨及其父苏洵衣冠埋葬地。主要由三苏祠、广庆寺、东坡湖、东坡碑林、三苏坟、三苏纪念馆、鸿翰苑等景点组成，属全国重点文物保护单位，国家AAAA级风景旅游区。</span><br/></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; text-align: center; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><img title=\"三苏园\" style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; vertical-align: middle; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" alt=\"三苏园\" src=\"/ueditor/php/upload/image/20190707/1562467244192367.jpg\"/><br/></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">园内广庆寺前寺后祠，是全国唯一的寺祠合一建筑；三苏祠内供奉着全国现存的年代最远，保存最好的元代三苏泥彩塑像；在“金蛙迎宾”道石阶下拍手或跺脚，能听到如青蛙鸣唱的回声，是全国四大回音建筑之一；园内古柏郁郁葱葱，遮天蔽日，神奇的柏树一律向西南倾斜，传说是三苏父子思念四川老家，被称为“望乡古柏”，已被列入中国奇树大观的；园内更有难得一遇的奇景，月明风清之时，便会响起潇潇雨声，被称为郏县八景之首“苏坟夜雨”。<br/></span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">三苏父子其丰富的人生经历、深邃的哲学思想、卓越的政治实践、辉煌的文学艺术成就，形成了中国文化史上一个独特的文化体系—三苏文化。三苏园景区则是缅怀三苏、启迪后人的圣地，古往今来，无数莘莘学子、社会名流前来朝贤拜圣，留下许多珍贵佳作，园内现存宋代的墓、明代的坊、清代的碣以及代表当代书法艺术最高水平的300余块碑刻，是学习优秀传统文化、陶冶情操的理想圣地。<br/></span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">3、千年古寺——姑嫂寺<br/></span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">位于镇区西南部的姑嫂寺，占地面积5700平方米，建筑面积1100平方米。该寺分东西两院，西为姑嫂庙，东为姑嫂寺。<br/></span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">据《郏县地名志》载：该庙始建于东汉光武年间。西汉末年，王莽篡汉，天下大乱。光武帝刘秀与其兄刘寅起兵反抗王莽，遭到王莽追杀。其胞妹与妻无法在家乡立足，遂相携出逃在外，辗转流离，隐居乡里，曾藏身于现姑嫂寺村。后来刘秀称帝，姑嫂还朝。为纪念姑嫂二人，当地村民遂于其隐居之处立庙建祠，四时祭拜。佛教传入中原之后，村人又因庙建寺，称姑嫂寺，村名亦称为姑嫂寺村。姑嫂寺庙曾几经焚毁又数次重修，现存建筑主要为明清时期遗物。<br/></span></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; font-family:; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">西院的姑嫂庙现存主殿为清乾隆二十四年(1759年)十月所重建，斗拱飞檐，浮雕彩脊，形制规整，具有明显的明代建筑传承痕迹，保存基本完好。<br/></span></p><p><br/></p>','/uploads/20190707/1f310018ffe6e65da0860d620df6ab82.jpg','/uploads/20190707/70e32ac43bebe87c997a6d15fa6d9160.jpg,/uploads/20190707/3ec97d23dbeb691df31747b0f7d16c4c.jpg','1562467364',1,0,49,1,'象眼旅行—平顶山','113.104886','34.001567',50,12,20,90,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳</span></p>','<p><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun;\">平顶山——郏县——<span style=\"font-family: 宋体, SimSun; background-color: rgb(255, 255, 255);\">茨芭镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">一年四季皆可，春季最佳。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img title=\"1562467360152021.jpg\" alt=\"微信图片_20190707093953.jpg\" src=\"/ueditor/php/upload/image/20190707/1562467360152021.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(92,7,'平顶山市','','油桃，大黄杏熟了，康平生态采摘园约你来采摘。','<p><span style=\"font-family: 宋体, SimSun;\">每到夏天，闲暇的人们都会不约而同的来到新城区位于车管所东南面的康平生态采摘园，尽情享受大自然带来的丰硕成果。</span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562484796292837.jpg\" title=\"1562484796292837.jpg\" alt=\"5.jpg\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562484807652673.jpg\" title=\"1562484807652673.jpg\" alt=\"6.jpg\"/></p><p><span style=\"font-family: 宋体, SimSun;\">当你走进景色宜人的生态园时，首先映入眼帘的就是水泥路两边郁郁葱葱的各种果树，这些树长得枝繁叶茂，就像是一位位守门员在看守着这个生态园。再往前走，就能看见一片果园。在春，夏，秋，冬四个不同的季节中，果园有着不同的变化。其中我最喜欢夏天的果园。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562484871311741.jpg\" title=\"1562484871311741.jpg\" alt=\"1.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562484904458889.jpg\" title=\"1562484904458889.jpg\" alt=\"7.jpg\"/></span></p><p><span style=\"font-family: 宋体, SimSun;\"></span></p><p style=\"white-space: normal;\"><span style=\"font-family: 宋体, SimSun;\">又一次来到采摘园，那一个个大大的，黄黄的酸甜可口的杏；那一个个又大又脆，又香又甜的油桃，真让人们留恋不舍……</span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562484921292756.jpg\" title=\"1562484921292756.jpg\" alt=\"4.jpg\"/></span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562484939785024.jpg\" title=\"1562484939785024.jpg\" alt=\"6.jpg\"/></span></p><p style=\"white-space: normal;\"><span style=\"font-family: 宋体, SimSun;\">一到夏天，果园便呈现出一片丰收的景象。沉甸甸的果子把树枝压弯了腰，一个个成熟的果实挂在树上，笑得乐开了怀，好像在告诉人们丰收的喜讯。</span><br/></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562484961250581.jpg\" title=\"1562484961250581.jpg\" alt=\"8.jpg\"/></span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562485139894128.jpg\" title=\"1562485139894128.jpg\" alt=\"16_看图王.jpg\"/></span></p><p style=\"white-space: normal;\"><span style=\"font-family: 宋体, SimSun;\"></span></p><p><span style=\"font-family: 宋体, SimSun;\">康平农业生态园，</span><span style=\"font-family: 宋体, SimSun;\">国家绿色食品认证。</span><span style=\"font-family: 宋体, SimSun;\">集休闲，娱乐，采摘、休闲观光为一体自然观光园。水果新鲜、有机、无污染。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562485309115971.jpg\" title=\"1562485309115971.jpg\" alt=\"12.jpg\"/></span></p><p><br/></p>','/uploads/20190707/328f097a2ee74dfcbfd3c0a1f3419c58.jpg',',','1562473073',1,0,68,1,'象眼旅行—平顶山','113.25686','33.74641',50,12,125,0,'<p style=\"text-align: left;\">自驾游（1天）为佳</p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——新城区</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春季最佳。</span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562485326691221.jpg\" title=\"1562485326691221.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">长按识别关注 让旅行变得更简单</span></p>',''),(93,6,'舞钢','','舞钢市尹集镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">尹集镇坚持“生态建镇、产业立镇、旅游名镇”的发展战略，制定了《尹集镇全域旅游工作实施方案》、《尹集镇打造特色乡镇未来三年行动计划》，目前已获得国家级卫生镇、国家级生态镇、全国重点镇、全国文明镇、国家级特色景观旅游名镇等荣誉称号。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; text-align: center;\" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562486710240683.jpg\" alt=\"九头崖15.4\" title=\"九头崖15.4\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562486711618632.jpg\" alt=\"九头崖溪流\" title=\"九头崖溪流\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><span style=\"font-family: 宋体, SimSun;\">尹集镇</span>初步形成了以虎头山新四军纪念园为代表的红色游;以4A级景区二郎山为代表的山水休闲游;以张庄社区为代表的乡村游;以九头崖、凤凰谷为代表的生态游;以誉森农业观光、百合种植园、金花葵种植园、草莓种植园等现代农业观光游。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190707/1562486711413318.jpg\" alt=\"16--二郎山鸟瞰风光\" title=\"16--二郎山鸟瞰风光\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun;\">目前发展农家乐170余家，其中三星级旅游饭店3家，旅游开发企业6家，开发有老粗布、山野菜、薄皮核桃、柴鸡蛋、变蛋、金花葵茶、艾枕、菊花枕等中草药用品开发支持力度。另外包装开发小磨油、石磨面、野蜂蜜、中华响石等深受游客欢迎的具有尹集地方特色旅游产品。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190707/1562486712214491.jpg\" alt=\"环湖马拉松赛1\" title=\"环湖马拉松赛1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/><br/></p><p><span style=\"font-family: 宋体, SimSun;\">下一步，<span style=\"font-family: 宋体, SimSun;\">尹集镇</span>将以全域旅游优秀乡镇创建工作为抓手，统筹全镇各项事业的发展，培育壮大尹集旅游业，实现尹集经济的腾飞发展。</span></p>','/uploads/20190707/36ea0c0f5ea544b6e9989c727456cef4.jpg',',','1562486976',1,0,15,0,'象眼旅行—平顶山','113.52784','33.27942',50,12,26,101,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（2天）为佳</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——舞钢市——<span style=\"background-color: rgb(255, 255, 255);\">尹集镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562487426696945.jpg\" title=\"1562487426696945.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p><span style=\"font-family: 宋体, SimSun;\"></span></p><p style=\"white-space: normal; text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><span style=\"font-family: 宋体, SimSun;\"><br/></span></p><p><span style=\"font-family: 宋体, SimSun;\"><br/></span><br/></p>','平顶山市文化广电和旅游局');
INSERT INTO `ddsc_rural` VALUES (94,6,'舞钢市','','舞钢市杨庄乡','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">杨庄乡位于舞钢市中南部，素有“山水画廊 秀美杨庄”的美誉，全乡辖21个行政村，总人口2.7万，总面积134平方公里，区位优势明显，焦桐高速，周南高速穿境而过，四通八达，交通条件便利，旅游发展基础良好。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562487916328524.jpg\" alt=\"天池秋色\" title=\"天池秋色\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">境内自然风景和历史人文景观相得益彰，不仅有国家4A级景区灯台架、国家3A级景区祥龙谷、千年佛光禅寺、历史遗迹母龙寨民俗文化苑、休闲观光花卉博览园、草莓种植园、金凤凰养殖园、灯台架生态养殖园、生态文化旅游示范园、楚长城遗址等文化旅游资源丰富，还有流传千年的非物质文化遗产-----古轧琴，至今仍能演奏出美妙的旋律，以及臧坪、岗李、水田村旅游民宿开发区等。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190707/1562487916886060.jpg\" alt=\"灯台架星空\" title=\"灯台架星空\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">现有四星级乡村旅游经营单位一家、三星级乡村旅游经营单位两家；旅游商品开发企业5家，开发的有老粗布、山野菜、薄皮核桃、柴鸡蛋、变蛋、艾枕等中草药用品。岗李村2016年列为河南省五个移民乡村旅游试点村之一。吴庄村、水田村被确定为全国乡村旅游扶贫重点村。2018年被评为省级生态旅游优秀乡镇。</span></p><p><br/></p>','/uploads/20190707/7f9f17d12fd69e9208c7eb2608782157.jpg','/uploads/20190707/cb04edb754e319d1b693a768ff2999e2.jpg,/uploads/20190707/0ebaf445fed401bd53c2547be5339c68.jpg','1562488325',1,0,8,0,'象眼旅行—平顶山','113.53696','33.33359',50,12,26,103,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（3天）为佳</span></p>','<p><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">平顶山市——舞钢市——<span style=\"font-family: 宋体, SimSun; background-color: rgb(255, 255, 255);\">杨庄乡</span></span></p>','<p>春夏秋冬。</p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562488268244683.jpg\" title=\"1562488268244683.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(95,6,'舞钢市','','舞钢市武功乡','<p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">武功乡位于舞钢市东部，紧邻市区，地理位置优越，交通四通八达。全乡总面积51.1平方公里，辖22个行政村，64个自然村，总人口2.7万余人。武功乡历史悠久，旧时称“窑沟”，是原舞阳县四大名镇，清康熙58年间更名为“武功”，境内现有蔡公祠、法藏寺、显庆寺和沟头赵冶铁遗址等；辖区内矿产自然资源丰富，主要有铁矿、石英岩矿、花岗岩矿等；滚河自西南向东北蜿蜒流经全境，由曹庄村寺山流入西平县境内；田岗水库犹如一颗明珠璀璨靓丽，浇灌着5000亩良田；规上企业 12家，年产工业总值10.05亿元。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190707/1562488506304474.jpg\" alt=\"凤鸣湖4\" title=\"凤鸣湖4\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun; font-size: 16px;\">二、武功乡全域旅游发展思路</span><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun; font-size: 16px;\">武功乡按照“党委统领、政府主导、企业主体、市场运作、社会参与”的方式，把创建全域旅游优秀乡镇工作目标定位在充分发挥凤鸣湖、罗大山、冶铁文化遗址、将军故里、舞阳区委旧址、苗木花卉等资源特色，全力打造“滨湖休闲游、城市公园游、冶铁历史游、红色文化游、将军故里游、农家休闲游”六大旅游品牌，树立全域化、全要素、全产业的发展理念，以“田园城乡、人文武功”的发展定位，打造生态、休闲、文化、体育、农业、红色旅游目的地。</span></p><p style=\"white-space: normal; text-align: center;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190707/1562488507915518.jpg\" alt=\"凤鸣湖\" title=\"凤鸣湖\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">三、全域旅游发展五大优势<br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">一是区位优势。紧邻市区，距离不到十分钟车程。二是交通优势。乡域国道345穿境而过、省道七蚁线、高兰线一纵一横，县乡村道路条条通畅，交通条件十分便利。三是山水优势，凤鸣湖地势平坦、视野开阔，罗大山接市连乡、环境秀美。四是文化优势。境内有曹刚川将军故居、沟头赵冶铁文化遗址、原舞阳区委旧址等悠久历史沉淀和知名文化资源。五是生态优势。有全国最大的肉鸽养殖基地、晚秋黄梨基地、苗木花卉基地、林果种植基地等乡村休闲观光体验资源。实施“旅游+”行动，推动旅游业与一、二、三产业相融合，与美丽乡村建设和新型城镇化相融合，全力打造多旅游功能的休闲憩息地。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190707/1562488507290504.jpg\" alt=\"全国旅游标志\" title=\"全国旅游标志\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">四、全域旅游六大主题<br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">（一）打造滨湖休闲观光带。以凤鸣湖西岸湿地公园为依托，以“滨湖游”为主题，尽量减少房屋建筑，以风景林、花海（根据花期栽植不同品种，形成季季有花、四季常绿）、休憩区、微地形、亲水栈道、雕塑小品等为主，最大程度保持滨湖区域的自然状态，形成“山水相映、绿树含烟、远山叠嶂、碧水微澜”的怡情逸致风格，从而实现与龙泉湖观光栈道互动对接，与龙泉湖“半湖半城”景色相辅相成，更加显现和发挥出舞钢山水林城独特的景观景致风貌。同时，与已经确定的“体育中心”项目相互毗邻，完全融合体育、人文、康健、生态等资源优势。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun; font-size: 16px;\">（二）打造商务休闲观光区。以柏都大道西侧的罗湾村紧邻凤鸣湖的优势，以棚户区改造项目为支撑，围绕建设“江南小镇”特色开篇布局，以铺开“檐廊、粉墙、黛瓦、马头墙、吊脚楼”等元素，达到典型的江南“小桥、流水、人家”景色风格，使得观光区呈现一派古朴、明洁的宁静致远意境，让人在北国也能欣赏和享受“江南”风景。项目设计上重点构建全国特色饮食一条街、酒吧茶吧商务洽谈、养生休闲度假、婚纱摄影风景地。建设层次上做到楼层错落有致、层层递增，前不遮后、低称高显，让人耳目一新、流连忘返。最后，项目的实施，所需要的服务、保安、物业、保洁等岗位还可安排罗湾村群众就业，达成多方面共赢的局面。</span><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun; font-size: 16px;\">（三）打造罗大山城市森林公园。利用丰富森林资源的自然风光和白云寺的人文文化，与刘山公园遥相呼应、融合贯通，增加舞钢城区宜居宜游的风景如画空间。一是突出城市森林公园建设。以中心路贯通天桥为连接，打破城区与罗湾村的道路瓶颈制约，着力构建以植物造景为主，以园路、小品为烘托的自然空间，辅助建设城市发展、儿童游乐、健身康乐等主题区域，使之成为市民、游客观光、游乐的绝好去处。二是突出白云寺提升。提档升级白云寺建设，建设精品城市禅院，以修禅、修身、修心为三大主题，适当开设禅修课堂、国学课堂等文化项目，满足游客在旅游当中提升思想、升华境界的精神需要。三是突出尾矿库利用。充分依托尾矿库独特的“稀有认识”吸引游客，因地制宜开展种植芦苇、中华结缕草等耐碱草种，栽种黑松、雪松等抗风抗碱树种，建设垂钓、竹筏、水上蹬车等小型水上娱乐项目。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190707/1562488507690080.jpg\" alt=\"鱼灯舞\" title=\"鱼灯舞\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">（四）打造红色和农业观光旅游带。一是以曹集村曹刚川将军故里、将军广场和希望小学为核心景观，依托故居展示将军家庭生活、人生经历、事业成就、家风家规等图片、文字资料，建设兵器展览馆，按照时间顺序从冷兵器时代至现代武器进行重点展示，激发游客的兵器求知欲，同时可开发兵器小模型旅游产品，从而发展爱国主义旅游；二是以刁沟村原舞阳区委旧址为依托，恢复重建原貌，以蜡像方式重现当年革命先烈奋斗事迹，注重搜集老物件、老文件、老故事，以实物陈列、战斗还原、成就意义丰富旅游内容，从而发展红色旅游；三是以两村之间的后营村、岗王村等苗木林果种植等自然资源为纽带，实现红色旅游、爱国主义教育和农家休闲观光体验互动的完美结合。同时，依托天成鸽业项目，开发精品“笼养对鸽”的“养鸽放飞梦想”产品以及“真空包装卤鸽”产品，增加旅游产品吸引力。再者，即将启动的国道345项目正好作为连接将军故里和区委原址两个项目的交通纽带，也给游客到达旅游目的地提供了便利。</span><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190707/1562488508730196.jpg\" alt=\"田岗水库\" title=\"田岗水库\" style=\"font-size: 16px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">（五）打造冶铁文化展示区。以田岗村沟头赵冶铁文化遗址为依托，借助遗址紧邻凤鸣湖东北岸的优势，发展冶铁文化展示游，一方面借助现有村室开设冶铁文化博物馆，陈列古物、古迹，村室再择址重建，开发冶铁小铸件旅游商品，丰富我市旅游产品种类。同时进一步挖掘柳州城历史资源，追溯历史渊源，还原历史故事，深厚舞钢历史文化，同时结合明年的美丽乡村特色村项目，提升田岗村形象。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"http://xiangyan.dd371.com/ueditor/php/upload/image/20190707/1562488508324436.jpg\" alt=\"后营苗木基地 (1)\" title=\"后营苗木基地 (1)\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">（六）打造武功村街区文化主题。全面整治街区环境，依托美丽乡村项目，以政府周边“四纵四横”八条街区为重点全力打造八大街区主题宣传长廊，分别是迎宾大道—中国梦，建设路—社会主义核心价值观，府前街—基层党建，朝阳路—党风廉洁建设，中心街—孝德文化，市场街—南段以诚信为主题、北段以文明风尚和乡规民约为主题，武功大道—法制建设，凤城路—模范人物等内容，广场周围设置社会主义核心价值观、“传统文化”、“廉洁文化”等大型标示牌，同时用发光柱、发光树、草地灯进行立体亮化，雕塑“马踏飞燕”下面安装大功率泛光灯，底座用灯带亮化，显示层次感，以街区文化主题提升核心区域形象。</span></p>','/uploads/20190707/77134d41269635575278c81f2069669c.jpg','/uploads/20190707/715d3666b2fd933c65e7a9c3602d5b97.jpg,/uploads/20190707/9aabd59b822396050b3e7743dfe815a3.jpg','1562489283',1,0,3,0,'象眼旅行—平顶山','113.53696','33.33359',50,12,26,105,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（2天）为佳</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——舞钢市——<span style=\"background-color: rgb(255, 255, 255);\">武功乡</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">一年四季皆可，夏季最佳。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562489264963632.jpg\" title=\"1562489264963632.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><span style=\"font-family: 宋体, SimSun;\"><br/></span></p>','平顶山市文化广电和旅游局'),(96,6,'舞钢市','','舞钢市庙街乡','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">庙街乡位于舞钢市西北部，属半山区乡镇、东承舞钢市产业集聚区，西接焦桐高速2公里，区位优越，交通便利。</span><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">&nbsp;</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562489752817668.jpg\" alt=\"九龙山4 (2)\" title=\"九龙山4 (2)\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">风光独特的九龙山自然风景区位于庙街乡西南部，位居焦铜高速出口，交通区位优势优越。山上有飞来石、刀劈石、情人石、刘秀洞、群仙会石群等，山脚下有两条水库，山水相连，森林茂密，浓荫匝地，山顶怪石遍地，像人似物，美丽的风景伴有许多动人的传说，享有“中原天然石雕园”之美誉，奇特异常，是大自然鬼斧神工之作，吸引游客流连忘返，成为舞钢市发展特色旅游业重点景观。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562489752806016.jpg\" alt=\"九龙山三仙论道\" title=\"九龙山三仙论道\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">境内座落五座水库，群山连绵，山水相间，风景秀丽、宜居宜业。人头山村“水美乡村”已创建成功；九龙山北侧刘沟村“美丽乡村”创建成功准备实施。2017年以来被评为国家卫生乡镇、省级生态乡、园林乡、省级文明村镇等荣誉称号。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">依托风景区区位优势，规划万亩苗木花卉基地，招商引资引进有实力的公司发展旅游业。目前发展的有油菜、鲁冰花、格桑花、月季等。同时，利用生态廊道，沿乡村主干道发展林下中草药种植，路边合理规划，种植白芍、药牡丹、紫丹参等，每到春季，成为一道花卉旅游观光带。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562489753777091.jpg\" alt=\"党庄石文化\" title=\"党庄石文化\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562489753431126.jpg\" alt=\"微信图片_20190529114226\" title=\"微信图片_20190529114226\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">大力发展茶宿旅游项目，利用荒山在人头山村玲珑山投资50万元的千宝茶场已初具规模，2019年争取上级资金扶持发展茶宿旅游项目，为舞钢市填补了茶文化空白。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562489753882744.jpg\" alt=\"千宝茶场2\" title=\"千宝茶场2\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">通过全方位的努力，庙街乡全域旅游工作在上级部门及乡政府的共同努力下，乡村面貌焕然一新，下一步，借助山水自然资源优势，计划发展民宿打造吃喝玩乐为一体的的乡村旅游景区。</span><span style=\"font-family: 宋体, SimSun; font-size: 16px;\"><br/></span></p><p><br/></p>','/uploads/20190707/4fe7f48f26b3dbc851c3eab17609c910.jpg','/uploads/20190707/bea57fd05dcfea9f91c9120bf0572362.jpg,/uploads/20190707/6c9db13103243e3596c77e9a9f873e56.jpg','1562490016',1,0,4,0,'象眼旅行—平顶山','113.525332','33.295374',50,12,26,109,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳</span></p>','<p><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">平顶山市——舞钢市——<span style=\"font-family: 宋体, SimSun; background-color: rgb(255, 255, 255);\">庙街乡</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562489991175481.jpg\" title=\"1562489991175481.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(97,6,'宝丰','','宝丰县观音堂乡','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">观音堂乡位于豫西山区，距宝丰县城70华里，总面积72平方公里，总人口2万余人。地势西高东低，南北石河将该区域分割成“三山+两川”状。境内文化底蕴深厚，现有省级传统村落三处，县级重点保护不可移动文物七处；既有许多民间传说（如九龟朝赧王），又有近代历史名人（如李禄、段花），源远流长的历史，与现代建设的结合，构成了一副壮丽的画卷，加之景色宜人的自然景观，成为发展山区文化资源业的良好前提。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562490404811217.png\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun; font-size: 16px;\">拥有县级不可移动文物近二百余处，其中有七处属重点保护文物，这七处县级保护不可移动文物，既有自然景观的可视性，同时又赋予一些优美的传说。具有可视、可听的旅游价值。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun; font-size: 16px;\">1、金井洼古井</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun; font-size: 16px;\">该井建于明代，是在岩石上凿制而成形，上端大，越往下越小，成锥形。井壁东侧有被绳子磨出的槽痕。井壁南侧上部用红砖维修镌砌，井口上部用青石围砌，井台用水泥抹面。井口东西长0.55米，南北宽1.6米，井深约27米。南侧现存一块红石井架，井架高0.95米，下宽0.8米，上宽0.35米，上方有“U”形榫。井房墙体用大小不一的青石板叠压垒砌，灰瓦顶。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562490405617034.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">2、香炉垛祖师庙<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">该祖师庙始建于隋唐，鼎盛于明清，后文革时期被毁。四面绿水环绕，八方群山朝拜，现存碑刻3通，祖师殿东边一通为“赦建中岳庙记”碑，碑正面正中部有一圆形和竖长方形凹槽，背面为捐款人姓名。祖师殿前东侧一通重修霛宫殿记碑，宽0.5米，高1米，厚0.1米，上部残，“雍正九年岁次辛亥仲秋立”。一通佚名碑高1米，宽0.7米，厚0.4米。祖师殿东侧有一奇石，石上有一形似龙翔凸起图案，非常生动。祖师殿东侧一个石龟，宽2米，长3.1米，高1.7米，造型逼真，栩栩如生。祖师殿前有一石香炉，元宝型高0.25米，宽0.5米，厚0.2米，一面刻有人名，为捐款者姓名，似用石碑刻凿而成。祖师殿前有一残柱础，上刻莲瓣纹饰。现一直上新建祖师殿三间，关爷殿一间，药王殿一间。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562490405854024.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">3、韩家窑址<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">建于宋元年间，位于崔家村韩家组自然村东部，该窑址南北长110米，东西长70米，占地面积7700平方米，据从宝址拣到的窑具（匣钵）、瓷长确定为宋、元瓷窑址。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562490406425433.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">4、三间房村大渡槽<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">位于三间房自然村东北部，该渡槽建于1961年，东西走向，因红石砌筑、石拱结构，共有大小拱10孔跨度50米，高20米，北面刻有“愚公移山、改造中国垛上大队返修大渡槽”，南面上刻“水利是革命命脉，垛上大队返修大渡槽”。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">5、庄科洞穴遗址<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">位于庄科村南，洞穴为一深洞，深7米，高1.7米，面积50余平方米，1977年发现。洞内有大量动物化石，种类有鹿、牛等，并有火烧痕迹，部分骨片似曾初步加工。属旧石器时代遗址。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">6、罗顶寨遗址<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">该遗址地势险要，呈不规则三角形，位于山顶部。为清代防匪患而建，约毁于建国初。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">7、没梁庙<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">始建于隋唐年间，因建筑结构全部用石灰岩凿砌，无一根木料，故此而得名“没梁庙”。</span><br/></p><p><br/></p>','/uploads/20190707/4aecf3c298adeccc88ede20db6227f3b.jpg',',','1562490282',1,0,6,0,'象眼旅行—平顶山','114.25965','30.58015',50,12,25,126,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游为佳</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——宝丰县——观音堂乡</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562490631972317.jpg\" title=\"1562490631972317.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(98,6,'鲁山县','','鲁山县尧山镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">尧山镇位于河南省平顶山市鲁山县西部，地处平顶山、洛阳、南阳三市结合部。北临汝阳县王坪乡、西接嵩县车村镇，南靠南召县马市坪乡、崔庄乡，东临鲁山县赵村乡、四棵树乡。全镇总面积305平方公里，辖22个行政村，146个村民组，489个自然村，总人口约3万人，郑尧高速、311国道纵贯全境。</span><img src=\"/ueditor/php/upload/image/20190707/1562490765955187.jpg\" alt=\"尧山镇3\" title=\"尧山镇3\" style=\"font-size: 16px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">尧山镇最高点尧山玉皇顶位于伏牛山主脊地带，海拔2153.1米，最低点位于尧山村北边靠东沙河河床中，海拔347米;雨水充沛，植被丰富，旅游资源丰富，集纳了鲁山县境内90%以上的旅游单体资源，其中国家5A级风景名胜区--尧山风景区享誉省内外。是我国战国时期著名思想家墨子的故里，河南省首批特色景观旅游名镇、中州名镇，2009年被平顶山市确定为市级中心镇。2012年6月尧山镇党委被中组部授予&quot;全国创先争优先进基层党组织&quot;。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562490765871973.jpg\" alt=\"尧山镇1\" title=\"尧山镇1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br/></span></p><p><br/></p>','/uploads/20190707/a02660dc4613a7a8088f2a7e61c07a4d.jpg','/uploads/20190707/cfb913a6161d689e0f4cbb21238faa7e.jpg,/uploads/20190707/2b0bef7628dd3fcfed0d5765e6e7325c.jpg','1562490943',1,0,28,1,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,39,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（2天）为佳</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">尧山镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">一年四季皆可，夏、季最佳。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562490928767381.jpg\" title=\"1562490928767381.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(99,6,'郏县','','郏县堂街镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">堂街镇具有优越的自然人文资源，是一个有山有水有故事、有寺有庙有文化、有镇有寨有传承的中州名镇。东依紫云山，北环汝河水，山水相连、风光秀丽，物华天宝，人杰地灵。镇域面积68平方公里，辖36个行政村，108个自然村，52000余口人，耕地面积60090亩。镇域地势东高西低，包括平、沙、山、岗、洼五种地形。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562491052541335.jpg\" alt=\"4、专业合作社\" title=\"4、专业合作社\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; background-color: rgb(255, 255, 255);\">堂街镇具有优越的地理区位，道路四通八达。镇区南距平顶山市区20公里，东距平煤集团十三矿4公里，向北经柏堂路5公里与S238省道相连，向南5公里与S329省道南石线贯通，平郏公路穿境而过。柏堂路汝河大桥的建成，实现了堂街镇与郑南西线与洛界路的直接贯通。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562491053604841.jpg\" alt=\"2 、存栏红牛\" title=\"2 、存栏红牛\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">堂街镇具有厚重的历史文化底蕴，境内原真性的文化遗存和历史遗迹众多，而且产业优势和资源优势明显。郏县八大景之“紫云晴雪”和“汝水秋声”都在堂街境内；千年古刹紫云寺、宝象寺与观音祖庭香山寺构成汉传佛教区著名的大香山宗教文化区；再现历史儒、释、道三教并存并兴的“三教堂”遗址成为堂街的又一张名片；有三国时期曹操在紫云山北麓摩陂下建立的 “行宫”，以及曹操出“行宫”七里处劳徐晃军的徐晃回军庙；国家重点文物保护单位、国家历史文化名村、中原第一红石古寨——临沣寨的明清古建筑群错落有致、保存完好；有“三线”建设时期的国防工程——炮兵洞；有国家“七五”期间重点科研攻关项目、科研成果被列入我国大专院校教材的全国第一处恒压喷灌试验工程；被誉为“中国第二圣人“的中国人民解放军特等功臣祁建华于1921年7月1日出生在此；驰名中外的“山儿西烟”作为高端香烟的主配料，发展势头强劲。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190707/1562491053998047.jpg\" alt=\"1、烟叶丰收\" title=\"1、烟叶丰收\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190707/1562491054436469.jpg\" alt=\"107  临沣寨砖雕\" title=\"107  临沣寨砖雕\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p><br/></p>','/uploads/20190707/99ed1f75fb88a25aaab90bd2ad2ce0cd.jpg','/uploads/20190707/100de36bf18e4b837db0a4703e8095a2.jpg,/uploads/20190707/c74b3b7d96eb1dff3f429ecfbf61f740.jpg','1562491327',1,0,11,0,'象眼旅行—平顶山','113.210554','33.972885',50,12,20,88,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游为佳</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——郏县——<span style=\"background-color: rgb(255, 255, 255);\">堂街镇<span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun;\"></span></span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">一年四季皆可。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562491308870649.jpg\" title=\"1562491308870649.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><span style=\"font-family: 宋体, SimSun;\"><br/></span></p><p><br/></p>','平顶山市文化广电和旅游局'),(100,6,'舞钢市','','舞钢市八台镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">八台镇位于舞钢市西北部，辖20个行政村，总面积56平方公里，总人口3.05万人，是个典型的平原镇。八台镇交通便利，位置优越，资源丰富，是舞钢市城乡一体化试点乡镇。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562491529699656.jpg\" alt=\"河道治理\" title=\"河道治理\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">近年来，八台镇围绕淃河、泥河、马河三条水系，以深挖宋代历史文化渊源，打造文化特色村庄，大力发展全域旅游。投资100多万元建设农耕文化广场1000多平方和农耕文化展馆150多平方，在展馆收集保存农耕生产生活用具1000余件，绘制农耕文化墙体画一条街，再现农耕文化场景，打造泥沟陈村农耕文化特色。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562491530577349.jpg\" alt=\"孝道文化墙1\" title=\"孝道文化墙1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">石桥杨村结合本村实际，打造本村特色，深挖孝道文化，通过搜集身边典型家庭事迹，挖掘优秀的家风、家训，整理并制作优秀家风、家训牌49面；彦张村按照“个性化、景观化、差异化、特色化”的思路，立足自身气候、土壤实际，突出抓好月季花种植工作，2019年，彦张村开始打造月季花繁殖基地，着力打造彰显地方特色的“月季花村”生态风景线。泥沟陈村大力发展特色农副产品，以“助农消费、帮农增收、振兴乡村”为宗旨，大力发展“土味儿”、农特产，该村的石磨面、农家自酿酒、枣花馍、乔氏胡辣汤、三不沾猪蹄、农家自酿醋、纯红薯粉条等具有农家特色的绿色、生态、地道的农副产品，从而丰富城乡文化生活，助力乡村振兴。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562491530549638.jpg\" alt=\"自酿酒\" title=\"自酿酒\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p><br/></p>','/uploads/20190707/873aa04c98534f825e714beb671c9e6c.jpg','/uploads/20190707/9ff89d06dc9648b972b38a6e95f93757.jpg,/uploads/20190707/6d88c325af117d3f23ab3f36e350352c.jpg','1562491681',1,0,13,1,'象眼旅行—平顶山','113.53696','33.33359',50,12,26,102,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——舞钢市——<span style=\"background-color: rgb(255, 255, 255);\">八台镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562491666677147.jpg\" title=\"1562491666677147.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(101,6,'宝丰','','宝丰县杨庄镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">杨庄镇位于宝丰县城南，距县城2公里。全镇辖19个行政村，7个社区居委会，1个市场管委会，57个自然村，158个村民小组，总人口约5.26万人，总面积62平方公里。郑尧高速、豫02线、长安大道穿境而过，焦枝铁路、平韩铁路、漯宝铁路在此交汇，平顶山西站、高铁商务区、高铁站、宝丰长途汽车站座落镇区境内。发展中的产业聚集区、遥望中的宝丰新城、规划中的平顶山新区交融于此。按照全镇总体规划，突出空间布局，着力打造农业景观带，完善旅游服务配套功能，致力于五大旅游节点的全面开发。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562491783112972.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">马街村享有“中国曲艺之乡”荣誉称号，响誉全国的马街书会是杨庄镇文化建设的重要特色，也是杨庄镇对外形象的骄傲。马街书会距今已有700多年沧桑历程，以其历史悠远、规模宏大、曲种古老、原汁原味、自发汇聚、民风淳朴而名扬千里。马街书会民俗园景区是国家AAA级旅游景区，主要有火神庙、主题雕塑广场、名人苑、状元苑、中华曲艺展览馆、刘兰芳艺术馆、中国曲艺交易中心等景点。在这里，“一日能看千台戏，三天可读万卷书”，可以感受幕天席地和人山艺海的奇观，品味曲艺艺术的原始风貌。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562491784497669.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">仝岭湛河源依据人水和谐的环境水利理念，坚持工程改造与生物技术相结合，治污与造景、绿化相结合的原则，重点实施了河面保洁、沿岸截污、源头造景引水、两岸绿化等，着力把湛河源头打造成“生态、优美、安全、舒适”，并与周围自然环境相协调的绿色河、人文河、景观河、效益河。湛河源头莲花湿地景区是国家AAA级旅游景区，园区规划以“圣水湛河源·赏心莲花镇”为主题，打造“一带四区”的功能分区，以高效种植莲藕为主；在农业生产的同时，打造“接天莲叶无穷碧，映日荷花别样红”的景观。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562491784178605.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">按照全域旅游发展思路，根据全区的旅游发展总体布局，把握“十三五”旅游业“爆发式”增长的机遇，依托马街书会民俗园、湛河源莲花湿地景区、石磨房农家院、高铁商务交通资源，进一步巩固旅游龙头地位，对接旅游“高铁时代”“互联网时代”和“个性时代”，大力发展乡村旅游，强力推动旅游项目发展，着力构建以生态休闲、度假养生、文化旅游、观光体验、户外运动、民俗民宿为主体的旅游产品体系，大力推进旅游业与农业、商贸等产业的深度融合，实现由景点旅游向全域旅游转变，形成以旅游业态融合为拓展的“大旅游”发展之路，谱写杨庄发展魅力新篇。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562491785618968.jpg\" alt=\"DJI_0232\" title=\"DJI_0232\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p><br/></p>','/uploads/20190707/b5d33f0197e103d0b765f0fd5f587051.jpg','/uploads/20190707/44f4a099e48d4dbe481384c05c7947fb.jpg,/uploads/20190707/c8ef6e67d896e166520e3fb33843334c.jpg','1562492035',1,0,16,1,'象眼旅行—平顶山','114.25965','30.58015',50,12,25,75,'<p>自驾游为佳。</p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——宝丰县——<span style=\"background-color: rgb(255, 255, 255);\">杨庄镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">一年四季皆可，春季最佳。</span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562492011606918.jpg\" title=\"1562492011606918.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(102,6,'鲁山县','','鲁山县下汤镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">下汤镇地处伏牛山脉腹地、淮河源头，东靠库区乡，南接团城、四棵树乡,西接赵村镇，北与观音寺、土门、瓦屋镇交界。全镇地域轮廓呈“扇贝”状，地势西北高东南低，平均海拔176米，海拔800米左右的山峰有祖师顶、朝阳观、鹰背垛、孟良寨等；横穿全镇的沙河与团城河、清水河在此交汇；上靠尧山—大佛5A级风景区，下临昭平湖4A级景区及世界“中华刘姓始祖苑”景区。交通十分发达，二广高速与郑尧高速在此交汇，均设有下汤出口。311国道、207国道贯穿全境，距鲁山民用机场、郑万高铁站仅20分钟车程，出行十分便利。全镇总面积121.86平方公里，辖20个行政村，2个社区居委会。总人口3.79万人，其中镇区人口14478人。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562492402889033.jpg\" alt=\"1下汤地标\" title=\"1下汤地标\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">下汤镇温泉资源得天独厚，常年沸汤涌流，温高量大，祛寒祛疾，西周以来，多被记载于史典文集，素以温度高、流量大、含有矿物质种类多、医疗价值高闻名遐尔，享誉华夏。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562492403800188.jpg\" alt=\"下汤镇2\" title=\"下汤镇2\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">这里森林覆盖率达85%以上，每年蓝天率在85%以上，有雾无霾，“小气候”滋润万物。年降水量丰富，气候清新怡人，被赞为中原“小江南”。同时下汤镇22个村中，每个村都有自己的独特旅游景观。比如竹园沟的历史文化“花门楼”，林楼村、社楼村的万亩桃园种植基地，王画庄村千亩花椒基地，以及杨家庄村、王画庄村的祖师顶、孟良寨、牛劈峡等景点，近年来更是吸引了很多游人前来游玩，民风淳厚。每年3月底的桃花节，每天的人流量都能达到5万人，既带来了大量的优质口碑，也给当地带来了一定的经济收入。自然风景和历史文化的交融，更是让下汤镇成为鲁山一颗耀眼的明珠，有“中州双十佳、华夏第一汤”的美誉。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190707/1562492403682967.jpg\" alt=\"下汤镇3\" title=\"下汤镇3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">近年来，在各级党委、政府的正确领导下，下汤镇认真贯彻落实党的十九大精神，紧扣“保发展、保稳定、保民生”主题，大力发展温泉旅游与民族特色餐饮，推动了全镇经济又好又快发展。2013年、2014年先后被省委、省政府授于河南省“旅游名镇”、“双十佳名镇”、“河南省卫生城镇”等荣誉称号。2015年获得“全国重点镇”国字号荣誉，2016年成功争创“省级生态示范镇”，2017年“国家级卫生镇”（2017至2019年）通过复审。</span><br/></p><p><br/></p>','/uploads/20190707/b1147320d0796c5238fbd900d4bd3666.jpg','/uploads/20190707/895e1aaff8e6b6396eed1c407ffd70bb.jpg,/uploads/20190707/2a7e82e07b3c53aa29db0881c76cc64e.jpg','1562492605',1,0,8,0,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,35,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（2天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">下汤镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span><br/></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190707/1562492587891563.jpg\" title=\"1562492587891563.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p><p><br/></p>','平顶山市文化广电和旅游局'),(103,6,'郏县','','郏县李口镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">李口镇位于郏县东南部，背靠铧角山与马鞍山，与平顶山相望，镇域面积50平方公里，3万口人，23个行政村，54个自然村，境内有芝河、泥河、程寨沟河三条主要河流，省道南石线（S329)与县道平郏路在境内纵横贯穿通过。李口镇自古就有人类活动，宛洛故道就通过李口，历史上名人辈出，人杰地灵，历史文化厚重，代表人物有汉初三杰之一的谋圣张良、明隆庆年间的户部天官李一本、明布政使王尚絅等。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562587890150040.jpg\" alt=\"微信图片_201906271628574\" title=\"微信图片_201906271628574\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">烟草科技园<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">烟草科技园位于李口镇白龙庙村，科技园内建有平顶山烟草博物馆，馆内主要展示现代烟草农业、现代烟草工业、现代烟草营销、现代烟草物流配送及现代烟草专卖管理五项内容，是国内首个完整诠释现代烟草全产业链条的专门类别博物馆。现在已建成全省首家现代化电子烘烤房2座，小型电子炕48座，传统炕房7座，建设现代化烟叶育苗大棚12座，可为8000亩烟田提供烟苗，建有省内唯一一家烟草研究博士后工作站等建筑目前已投资逾1亿元。李口镇围绕烟草博物馆，先后规划建设了千亩“山儿西”烟烟叶示范基地、白龙庙月季园、烟草文化园、“山儿西”烟宾馆，烟王亭、“山儿西”烟亭。李口镇2017年、2018年烟叶税收突破800万元，实现了烟农得实惠、政府得税收、企业得发展的多好局面，2019年烟叶面积扩大至6000亩，2020年拟规划烟叶面积8000亩，李口以烟叶发展为依托全面规划建设烟草产业园，全面规划建设田园综合体、全面打造平顶山烟叶小镇。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562587890975935.jpg\" alt=\"微信图片_201906271628573\" title=\"微信图片_201906271628573\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">张良故里<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">高度重视张良故里的保护开发工作，通过争取项目、镇政府投资，不断完善张良故里基础设施。目前，张良故里已投入资金1080万元，完成基础设施建设项目4个：一是投资518万元修红石路1300米，建设消防管道1300米，目前，红石铺路、下水道、监控、灯光等已全部安装到位；二是投资300万元，建设污水处理厂1个，目前工程已完工；三是投资50万元建设张店村桥梁一座，目前已完工；四是投资200万元，建设通往南山2.5公里的一条水泥路。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562587890362083.jpg\" alt=\"微信图片_201906271628561\" title=\"微信图片_201906271628561\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">依托张良文化、烟草文化所打造的文化旅游路线已初见雏形：上山看古寨赏自然风光、下山看张店明清古建学张良文化、进馆看烟草发展史赏烟叶小镇美景、入园尝百果品美味农家菜肴。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562587891141110.jpg\" alt=\"微信图片_20190627162856\" title=\"微信图片_20190627162856\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">重点园林建设<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">李口镇不断破解各类发展难题，全面建设美丽李口。张沟村建设各类红石文化广场，打造红石传统小山村，李口镇闫集地域清风苑总投资已达到450余万元，建起了李口镇对外宣传展示牌，总高度25米，沿南石路两侧的清风苑、清风湖、清风亭、清风石等已建成。成为李口镇对外宣传张良故里、烟叶小镇、历史文化名镇、平顶山后花园的窗口，成为李口建设生态李口、文化李口、宜居李口的目标与不懈努力方向。李口镇整体工作布局围绕张良故里、烟叶小镇，以点带面促镇域发展与对外形象整体提升。</span></p>','/uploads/20190708/78649fb63cb4b8b7646b595cd3607a7f.jpg','/uploads/20190708/07405a8acbe07e52c4702dc431fb2e06.jpg,/uploads/20190708/06802a24178d2b9d421022a354f342f0.jpg','1562588225',1,0,4,0,'象眼旅行—平顶山','113.242','33.97653',50,12,20,91,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——郏县——<span style=\"background-color: rgb(255, 255, 255);\">李口镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562588191578403.jpg\" title=\"1562588191578403.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(104,6,'郏县','','郏县广阔天地乡','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">广阔天地乡地势平坦，交通便利，气候宜人，全乡总面积14平方公里，辖11个行政村，1.4万人，1.4万亩耕地。可以说，广阔天地乡是地方不大名气大。1955年，毛泽东同志对河南省郏县大李庄乡《在一个乡里进行合作化规划的经验》一文作了光辉批示：“……，农村是一个广阔的天地，在那里是可以大有作为的。”</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562588293996918.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">正是因为毛主席的这个批示，引发了一场轰轰烈烈波及全国的知识青年上山下乡运动。从而广阔天地乡名扬天下。这里曾经发生的一切不仅在当时是中国社会发展实践的一个缩影，重要的是作为平顶山市唯一的全国文明村镇、河南省历史文化名镇、河南省人居环境范例奖获得者，2010年3月份又是全国景观旅游名镇的获得者，深厚的知青文化与优美的自然环境一起共同汇集了广阔天地的自然景观和人文景观，构成了丰富的旅游资源，形成特有的广阔天地知青文化的精髓。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562588294143548.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">为了开发知青特色旅游广阔天地乡本着尊重历史、展现历史、实事求是、立足广天、辐射全国的原则，创建了全国唯一的一家知青园景区，知青园下属广阔天地大有作为纪念馆、知青广场、知青旧居、知青之家、知青购物一条街等，旨在通过对历史的真实再现给人们以启迪，给青年以激励，给研究者以借鉴。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562588294948295.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">自2002年10月以对外开放以来，广阔天地乡党委、政府为了给游客提供更好的优质服务，在知青园景区旅游资源的基础上，与周边的三苏园、临沣寨、文庙以及汝州的风穴寺、怪坡等景点，强强联合，加大宣传力度，多措并举，推出了“作一天知青，体验知青生活一日游”、错峰免费等举措。当年的老知青把它作为抚今追昔，回味当年生活的第二故乡，常回家看看。有关部门还把这里作为对青少年进行艰苦奋斗、培养坚强意志、热爱劳动、热爱祖国的教育基地。更多的人则把她作为理想休闲观光的目的地。2013年，知青园景区被评为国家3A级景区、广阔天地大有作为纪念馆被评为河南省爱国主义教育示范基地，2015年被评为平顶山党员干部现代化远程教育教学培训基地和平顶山党员干部现代化远程教育工作示范基地，2018年被评为平顶山市廉政教育基地。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562588295706858.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">除了广阔天地大有作为纪念馆，目前，还有毛主席批示手迹碑，由知青们亲手建造的知青井、知青桥、知青坝、知青工厂等明显带有知青烙印的建筑。同时，上世纪70年代初由建设部专家组织、由公社社员共同建造的相对集中的社会主义新农村建设样板房共有16个小区114栋，建筑面积3.9万平方米，全部为两层红色楼房。这些红色楼房设计科学、布局合理、朴实简洁，具有鲜明的时代特色，成为当时社会主义新农村建设的标志，全国学习的榜样，推广的典型。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562588295899038.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">广阔天地乡不愧是中国知青运动的发源地。全乡村村有知青，家家有故事。对知青运动的全方位探讨与研究有着深远的历史意义和较高的理论研究价值。广阔天地乡邱庄、大李庄、杨庄、吴堂村是当年下乡知青最为集中的地方。这里的每一栋房子、每一个工厂、每一条道路、每一座桥梁、每一块土地等都与知青紧密相连。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">改革开放以来，广阔天地乡党委政府抢抓政策机遇，挖掘整合旅游资源，大力发展以知青文化为背景的乡村旅游业，投入大量资金对相关景点进行规划修复和维护，投资60万元编制《广阔天地总体规划》、《广阔天地乡旅游发展规划》等规划设计，开工建设了知青纪念馆、知青博物馆。这些规划和编制和实施既保持了当年知青运动时的原貌又改善了旅游参观的环境。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">广阔天地乡还把传统文化和现代文化有机结合，形成了旅游产业链，以适应不同阶层不同文化层次游客的需求。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">当年，知青的到来，使这里出现了勃勃生机，通过知青和广大干部群众的奋战，广阔天地的农业生产条件、农民科技意识、种田水平得到了很大提高，在知青的直接带动下，创办了当时广阔天地大有作为砂轮厂、锅厂等一大批企业，为广阔天地今后的经济发展打下了坚实的基础。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">随着国家改革政策的深入实施，目前，广阔天地乡铁锅生产企业已发展到106家，年生产6千万口，年产值8亿元，产品由原来的传统铁锅发展到高档搪瓷铁锅、不粘锅、保健铁锅等30多个品种、380多个系列。工艺也由原来的传统手工作坊型发展为现代流水线作业型。铁锅年总产量占全国半壁江山，产品畅销全国各地，还出口到韩国等13个国家和地区。广阔天地乡铁锅产业被评为河南省重点产业集群，广阔天地乡成为名副其实的知青铁锅第一乡，其中的赵华园村被誉为“中华铁锅第一村”。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">广阔天地乡优美的自然环境和良好的投资环境引来了一批又一批的客商。乡党委、政府因势利导，欢迎、鼓励各地知青及各地客商前来投资创业。由知青合资的企业28家，并成立了铁锅研发中心，这些产业的发展，不仅解决了当地不少农民工就业问题，带动了当地经济增长，同时，还吸引了大量游客参观、旅游，促进了当地工业旅游项目的发展。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">广阔天地乡党委、政府依托“广阔天地”这一特殊的优势，突出知青特色，大力开发知青旅游业，完善相应服务制度和服务质量。已初步形成了集旅游观光、休闲度假、体验今日农村生活、探究知青历史渊源、了解现实国情民生的知青文化旅游乡镇。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">目前，广阔天地乡正在加紧建设全国知青信息存储中心、知青文化研究中心。不久的将来，这里将打造成集旅游、休闲、购物、会议、商务、住宿、知青文化体验为一体的休闲度假区，逐步成为全国最能体验浓郁知青文化的核心区和精品乡村旅游度假名镇。</span></p>','/uploads/20190708/074f79bafeaa8be36d75c9ef3fb1a2e2.jpg','/uploads/20190708/6e44653b722db433fbca82f471ef9906.jpg,/uploads/20190708/50ec0a9d6c2c849bb74050a6c4a05dc1.jpg','1562588616',1,0,9,1,'象眼旅行—平顶山','113.29085','33.97043',50,12,20,94,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——郏县——<span style=\"background-color: rgb(255, 255, 255);\">广阔天地</span>乡</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562588596691028.jpg\" title=\"1562588596691028.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(105,6,'叶县','','叶县常村镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">常村镇位于叶县西南部35公里，西、南两面与南阳市方城县接壤，北界鲁山县，东邻夏李乡，全镇总面积178平方公里，辖37个行政村，208个自然村，283个村民组，总人口4.8万人，耕地面积6.7万亩。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562588783527308.jpg\" alt=\"G0010177\" title=\"G0010177\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">常村镇是叶县对外树立美好形象的西南大门，地势西高东低，西部边缘的老青山，又名西唐山，为叶县第一名山，主峰650.2米，是叶县、鲁山、方城之界山。境内省道逍白公路横贯东西，县道曹常线—常村至平顶山市区公路纵穿南北，乡村主干道150多公里，形成全镇纵横交错、四通八达的交通网络。2006年被确定为革命老区乡镇、2014年被国家七部委列为全国重点镇，后又被省政府列为全省重点镇。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562588784707299.jpg\" alt=\"0U1A0683\" title=\"0U1A0683\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">明初常姓来此建村，取名“常村”。1947年解放后，叶县人民民主政府设在常村，1965年7月成立常村公社，1984年由人民公社改为乡，2010年经省人民政府批准撤乡建镇，现被叶县人民政府确定为全县8个中心镇之一。1935年元月6日，红二十五军实施战略转移，北上抗日，在方城县七里岗被国民党军截击，战斗中26位红军战士壮烈牺牲，伤员由地方党组织安排在常村疗伤，在中共常村地下党组织的领导下，通过常村群众与国民党军队和地主武装的英勇斗争，对伤员精心疗养，使伤员很快得到了痊愈，痊愈后，伤员们继续北上抗日。2006年，常村被确定为革命老区乡镇。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">常村镇为浅山丘陵区，地势西高东低。境内有老青山等山头69座，河流13条。境内有丰富的林木、矿产、水产、旅游资源。林木资源：全镇林地面积11.5万亩，核桃等经济地3万亩，速生杨等用材林2.1万余亩，栎木资源2.2亩，境内植被茂盛，生态良好，森林覆盖率达到60%以上。矿产资源：有白云岩、高岭土等十多种矿藏，储量大，品位高。其中白云岩储量在1240万吨以上，白土矿储量也在千万吨以上，有较高的开采价值。水产资源：境内有孤石滩等大小型水库9座，为水产养殖提供了便利条件。其中孤石滩水库是以灌溉、防洪、发电、养鱼等综合利用的大型水库。境内澧河清澈见底，青山美景如画，自古这里就有“青山不墨千秋画，澧水无弦万古琴”的美誉，悠久的历史，灿烂的文化为美丽常村开发奠定了良好的基础。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562588784278406.jpg\" alt=\"G0030319\" title=\"G0030319\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">二、人文资源<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">常村镇文化底蕴深厚，远古时代，人们就在这里繁衍生息。岩画天书、仰韶文化遗址、楚长城遗址、高公祠、二贤祠、明代牛凤墓等文化古迹，记载着常村文化历史的古老、悠久和文明。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562588785155230.jpg\" alt=\"0U1A2435\" title=\"0U1A2435\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">风景优美的胡家、赵家、三道沟、牛角沟、白石崖沟、王房沟等小村落，实行旧村改造，创建宽敞、舒适、医疗、护理、一院多套的农家寄养公寓，为离休、退休职工，解决因子女工作忙无人护理的社会问题。同时也让这些一生劳累奔波的老人，体验晚年隐逸、恬淡、家人关爱的生活环境。健全环境管护、治安管理、人身安全防护等机制，创建山清水秀、镇净村美、宜居宜游的美丽常村。</span></p>','/uploads/20190708/4c3b528edf40408562785bb051766f05.jpg','/uploads/20190708/26d80df45f847e989f35ee28ca77cc7e.jpg,/uploads/20190708/cdb865884678489815cbe71c2b3ffde5.jpg','1562589012',1,0,14,0,'象眼旅行—平顶山','113.3575','33.62255',50,12,29,62,'<p>自驾游（1天）为佳。</p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——叶县——<span style=\"background-color: rgb(255, 255, 255);\">常村镇</span></span></p>','<p>春夏秋冬。</p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562588997567725.jpg\" title=\"1562588997567725.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\">长按识别关注 让旅行变得更简单</p>','平顶山市文化广电和旅游局'),(106,6,'郏县','','郏县龙湖社区','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">龙湖社区位于平顶山市郏县东北部，汉文帝之母薄姬冢附近，属于新型农村社区，这里风景秀丽，环境优美，龙湖湾旅游景区位于龙湖社区。旅游品牌有：圣贤教育之中华“孝心村”、 水上乐园、青少年社会实践教育培训基地、党员干部教育基地、千亩苗木花卉基地。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562589172184076.jpg\" alt=\"微信图片_20190621101709\" title=\"微信图片_20190621101709\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">龙湖社区，是孝悌文化发源和传承之地（中原孝心村），是家邻和睦的文明之乡，是鸟语花香、水流鱼欢、苗木林立、空气宜人的宜居之地，是培育团队凝聚力和战斗力，奋发图强精神的理想之寓，是廉政文化教育、党员、干部培训基地，是假期青少年教育和社会实践应用、国学教育、中华道德教育的理想大讲堂。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562589173995136.jpg\" alt=\"微信图片_20190621100659\" title=\"微信图片_20190621100659\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">龙湖湾集休闲农业与乡村旅游为一体，浑然天成，占尽天时、地利、人和。1800亩的苗木园林是天然氧吧和森林公园，这里空气清新，花卉苗木尽放异彩，争奇斗艳，景色宜人。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562589174920690.jpg\" alt=\"微信图片_20190621100358\" title=\"微信图片_20190621100358\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">龙湖湾水上乐园，游人们可以在网红桥摇摆、也可以透过喊泉释放自己的喜怒哀乐，还可以划一叶小舟在水中畅游！<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">龙湖湾不仅拥有1800亩的苗木园林，更有完善的培训基地，大（400人、200人），中（70人），小（20人）的多媒体会议室，同时建有容纳500人餐饮的饭店和300人住宿的宾馆。农副产品资源丰富，盛产冬瓜、红薯、粉条等。这里已经形成以会议接待培训、旅游观光、生态养殖、生态采摘，餐饮、洗浴、住宿、休闲、娱乐等为一体的乡村旅游示范景区。</span></p>','/uploads/20190708/020381337087f36dd9ae086ee84e7c38.jpg','/uploads/20190708/8057368f4d2cd452e4e7227d76a2dfaf.jpg,/uploads/20190708/eadf22c8577852bc42b975c9b9873346.jpg','1562589405',1,0,4,0,'象眼旅行—平顶山','113.29085','33.97043',50,12,20,130,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——郏县——<span style=\"background-color: rgb(255, 255, 255);\">龙湖社区</span></span></p>','<p>春夏秋冬。</p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562589377322641.jpg\" title=\"1562589377322641.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\">长按识别关注 让旅行变得更简单</p>','平顶山市文化广电和旅游局');
INSERT INTO `ddsc_rural` VALUES (107,6,'舞钢市','','舞钢市尚店镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">尚店镇位于舞钢市西南部，属半山区乡镇、东邻尹集镇，高速公路全境通过，区位优越，交通便利，经济发达，生态宜居。耕地87620亩，总人口42300人，辖32个行政村，生态环境极佳，林木资源丰富，森林覆盖率达66%。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190708/1562589534530214.jpg\" alt=\"微信图片_20190624141624 (1)\" title=\"微信图片_20190624141624 (1)\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">风光独特的五峰山自然风景区位于尚店镇东南部，位居周南高速出口三公里处，交通区位优势优越。山上有飞来石、刀劈石、情人石、刘秀洞、群仙会石群等，森林茂密，浓荫匝地，山顶怪石遍地，像人似物，美丽的风景伴有许多动人的传说，享有“舞钢第一峰”之美誉，奇特异常，是大自然鬼斧神工之作，吸引游客流连忘返，成为舞钢市发展特色旅游业重点景观。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562589535667509.jpg\" alt=\"微信图片_20190624141616\" title=\"微信图片_20190624141616\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">利用丰瑞生态观光园和油房山水库的天然优势，2019年争取上级资金扶持发展民宿旅游项目，为舞钢市填补了民俗文化空白。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562589536290388.jpg\" alt=\"微信图片_20190624141652\" title=\"微信图片_20190624141652\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">通过全方位的努力，尚店镇全域旅游工作在上级部门及乡政府的共同努力下，乡村面貌焕然一新，下一步，借助山水自然资源优势，计划发展民宿打造吃喝玩乐为一体的的乡村旅游景区。</span></p>','/uploads/20190708/5844658beb50dc5001f224996ac96b0f.jpg','/uploads/20190708/3771865d3687e9cff14cf2a783b060ec.jpg,/uploads/20190708/20a76cf7eb40b42630667ab7389b4fc9.jpg','1562589739',1,0,2,0,'象眼旅行—平顶山','113.53696','33.33359',50,12,26,100,'<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——舞钢市——</span><span style=\"font-family: 宋体, SimSun; background-color: rgb(255, 255, 255);\">尚店</span><span style=\"font-family: 宋体, SimSun;\">镇</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562589725519649.jpg\" title=\"1562589725519649.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(108,6,'鲁山县','','鲁山县四棵树乡','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">四棵树乡位于鲁山县西南深山区，背靠国有鲁山林场，东与下汤镇相邻，西与尧山镇相接，北与赵村镇接壤，南与南召县搭接。全乡总面积127.84平方公里，耕地面积4120亩，辖12个行政村，85个村民组，人口1.7万。境内旅游资源丰富，生态良好，森林植被茂密，森林覆盖率达90%，是鲁山县重点旅游乡镇。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562589859752789.jpg\" alt=\"四棵树乡3\" title=\"四棵树乡3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562589859735325.jpg\" alt=\"四棵树乡2\" title=\"四棵树乡2\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">近年来，四棵树乡立足实际，围绕“山”字做文章，持之以恒地抓好旅游开发、项目建设,经过多年的不懈探索和努力，逐步走向“文农旅”融合的生态发展之路，乡村旅游由小到大，由弱到强，建成了1家农家乐旅游接待中心，培育了中国乡村旅游模范村1个、河南省乡村旅游示范村1个，河南省乡村旅游创客基地1个，建成了德秀牌野生葡萄酒加工厂、张沟野猪养殖基地、一花柞蚕养殖基地，辐射带动农家乐宾馆136家。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190708/1562589860794925.jpg\" alt=\"四棵树乡5\" title=\"四棵树乡5\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p>','/uploads/20190708/d02674fdf2c35a59e551c27adc822fb8.jpg','/uploads/20190708/fa6a8603f6523ef36a18099269419e22.jpg,/uploads/20190708/9ef75d739a34ce8b2f4ffd24104623e5.jpg','1562590000',1,0,61,1,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,53,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">四棵树乡</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562589983474014.jpg\" title=\"1562589983474014.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(109,6,'舞钢市','','舞钢市枣林镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">枣林镇位于舞钢市最北部，地处平顶山、漯河、驻马店三市交汇处，南有滚河环绕，北有韦河束腰，焦桐、宁洛高速两侧延伸，省道七蚁线纵贯南北，镇村公路四通八达，素有舞钢“北大门”之称。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562591508615509.jpg\" alt=\"枣林镇安寨村富硒西瓜3\" title=\"枣林镇安寨村富硒西瓜3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">枣林镇有着厚重的历史文化。辖区内有枣林村南岗龙山文化遗址、安寨村的关帝庙、铁炉王村寨遗址等文物保护遗址。 游客可将体验刀耕火种的史前人类生活方式,真切感受龙山文化的厚重底蕴。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; text-align: center;\" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562591509132051.jpg\" alt=\"枣林镇李堂村莲藕 拷贝\" title=\"枣林镇李堂村莲藕 拷贝\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">通过发展莲藕种植、林果种植特色农业产业链条进一步壮大。打造了生态观光旅游产业的现代农业发展新模式。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; text-align: center;\" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562591509613734.jpg\" alt=\"张卜庄村晚秋黄梨\" title=\"张卜庄村晚秋黄梨\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">先后荣获国家级生态乡镇、国家级卫生乡镇、全国全民健康示范工程乡镇、全省“先进基层党组织”、“创先争优先进基层党组织”、河南省文明乡镇、河南省经济发达镇行政体制改革示范试点乡镇、河南省建制镇示范乡镇等国家级荣誉2项、省级荣誉20项、地市级荣誉34项，综合实力排名连续多年位居平顶山市前20强。</span></p>','/uploads/20190708/5fa286b6708bf12b9ab06b6b27777ecc.jpg','/uploads/20190708/aaf23b26f9276df7c07bc7f43c67411a.jpg,/uploads/20190708/032de43ca06fa7ee209cbf371f05fcbd.jpg','1562591662',1,0,3,0,'象眼旅行—平顶山','113.53696','33.33359',50,12,26,106,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——舞钢市——<span style=\"background-color: rgb(255, 255, 255);\">枣林镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562591644330305.jpg\" title=\"1562591644330305.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(110,6,'宝丰','','宝丰县大营镇','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">大营镇位于宝丰县城西20公里处，全镇总面积60平方公里，下辖34个行政村，1个社区，47个自然村，186个村民组，人口5.6万人。是全县第一人口大镇，第一资源大镇，是全省首批命名的“中州名镇”，省确定的“小城镇建设重点镇”，省命名的“省级卫生乡镇”，“河南省特色文化乡镇”、“河南省历史文化名镇”、“河南省特色旅游景观镇”、“河南省民间文化艺术之乡”。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562592960827089.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">大营镇历史悠久，文化底蕴厚重。春秋时期形成村落，已有2700多年的历史记载。宋代因冶铁、采煤、制瓷等业发达，被宋徽宗钦赐易名为“兴宝镇”。境内的清凉寺汝官窑遗址被学术界称为第二个“兵马俑”，是全国重点文物保护单位。汝瓷烧制技艺被列入全国非物质文化遗产名录。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562592960222832.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">大营镇资源丰富，区位优势明显。境内有主焦煤、铝粘土、铁矿石、伊利石等十余种，且储量大、品质优、易开采。该镇也是宛（南阳）洛（阳）古道重镇，是隋唐以来豫西鲁、宝、汝三县交界处商品物资的重要集散地，曾因市井繁华有过“双城”之美称。现在镇内交通发达，207国道、南石线和县道戎大线从镇穿境而过，平石快速通道与207国道相连接。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">近年来，在县委县政府的正确领导和支持下，借助国家乡村振兴战略契机，努力打造“一区一路一带”的乡村旅游，并积极创建平顶山市全域旅游优秀乡镇。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562592960459003.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">一区，即卧佛山汝窑文化生态旅游景区。该景区主要借助汝瓷文化这张靓丽的名片，通过旅游开发、文化兴旅，复兴汝瓷文化，打造文旅相融的汝窑文化生态旅游景区，可辐射清凉寺、白石坡、韩庄、李坪、边庄、上高庄和赵庄等7个村。目前，相关重点项目建设顺利：清凉寺汝窑遗址展示馆、汝窑博物馆已相继建成开馆；中国陶瓷工艺博物馆和平顶山学院陶瓷学院近期将开工建设；景区内的中国汽车拉力赛（宝丰站）赛场已建成，并圆满承办了“2018宝丰酒杯中国汽车拉力锦标赛宝丰赛区”的比赛。汝窑古镇项目已完成规划设计。另外，已建成樱桃谷、九龙湖等，卧佛山汝窑文化生态旅游景区初见规模。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">一路，即着力打造大观路沿线的赵楼、牛庄和上李庄三个休闲旅游民俗村。目前，赵楼村的合成农业种植基地内的梨树、苹果树挂满了水果，每到周末都能吸引比较多的游客观光。牛庄和上李庄着力打造民俗体验村，这两个村借助人居环境改善，效果明显，游园、亭子已陆续建成。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562592961762725.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">一带，即蛮子营村至小沈河村的泥河段沿线村，涵盖蛮子营、古垛、西张庄、西古庄和大小沈河7个村。借助古垛、大沈河两个省级传统村落建设，推动其旅游开发。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">大营镇境内独特的历史人文、自然景观简介：<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">1、清凉寺汝官窑遗址。位于大营镇西南3公里处的清凉寺村。清凉寺汝官窑遗址被学术界称为第二个“兵马俑”，是全国重点文物保护单位，汝瓷烧制技艺是国家级非物质遗产项目。该遗址呈不规则的长圆形，面积约133.2公顷，是宋、金、元代以来烧造历史延续数百年的重要制瓷场地，是北宋五大名窑之首。目前，以汝瓷文化为核心的清凉寺汝官窑考古遗址公园景区建设正加快推进。汝瓷大道已建成通车，汝瓷博物馆、汝官窑遗址展示馆等重点工程有望近期竣工，汝瓷烧造园、汝瓷古玩市场初具规模。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562592961251640.jpg\" alt=\"7\" title=\"7\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">2、中国传统村落村大营村。位于大营镇镇区，保存着许多明清及民国时期的传统建筑，如：山陕会馆、王之晋旧居、王栓柱老宅等，这些建筑结构严谨，气势恢弘，石刻、木雕、砖雕工艺精巧，集中反映出明清及民国时期豫西民居的建筑风格和工艺水平，具有很高的历史研究价值。其中，山陕会馆又名关帝庙，豫西军工处兵工厂二分厂，位于镇区南街村南部，是省级重点文物保护单位。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">3、中国传统村落村白石坡村。位于大营镇西南三公里处，村南是清凉寺汝官窑遗址，因村庄附近多有烧制汝瓷的主要用材—白色黏土，故名白石坡。村人自古至今多以烧制瓷器、陶器等为主，是国家级非物质遗产项目汝瓷烧制技艺传承村。村庄东西走向，依山丘斜坡呈梯田式自上而下逶迤而建，村中房屋坐北朝南。村中建筑大多石块地基、青砖灰瓦、精致典雅。村内清广寺已有上千年历史。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">4、老龙窝“三化”协调发展试验区。位于大营镇西南5公里处，是集观光、体验、休闲、养生为一体的绿色生态旅游景区。试验区内卧佛山山势奇特，九大连湖独具特色，山顶公园景色秀美。以农业观光、农事体验为主题的生态农业体验园内日光温室蔬菜采摘、樱桃谷石榴园果实采摘体验让游客流连忘返。另外，试验区内的市级文物保护单位—千年古寺寿丰寺也是豫西军工处兵工厂一分厂所在地，她为解放战争提供了大量的武器弹药。</span></p>','/uploads/20190708/0df493a8315b2dcd670ac2b25b8dda49.jpg','/uploads/20190708/015140acc57be70e95cfabaa36b54215.jpg,/uploads/20190708/338cff67bfedb73e6a81bc7b72191126.jpg','1562593178',1,0,3,0,'象眼旅行—平顶山','110.01107','32.30034',50,12,25,77,'<p>自驾游（1天）为佳。</p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——宝丰县——<span style=\"background-color: rgb(255, 255, 255);\">大营镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562593161538400.jpg\" title=\"1562593161538400.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化和旅游局'),(111,6,'鲁山县','','鲁山县尧山镇四道河村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">四道河村位于鲁山县尧山镇西部，距鲁山县城60km，辖区内有4A和3A级旅游景区各一家，位置优越、交通便利。该村共300多户，1800多口人。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562593302157270.jpg\" alt=\"四道河3\" title=\"四道河3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; background-color: rgb(255, 255, 255); font-size: 16px; color: rgb(0, 0, 0);\">全村基础经济以林为主。产品有柞蚕茧、核桃、猕猴桃、木耳、猴头菇、拳菜、辛夷、山朱萸、冰凌草、灵芝等，具有优越的风景资源及野生资源优势。据统计，在旅游资源的带动下，该村年人均收入已由原来的不足500元发展到现在的1万元。农民收入的显著增加。目前从事乡村旅游人数有500多人，经营农家宾馆有100多家，年受益500万元，带动贫困人口50多人。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562593302757719.jpg\" alt=\"四道河4\" title=\"四道河4\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p><br/></p>','/uploads/20190708/d068a91d45ea42b12b10c2bb7c43cf16.jpg','/uploads/20190708/da4b4526d6a77d3bfbbe50caca2a12db.jpg,/uploads/20190708/1a323e81f43f981583c79a370015cf57.jpg','1562593485',1,0,31,0,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,39,'<p>自驾游（2天）为佳。</p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">尧山镇</span></span></p>','<p>春夏秋冬。</p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190708/1562593471857970.jpg\" title=\"1562593471857970.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\">长按识别关注 让旅行变得更简单</p>','平顶山市文化广电和旅游局'),(112,6,'鲁山县','','鲁山县平沟村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">平沟村位于四棵树乡西北部，总面积20平方公里，林地面积29000亩，平均海拔950米，这里有珍珠潭风景区和挂鼓楼原始森林，人文景观有文殊寺。加上民风淳朴、山高林密、海拔较高，夏季凉爽，是休闲旅游的好去处。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562593572636323.jpg\" alt=\"平沟3\" title=\"平沟3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; background-color: rgb(255, 255, 255); box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">石林路从尧山、六羊山经平沟村到林彪指挥所。这里农家宾馆20多家，“五一”前来这里看漫山遍野的杜鹃花，秋季来文殊寺观赏老参天的银杏树落下的金黄的银杏叶，还可以到林中采摘野生猕猴桃、野葡萄、野柿子。现在正在对双庵开发，花园沟抽水蓄能电站正在酝酿中。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562593572109138.jpg\" alt=\"平沟4\" title=\"平沟4\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562593573935768.jpg\" alt=\"平沟村\" title=\"平沟村\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p><br/></p>','/uploads/20190708/b97e720e83af657fef2ab4716c299d1b.jpg','/uploads/20190708/1091fa3edfe7777333d4f0f912c88856.jpg,/uploads/20190708/6a194e4943fbf70fd0b4dc19ce66b035.jpg','1562593794',1,0,41,0,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,53,'<p>自驾游（1天）为佳。</p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">四棵树乡</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562593775194736.jpg\" title=\"1562593775194736.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(113,6,'鲁山县','','鲁山县赵村村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">赵村村主要旅游产品主要有：艺术根雕、奇石美石、蜂蜜、核桃、木耳、香菇等土特产品。“地方风味，乡土美食”，在我村屈指可数。广场舞、腰鼓队随时随地召之即来，艺术精湛；“尧山第一别苑”，乡土气息浓厚，匠心独具；新农村建设两期已入住290户1100人，环境优雅，民风淳朴；老街有百年老居，古色古香。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562593951302877.jpg\" alt=\"赵村村_副本\" title=\"赵村村_副本\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">赵村村北有S88武西高速至尧山5A景区和中原大佛5A景区，郑尧高速尧栾段正在兴建中；G311国道穿村而过，赵村大桥即将通车；村内通讯设备齐全，信号优良，移动、联通营业厅6处；医疗服务设施良好，有镇中心卫生院、村卫生室、妇幼保健所、大药房等；供水安全，排污设施良好，垃圾无存放，随时运往县垃圾处理厂。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562593951536342.jpg\" alt=\"赵村村2\" title=\"赵村村2\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">我村2012年就被平顶山市环保局命名为“市级生态村”，2016年被省环保厅命名为“省级生态村”，村里卫生整洁，绿化美化项目正在实施建造中，生活用水达到国家标准。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562593951959100.jpg\" alt=\"赵村村1\" title=\"赵村村1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">赵村村旅游服务完善，具备一定的旅游接待设施和服务能力，有农家宾馆25家，特色餐饮27家，服务热情，独具特色，集住宿、餐饮、停车、娱乐、购物、咨询为一条龙服务，功能配套完善，服务态度良好，游客满意多，回头客较多。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">赵村村有得天独厚的旅游发展条件，现在为国家五A级景区尧山风景名胜区和中原大佛景区的窗口，交通要道，中原大佛和佛泉寺等景区坐落在我村部分土地山林中。赵村村不仅交通方便，依山傍水，民风淳朴，风清景秀，而且旅游秩序良好，无坑蒙拐骗现象。</span></p>','/uploads/20190708/dab41f179a2473856d189a01f4824b36.jpg','/uploads/20190708/c03c91747cd4bd9d4e1d77e3d5bc15b1.jpg,/uploads/20190708/cb8b2a65cf1ece6aa0b8e12627792ce2.jpg','1562594219',1,0,7,0,'象眼旅行—平顶山','112.91666','33.74881',50,12,22,45,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">赵村镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562594201707430.jpg\" title=\"1562594201707430.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(114,6,'鲁山县','','鲁山县张沟村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">张沟村依托农家乐生态旅游为主导产业，原生态绿色林下野猪养殖，原产地保护柞蚕养殖基地等打造生态旅游项目，建成农家乐接待中心，推出了篝火晚会，手工烙油馍，山野菜，酱焖鸡等具有浓郁山村风味的特色产品，食品，吸引了众多外来游客。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562594555113181.jpg\" alt=\"张沟2\" title=\"张沟2\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">张沟村基础设施齐全，内外交通便利，道路干净宽阔，旅游标识醒目，自动化公厕干净卫生，有一千多平米的文化广场及标准的文化大舞台，垃圾集中处理，道路沿线摆放有垃圾桶，并有专人清理。通信网络全覆盖，医疗服务设施有标准化卫生室，停车场地宽阔方便。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562594556312782.jpg\" alt=\"张沟\" title=\"张沟\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px;\">张沟村生态环境良好，青山绿水，是一处天然氧吧。村容村貌整洁，公共服务场所卫生良好，生活用水尽是泉水，具备旅游接待设施和服务能力，可以满足游客的游览观光，住宿，餐饮，购物，咨询等需求，功能配套比较完善，服务达到一定水平。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562594557324663.jpg\" alt=\"张沟4\" title=\"张沟4\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">张沟村农家乐乡村游自2003年开办到现在享有很高的声誉，旅游人次和收入已具有一定规模，并且能够吸纳本地劳动能力，尤其是贫困人口就业，形成了良好的社会效益，管理有序，安全有保障，能够促进本地旅游的可持续发展。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><img src=\"/ueditor/php/upload/image/20190708/1562594557375513.jpg\" alt=\"张沟3\" title=\"张沟3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p><br/></p>','/uploads/20190708/9063b9e245ba8b1a251e07721b640bc0.jpg','/uploads/20190708/c3cb4a4ffd09981bb545a45f206d9387.jpg,/uploads/20190708/8803a947e0008bbafc2a85789f16bc85.jpg','1562594768',1,0,40,0,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,53,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p>平顶山市——鲁山县——四棵树乡</p><p><br/></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562594749806321.jpg\" title=\"1562594749806321.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(115,6,'鲁山县','','鲁山县龙潭村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">龙潭村位于鲁山县下汤镇东部，距下汤镇政府5公里，空气新鲜甘甜舒畅，生态古朴自然，四面环山，山路环绕，山间小溪四季叮咚，石峡水库包围着山，山围着水库，造就了天然的旅游圣地。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562594859154736.jpg\" alt=\"龙潭\" title=\"龙潭\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">龙潭村村民朴实诚恳，辛勤劳作，以种植业、养殖业、粉条加工及旅游业为主。龙潭粉条具有工艺淳朴、口感纯正、历史悠久等特色，在下汤具有良好的口碑；龙潭生态养殖基地，利用资源优势，成为中国树鸡发源地，建设别墅式鸡舍；发展生猪“千头线”，增加农民收入；更充分利用资源优势、政策优势，因地制宜，筹建仰天峡玻璃栈道及其附属工程等。站在龙潭的最高处，有一览众山小的感觉。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190708/1562594859644894.jpg\" alt=\"龙潭1\" title=\"龙潭1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p><br/></p>','/uploads/20190708/79ff8dfd4ae87d97e6d12ef27c5cba2c.jpg','/uploads/20190708/aae0ce481d52dfdf981ef3d2b7e5317c.jpg,/uploads/20190708/678183e10c88b232f7f8e3bdbaafb54d.jpg','1562595152',1,0,7,0,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,35,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">下汤镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562595136591422.jpg\" title=\"1562595136591422.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(116,6,'鲁山县','','鲁山县花园沟村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">花园沟村位于团城乡西南部，距乡政府12公里，位于清水河源头，属深山区，与南召县一山之隔，总面积10.1平方公里，耕地面积127亩，人均0.2亩。全村共有7个村民小组，23个自然村，185户778人，现有党员26人，村两委班子健全。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562596458109631.jpg\" alt=\"三道瀑\" title=\"三道瀑\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">花园沟村风景秀丽，自然资源丰富，森林覆盖面积达95℅以上。素有“天然氧吧”、“辛夷之乡”之美誉。野生五角枫、辛夷、映山红、山紫荆、黄芦柴等树木摇曳生姿，山药、葛根、蒲公英等野生中药材不胜枚举。花园沟村旅游资源丰富。历史古迹楚长城，原始风情化石山、铁瓦庙，千年辛夷王，万年榆，珍珠三道瀑等旅游资源如诗如画。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562596459890122.png\" alt=\"辛夷王\" title=\"辛夷王\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px;\">花园沟村农家野味多。椴木香菇、黑木耳、野蘑菇等食用菌，拳菜、臭娘叶、香椿、珍珠花等山野菜入口弥香。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><img src=\"/ueditor/php/upload/image/20190708/1562596460416760.jpg\" alt=\"楚长城\" title=\"楚长城\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p><br/></p>','/uploads/20190708/18f39125c07b09a1c5e0fe23bfefa004.jpg','/uploads/20190708/4daf7821bcd5f9c69e7ab78a07892c2c.jpg,/uploads/20190708/4e0d1ccd2695ed650bf75ea98de2bf4d.jpg','1562596646',1,0,17,0,'象眼旅行—平顶山','112.64265','33.66513',50,12,22,55,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">团城乡</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562596615901903.jpg\" title=\"1562596615901903.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p><span style=\"font-family: 宋体, SimSun;\"></span></p><p style=\"text-align: center;\">长按识别关注 让旅行变得更简单</p><p><span style=\"font-family: 宋体, SimSun;\"></span></p>','平顶山市文化广电和旅游局'),(117,6,'鲁山县','','鲁山县上坪村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">上坪村位于鲁山县尧山镇西南10公里处，总人口786人，总面积40平方公里。农业主产小麦、玉米、水稻、豆类、辛夷、特产板栗、猕猴桃、核桃、柿子、中药材、木耳、香菇、鹿茸菜。著名的诗景龙潭峡景区和即将开发的九龙沟景区都是位于本村内。上坪村森林覆盖率95%，蜿蜒蛇形，游鱼细石，清澈透底，异草奇葩，林丰果硕，资源丰富，环境优美，物华天宝，人杰地灵，丰富的旅游业和成熟的乡村旅游餐饮住宿成为地区代表。 诗景龙潭峡景区自然景观优美，拥有伏牛山系特有的山区资源，有清澈透碧的山泉水；有叹为观止的大瀑布；有山花烂漫峡谷美景；有天地通灵的情侣峰；有神奇的金龟把门等。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562596716137347.jpg\" alt=\"图片1\" title=\"图片1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">上坪村含7个村民组，又呈蛇形包围着诗景龙潭峡，其中3个村民组保持着山村古韵，4个小组是乡村旅游农家院经营单位。村庄整体形态与周边环境相融合，村景交融，整体风貌和谐统一，干净卫生，被镇政府评定先进村。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190708/1562596716435986.jpg\" alt=\"图片3\" title=\"图片3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p>','/uploads/20190708/06653f89e4b6d6acca218e3f93eb743e.jpg','/uploads/20190708/49c6bf1522a7e428360ed5f262d46e0d.jpg,/uploads/20190708/16e8de19c2ced6c04696d06b5f2aebd8.jpg','1562596954',1,0,26,0,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,39,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">尧山镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190708/1562596936418893.jpg\" title=\"1562596936418893.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(118,10,'舞钢市','','虎头山新四军烈士陵园','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">虎头山新四军烈士陵园位于平顶山市舞钢市尹集镇苇子园村虎头山上，陵园南北长350米，东西宽300米。由舞钢市人民政府创建于1992年。主要建筑有时任军委副主席张万年题字“虎头山革命烈士陵园”。陵园内有十七座新四军烈士墓和其他烈士墓碑，战斗机一架，大炮两门。广场前有十五米高花岗岩雕塑一组。雕塑为军旗飘扬下的新四军战士手持钢枪，手捧军号正在奏响战斗号角。战士神采飞扬气势雄壮，威震敌胆，喊杀震天，响彻山谷。它记载了1945年2月新四军黄林司令员率领的河南挺进兵团38团一营300多名官兵在嵖岈山西部虎头山地区与日寇展开激烈的阻击战。战斗中我军迅速抢占制高点虎头山，打退敌人七次进攻，阵地上的白雪被鲜血染红。在敌人第八次冲锋时终因寡不敌众我军在击毙、击伤日军180多人的情况下，壮烈牺牲20多人，其余官兵迅速撤退进入嵖岈山腹地。其中有十七名烈士被埋葬在虎头山上。该陵园保存较好，对于研究新四军在中原嵖岈山地区的战斗历史提供了实物资料具有重要的历史价值和军事价值。&nbsp;</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562643027610788.png\" title=\"1562643027610788.png\" alt=\"微信图片_20190709111907.png\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">虎头山新四军烈士陵园作为革命文物，它承载着革命先辈们许多可歌可泣的动人事迹，承载着他们英勇奋斗，不怕牺牲、百折不挠的革命传统、革命精神，是我们党和国家、人民永远的宝贵财富。所以保护好研究好、利用好这些革命文物，对于我们坚定文化自信，培育和弘扬社会主义主义核心价值观，不断夯实中国特色社会主义的思想道德基础，具有重要意义。2016年，被省民政府公布为第七批省级文物保护单位。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190709/1562642971399984.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">虎头山新四军烈士陵园由舞钢市人民政府承建。第一期工程始建于1992年，完成于2002年。虎头山新四军烈士陵园自建成以来先后被舞钢市人民政府、平顶山市人民政府和团省委、河南省人民政府命名为各级爱国主义教育基地，青少年爱国主义教育基地和国防教育基地，是目前平顶山地区唯一一处省级国防教育基地。近年来，周边地区党团组织、解放军和社会各界前来凭吊、瞻仰和接受教育。</span></a></p><p></p>','/uploads/20190709/66085c8621396cd22cb1bf919885bdf0.png','/uploads/20190709/724b3eb2ca91c94f153eb0b3cdabc370.jpg,/uploads/20190709/da925a2170cd329466780cd7dd8b2b13.jpg','1562643134',1,0,3,0,'象眼旅行—平顶山','113.53696','33.33359',50,12,26,101,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——舞钢市——尹集镇</span><br/></p><p><br/></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190709/1562643114301573.jpg\" title=\"1562643114301573.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(119,10,'叶县','','叶邑镇段庄老区革命纪念馆','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">段庄村是革命老区村、省级重点贫困村。1928年，平顶山市第一个农村党支部在段庄村建立。为了传承红色基因，弘扬老区精神，在市县老促会支持下，由叶县人民政府筹集近200万元，于2018年6月建成了960平方米的段庄老区革命纪念馆。馆内陈列着革命时期段庄村著名共产党员段语禅、段风和、段邵勋、段永健等人生前留下的工作和生活物品约40余件。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562719961489470.jpg\" alt=\"中共段庄支部旧址\" title=\"中共段庄支部旧址\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all;\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">这片沃土，曾孕育了叶县地区最早的农村党支部。早在大革命时期，段庄就已经开始出现革命的星星之火，是叶县革命老区的重要组成部分。</span></p><p></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562719961433273.jpg\" alt=\"段庄老区革命纪念馆4\" title=\"段庄老区革命纪念馆4\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">历经百年风雨沧桑，段庄涌现出了一批批坚强的革命斗士，他们用鲜血和生命，谱写了段庄和叶县的革命篇章。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562719962549022.jpg\" alt=\"段庄老区革命纪念馆6\" title=\"段庄老区革命纪念馆6\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">段庄支部建立后，做了许多卓有成效的工作。1936—1937年间党员发展到20人1938—1940年党员发展到28人，由于工作突出被河南省领导向中央汇报称段庄党支部是一类支部的典范。1938年11月段庄建立区党委，段永新任党委书记。1944年在这里设立抗日区政府，段永新任区长，在这所党支部活动过的故居里，召开过多次秘密会议 布置过多次夺枪动，组建起了第一支地下游击队。在都可祥的带领下击毙了大日伪汉奸薛伟。1937年在这里成立了叶县抗敌自卫团段庄分团，段永新任团长，带领革命群众开展抗敌自卫，组织开办识字夜校，募捐抗日物质，开展抗日宣传活动。直到1947年叶县解放。这里是叶县革命史上最活跃的地方和革命战争年代的红色堡垒村。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562719962334688.jpg\" alt=\"段庄老区革命纪念馆正面照\" title=\"段庄老区革命纪念馆正面照\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">特色文化活动</strong><br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">革命传统教育和爱国主义教育基地，通过开展参观学习，使所有参与活动的党员干部进一步增强了党性观念和党组织的凝聚力。党员们纷纷表示要以这次红色教育为契机，不忘革命先烈用鲜血和生命铸就的民族精神，把学习红色精神和传承红色基因落实到工作中，进一步增强“四个意识”，牢固树立“四个自信”，立足本职，务实重干，发挥共产党员的先锋模范作用，为财政事业发展做出自己应有的贡献。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">日常开展活动</strong></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">革命传统教育和爱国主义教育基地。</span></p>','/uploads/20190710/681eff175c35871776c81d6fdcb9e9d5.jpg','/uploads/20190710/8ad93891f253caae90418f4485e7a5ed.jpg,/uploads/20190710/59ee5767a07a9416a9394f7810e611ac.jpg','1562720183',1,0,14,1,'象眼旅行—平顶山','113.3575','33.62255',50,12,29,60,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">平顶山市——叶县——<span style=\"font-family: 宋体, SimSun; text-align: center;\">叶邑镇——段庄</span></span></p><p><span style=\"font-family: \" helvetica=\"\" font-size:=\"\" text-align:=\"\"></span><br/></p><p><br/></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190710/1562720165787647.jpg\" title=\"1562720165787647.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(120,10,'郏县','','八路军豫西抗日纪念馆（曹沟旧址）','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">八路军豫西抗日纪念馆（曹沟旧址），位于河南省平顶山市郏县安良镇塔林坡行政村曹沟自然村。是河南省罕见的规模性八路军豫西抗日遗址，在河南抗战旧址中具有代表性。这里，伏牛山群峰环绕，野花林果遍地，环境幽僻自然，多有窑洞石屋，民风醇厚朴实；更有白云寨、灵泉寺和凤翅山洞点缀左右，风光毓秀。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\"><img src=\"/ueditor/php/upload/image/20190710/1562720376125709.jpg\" alt=\"景区大门\" title=\"景区大门\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">曹沟山灵水秀自古有之，源远流长。早在三国时期，曹操就在此屯兵牧马，曹沟也因此而得名。千年之后，王树声、戴季英、皮定均、徐子荣、张才千、刘昌毅等一代伟人，曾居守这里，浴血奋战，抗击日寇，谱写了壮烈的八路军豫西抗日斗争史。1944年4月，日本帝国主义为了援助其深入南洋的孤军，发动了打通中国大陆交通线的豫湘桂战役之河南战役，驻守河南的国民党四十万大军一触即溃，豫西地区很快沦陷。为了收复失地，解放河南人民，1944年7月，中共中央发出了向河南敌后进军的命令。先后组建了八路军六个支队，在王树声、戴季英等率领下南渡黄河，挺进豫西，创建了北抵黄河，南接伏牛，西起崤山，东邻平汉，雄踞中原的豫西抗日根据地；曹沟成了豫西抗日根据地腹地。1944年10月中共中央延安杨家岭会议决定成立河南军区，至1945年5月，中共河南区共开辟了六个专区，其中中共河南区第六专区和中共禹郏县的党政军机关驻扎曹沟。在这里，中国共产党领导抗日军民，浴血奋战，开辟了八路军豫西抗日主战场，贯通了鄂豫皖根据地与延安后防之链接，组建了稳固的县乡村地方政权，曹沟也成为豫鄂边新四军根据地联系陕北根据地的必经之地。</span></a></p><p></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\"><img src=\"/ueditor/php/upload/image/20190710/1562720377832500.jpg\" alt=\"景区雕塑\" title=\"景区雕塑\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">八路军豫西抗日纪念馆（曹沟旧址）占地108亩，核心区有三个院落三十三间房屋，二十一孔窑洞，三座炮楼，两条地下通道，一处暗堡。南院驻扎禹郏县独立团；前院设禹郏县抗日民主政府；后院为六专区党政军机关办公地；设司令部、政治部、区专署等机构。另有抗日宣传队、医护所、枪械所等分散左右，各种窑洞有109条；因战时需要，窑洞之间往往有暗洞相连，迂回曲折，机关无数。房舍大多为清末建筑，经百年沧桑，历战火洗礼，保存尚好。</span><span style=\"font-family: 微软雅黑, \" microsoft=\"\"><br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">八路军豫西抗日纪念馆（曹沟旧址）是政府事业单位。在各级党委、政府的关怀与支持下，曾经对核心旧址略施修缮，增图文及实物，以展示史事，传承文化，弘扬精神，激励来者。2004年3月以来,边维护边对外开放，接待社会各届的参观，使更多的党员干部和群众来到曹沟接受革命传统教育。2007年以后，纪念馆已达到一定规模。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\"><img src=\"/ueditor/php/upload/image/20190710/1562720377755462.jpg\" alt=\"景区核心旧址\" title=\"景区核心旧址\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">2014年，郏县县政府决定投资1500万元，第三次实施曹沟旧址保护和修缮工程，对主题旧址进行保护性修缮，更新了供水、供电和公厕、道路等基础设施；实施了绿化工程；在旧址外围新开辟9660余平方米空间，建设了豫西抗日根据地革命历史展示和教育平台。新建1300平米八路军豫西抗日曹沟纪念馆，塑造了八路军豫西抗日纪念馆和八路军豫西抗日主题群雕，重建景区大门及管理服务区；积极打造国家AAA级景区。在世界反法西斯战争胜利暨中国人民抗日战争胜利七十周年纪念日前夕，八路军豫西抗日纪念馆（曹沟旧址）免费对外开放。据统计，2014年全年接待游客8万多人；新建八路军豫西抗日曹沟纪念馆开馆后，仅2015年9月1日-20日景区接待各地游客120000余人，其中9月3日一天观展人数多达15000余人。至目前，豫西抗日根据地曹沟旧址（景区）已接待各类游客已超过1000000人次。社会各界反响强烈，发挥出了良好的社会效益。已被评定为国家级国防教育示范基地、国家老促会“弘扬老区精神 传承红色基因”示范基地、国家AAA级旅游景区、河南省爱国主义教育示范基地、河南省国防教育示范基地、河南省中共党史教育基地、河南省重点文物保护单位、平顶山市党性教育现场教学点、平顶山市青少年爱国主义教育基地、河南城建学院大学生思想政治教育基地、平煤神马医疗集团党性教育基地等一系列荣誉称号。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, \" microsoft=\"\" font-size:=\"\"><img src=\"/ueditor/php/upload/image/20190710/1562720378935814.jpg\" alt=\"景区鸟瞰图\" title=\"景区鸟瞰图\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"><span style=\"font-size: 16px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\">特色文化活动</span></strong><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"><span style=\"font-size: 16px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"></span></strong></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">爱国主义教育现场教学、主题党日活动现场教学、党性教育现场教学、国防教育现场教学及其他“红色”主题活动现场教学。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\"><strong style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-family: 微软雅黑, \" microsoft=\"\">日常开展活动</span></strong></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun;\">纪念馆基本陈列展</span></p>','/uploads/20190710/99247c9dcaabfd4f407d671ee03c3daf.jpg','/uploads/20190710/077f8a1fe79dc138b6d0172f101ee825.jpg,/uploads/20190710/a510fe5c748df056c8ef892c94f1bb39.jpg','1562720601',1,0,18,0,'象眼旅行—平顶山','113.29085','33.97043',50,12,20,87,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——郏县——安良镇——曹沟</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190710/1562720573178730.jpg\" title=\"1562720573178730.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(121,9,'宝丰','','宝丰县石桥镇高皇庙','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">高皇庙位于平顶山市宝丰县石桥镇高皇庙村中西部，清代。平面呈长方形，坐北朝南，该庙始建于汉，鼎盛于宋、明，现存高帝殿、文昌殿各三间，碑刻11通。高帝殿为筒瓦盖顶，面阔10.2米，进深7.4米，殿内明石柱二根，暗石柱二根。文昌殿面阔三间9米，进深7米。硬山灰瓦顶，殿内有明柱二根，暗柱二根，柱础为石质，古色古香的前檐走廊，其八角柱石和圆柱木精雕细刻。现有建筑保存基本完好。庙内立有明万历41年、清康熙44年、乾隆9年、嘉庆11年、道光10年、咸丰年间对高皇庙进行增建、修缮和捐款的碑刻11通，并有古柏1株，木香1丛。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562721944370043.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">高皇庙历史悠久、底蕴深厚，对研究当地的历史、人文环境以及宗教文化都具有一定的价值。2016年，被省政府公布为第七批省级文物保护单位</span></a><a style=\"white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\"></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">。</span></a></p><p></p><p></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p>','/uploads/20190710/f798f158254992312acd7bcb6cb5f07f.jpg','/uploads/20190710/26ccdda436371739d0281b5c0146cc2d.jpg,/uploads/20190710/90545f247a68ae3e53d65874846904a1.jpg','1562722073',1,0,3,0,'象眼旅行—平顶山','114.25965','30.58015',50,12,25,79,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><a style=\"white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\"></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">平顶山市——宝丰县——石桥镇</span></a></p><p></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190710/1562722055678281.jpg\" title=\"1562722055678281.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(122,9,'宝丰','','宝丰县龙兴寺及石塔','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">龙兴寺及石塔位于平顶山市宝丰县前营乡小店头村龙兴寺自然村，明、清。因唐宋时宝丰又名龙兴县，隶属汝州，故亦称汝州龙兴寺。元延祐年之后，龙兴寺又称训狐寺，盖因寺在训狐山之故。龙兴寺寺院坐北朝南，地势西南高东北低，殿宇建筑依山就势，北高南低，错落有致，原占地约40余亩。现仅存明清时期建筑9栋，建筑依次为：敌楼、下过殿、斋堂、客堂、皇姑殿、寮房、耳房、佛爷殿、西佛殿。其中西佛殿原为华严小殿，为明代建筑，其余建筑为清道光三十年（1850年）重建。总建筑面积约410平方米。</span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; text-align: center;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562722189257713.jpg\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></span></a></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">石塔位于村西北。该塔为明嘉靖十年（1531年）创建。塔为七级雕刻，高3.5米、塔基呈2.5米见方的正方形，均为青石雕刻垒砌，塔身二、五层为八角形，三层为四角形，四、七层为莲花形，塔顶有塔刹。</span></a></p><p></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">龙兴寺及石塔对研究古代历史文化、建筑雕刻艺术具有重要的作用，有较高的历史价值。2016年，被省政府公布为第七批省文物保护单位</span><a style=\"white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\"></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline !important;\"><a style=\"white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">。</span></a></p><p></p><p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\" white-space:=\"\"></a></p>','/uploads/20190710/fb191836f6aa307431772b36b32d92bc.jpg','/uploads/20190710/7fd63af7c1f5a23f61653bd6cd939a4e.jpg,/uploads/20190710/64be644824ccfeecc718200c6cd595c1.jpg','1562722316',1,0,19,0,'象眼旅行—平顶山','114.25965','30.58015',50,12,25,84,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><a style=\"white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\"></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><a style=\"white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" color:=\"\" font-size:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">平顶山市——宝丰县——前营乡</span></a></p><p></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190710/1562722299944940.jpg\" title=\"1562722299944940.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(123,9,'平顶山','','香山妙善观音信俗','<p><a style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-family:; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"></a></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\"><a style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-family:; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\">平顶山香山寺建于东汉末期，大约在汉灵帝光和年间，比白马寺晚百余年，历经汉、魏、晋、南北朝、隋、唐、宋、元、明、清，迄今已有1800多年的历史。由于地处中原，受朝代更替、兵火战乱的影响，屡毁屡建。20世纪50年代，还有部分明清所建殿堂，“文革”时尽遭破坏，僧人被赶走，殿堂被拆毁，如今的香山寺建筑群除千年宝塔和十几通古碑外，都是“文革”后依靠民间力量和政府支持逐步恢复起来的。</span></a></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; text-align: center; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\"><a style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-family:; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><img title=\"香山寺\" style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" alt=\"香山寺\" src=\"/ueditor/php/upload/image/20190710/1562724497771804.png\"/><span style=\"font-size: 16px; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\">&nbsp;</span></a></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\"><a style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-family:; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><br style=\"max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\"/></a></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\"><a style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-family:; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\">历史上的平顶山香山寺规模宏大。其主要部分依据香山山峰圆润的天然地理形势而建，一直保持着以塔为中心、四面配以殿堂的曼荼罗式布局形式。整体分布上横跨三座山峰，呈现出以香山为中心，以东西龙山为两翼，前出山脚，包括西院和南院的格局。同时还有很多分布在周围地区的下院。历代香山寺，一般都建有山门、金刚殿、天王殿、韦陀殿、关圣殿、弥勒殿、四面佛殿、观音殿、大雄宝殿、伽蓝殿、祖师殿、六祖殿、地藏殿、广生殿、山神殿、包公殿、藏经殿、法堂、禅堂、客堂、钟楼、方丈以及魁星楼，还包括佛塔、墓塔、经幢等建筑。</span></a></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; text-align: center; margin-top: 0px; margin-bottom: 0px; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\"><a style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-family:; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><img title=\"香山寺1\" style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; width: 545px; height: 463px; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" alt=\"香山寺1\" src=\"/ueditor/php/upload/image/20190710/1562724497119021.png\"/><span style=\"font-size: 16px; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\">&nbsp;</span></a></p><p style=\"padding: 0px; border: 0px currentColor; border-image: none; margin-top: 0px; margin-bottom: 0px; display: inline !important; -ms-word-break: break-all; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\"><a style=\"margin: 0px; padding: 0px; border: 0px currentColor; border-image: none; font-family:; -ms-word-break: break-all; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\" helvetica=\"\"><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun; font-size: 16px; max-width: 100%; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0);\">以香山观音为信仰对象的平顶山香山观音信俗，是广泛分布于平顶山地区的一种民间信俗文化现象，其历史悠久，传承广泛，影响深远，底蕴深厚。平顶山香山观音信俗包括传说信俗、道场信俗、庙会信俗和民间信俗等，具有重要的历史价值、艺术价值、文物价值和科学价值。</span></a></p><p><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">2015年，该项目被列入第四批河南省省级非物质文化遗产名录。</span></p>','/uploads/20190710/34be35bcc8fca57cd88573b249dd2213.jpg','/uploads/20190710/4ce3b6ea705ae40e4c63188414610e1a.png,/uploads/20190710/879e7f0f3f76f8594721a2855fd2f259.png','1562724713',1,0,44,1,'象眼旅行','113.20913','33.74882',50,12,31,0,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——新华区</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img title=\"1562724684256406.jpg\" alt=\"微信图片_20190707093953.jpg\" src=\"/ueditor/php/upload/image/20190710/1562724684256406.jpg\"/></span></p><p><span style=\"font-family: 宋体, SimSun;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局');
INSERT INTO `ddsc_rural` VALUES (124,6,'舞钢市','','舞钢市臧坪村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">杨庄乡臧坪村位于舞钢市南部，下辖左营、臧坪、完庄、贾沟4个自然村，11个村民组，共468户1740人，总面积6.3平方公里，属山地丘陵地貌。</span><img src=\"/ueditor/php/upload/image/20190710/1562742520251705.jpg\" alt=\"大美杨庄－夕阳下的山村\" title=\"大美杨庄－夕阳下的山村\" style=\"font-size: 16px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0); font-family: 宋体, SimSun;\">臧坪村交通便利，距离市区、焦桐高速舞钢南站均为10公里，市区通往灯台架景区和祥龙谷景区的旅游交通干线杨长路穿村而过，也是舞钢市开工建设的旅游景观大道水袁线南起点，周南高速舞钢出口所在地和省道S228卫新线的必经之地。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562742524763836.png\" alt=\"臧坪彩虹滑道\" title=\"臧坪彩虹滑道\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">2018年确定为省级旅游特色村。村内环境优美，森林覆盖率高，已开发的AAA级景区祥龙谷、佛光禅寺等景观和金凤凰养殖园、中草药种植园、集装箱酒店等农旅一体化项目吸引了众多游客来此休闲旅游。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562742526510526.jpg\" alt=\"见证历史的古树。 摄影--范垂范。 单位--舞钢离退休管理部老年摄影协会。联系电话--15836949336.\" title=\"见证历史的古树。 摄影--范垂范。 单位--舞钢离退休管理部老年摄影协会。联系电话--15836949336.\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><br/></span></p><p><br/></p>','/uploads/20190710/5a3aa96ba2d7b821d27714aef717b57a.jpg','/uploads/20190710/9771238a7269f8560bbc67b5f28d0e27.jpg,/uploads/20190710/4d2fb3a18de0cbfe7b26d9909998401d.jpg','1562742794',1,0,17,0,'象眼旅行—平顶山','113.53696','33.33359',50,12,26,103,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p><p><br/></p>','<p><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun;\">平顶山市——舞钢市——<span style=\"color: rgb(0, 0, 0); background-color: rgb(255, 255, 255);\">杨庄乡——<span style=\"color: rgb(0, 0, 0); background-color: rgb(255, 255, 255);\">臧坪村</span></span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190710/1562742751922015.jpg\" title=\"1562742751922015.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(125,6,'舞钢市','','舞钢市张庄村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">舞钢市尹集镇张庄村位于舞钢市南部，龙凤湖旅游度假区域内，南靠螃背山，北临国家级风景区石漫滩水库，尚尹公路穿梭而过，距离焦桐高速5公里，交通便利。张庄村风景秀丽，村落在群山怀抱之中，尤其是正在建设的张庄社区，公共设施齐全，规划合理整齐，在山水辉映下呈现出独特的自然美，走在村中，有“人在村中走，如在画中游”的美妙感觉。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562742922215381.jpg\" alt=\"张庄风景\" title=\"张庄风景\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">一、基本情况<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">张庄村有7个自然村，有8个村民组1076口人，人均耕地不到0.5亩，交通不便贫穷落后。旅游资源丰富，旅游产业兴旺，随之延伸的农家乐、农家宾馆给群众带来了很好的收益，如今张庄农家乐已经发展到56家，张庄农家乐成为享誉周边的品牌。已建设的张庄社区，公共设施齐全，规划合理整齐，在山水辉映下呈现出独特的自然美。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562742923241051.jpg\" alt=\"张庄社区（白东亮  摄）\" title=\"张庄社区（白东亮  摄）\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">二、旅游特色资源&nbsp;<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">张庄村立足本村资源，依托处在龙凤湖旅游度假区的区位优势，创新发展模式，定位为“吃农家饭、享农家乐”为主的旅游服务业和林果种植采摘、休闲农业观光游项目。张庄村现拥有“农家乐”56余家，农家宾馆10余家，旅游服务业已成为张庄的主导产业，入住社区农户收入大幅增加，年人均收入达到1.5万元。张庄村2010年被河南省旅游局确定为“百村万户”旅游富民工程特色村，2011年被河南省旅游局评为全省十大乡村游重点推介线路之一，2012年荣获河南省文明村，2013年又被河南省旅游局评为河南省“我想去旅游—魅力乡村行”5个精品示范村之一，2014年入选中国最美休闲乡村名单。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562742923918678.jpg\" alt=\"葡萄新品种\" title=\"葡萄新品种\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">（一）采摘园<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">张庄村采摘园位于张庄中心社区西部丘陵坡地，并紧邻张庄社区。南北平均长503米，东西平均宽173米，全园总面积133亩，分为春、夏、秋、冬、综合服务区和入口服务区。旅游观光采摘园，是集园林、旅游、果园生产采摘于一体，经济效益、生态效益和社会效益相结合的综合产物，是张庄中心社区大力发展旅游的一个重要环节，对于加快新型农村社区建设和生态文明建设具有巨大的促进作用。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562742924565800.jpg\" alt=\"山水张庄\" title=\"山水张庄\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">（二）螃背山<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">螃背山又名盘古山，位于张庄村南部，山区森林覆盖率达到98%，景区内峰奇洞幽、怪石峥嵘、气势壮观，远眺层峦叠嶂，云蒸霞蔚，近听鸟语欢唱，泉水叮咚。晴天可览群山野趣，飞禽走兽，雨后可赏白云游弋，飞瀑悬帘。春来山花烂漫，夏至阴郁敝日，秋季野果飘香，冬日群山素裹，玉树琼枝。集峻、险、奇、幽于一体，置身于此，可尽情体验回归大自然之乐趣。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562742924989221.jpg\" alt=\"石漫滩水利风景区端午节龙舟比赛。\" title=\"石漫滩水利风景区端午节龙舟比赛。\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">（三）石漫滩水利风景区<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">石漫滩水利风景区位于张庄村北部，山深林密，空气清新，风景如画，和复建后的石漫滩水库依依相偎。离舞钢市区仅为2公里，交通十分便利。始建于1998年，这里山峰叠翠、草木送香、群山环抱、水映青山，是“逐烟波于四时，赏彩霞于朝暮”的好去处。石漫滩以秀美的风光，山水林城融为一体的鲜明个性，于2001年被批准为首批国家水利风景区。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562742925793549.jpg\" alt=\"香杏\" title=\"香杏\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">（四）农家乐餐饮<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">张庄社区农家乐开始发展于2003年，经过近10几年的发展，已经由原来的家庭为单位发展成为专业经营为主导，家庭经营为辅助的经营模式。社区农家乐餐饮现有56家，农家院一号、南湖庄园、龙湖商务酒店为代表的专业经营已经走上正轨化道路，农家乐就餐环境优美，卫生条件达标，价格合理，符合食客的就餐要求。以农家院九号为代表的家庭式经营，让前来就餐的游客感受到农家的生活乐趣，品尝农家饭的美味。张庄农家乐以地锅鸡、豆腐脑、磨糊、懒豆、焦馍、山野菜等菜品为主导，已经成为周边首选的就餐场所。游览龙泉湖，观张庄社区，尝采摘果品，食农家饭美味，你一定会有不一样的收获。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190710/1562743188189419.jpg\" title=\"1562743188189419.jpg\" alt=\"微信图片_20190710150924.jpg\" width=\"882\" height=\"445\" style=\"width: 882px; height: 445px;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">(五)山野菜种植、加工等特色产业<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">随着前来社区旅游的人数增多，带动了张庄旅游产业链条的发展。目前社区已有3户山野菜加工，引进新机器，利用新技术，对山野菜进行精加工，给群众带来了良好的收益。同时实现了农家特色菜--山野菜加工-山野菜种植的链式方式，实现产业联动，经济循环发展。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190710/1562743176423084.jpg\" title=\"1562743176423084.jpg\" alt=\"微信图片_20190710150950.jpg\" width=\"879\" height=\"437\" style=\"width: 879px; height: 437px;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">(六)旅游纪念品加工<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">依托社区集生活居住、观光旅游、休闲度假、服务为一体的优势，结合农家乐等服务业的发展现状，根据社区内部分闲散劳动力的实际，社区组织剩余劳动力开发了旅游纪念品加工行业，刺绣、编织等旅游纪念品深受广大游客喜爱。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">(七)旅游带动其他项目发展<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">张庄的区位优势所在及旅游的带动发展，更多的项目旅游滋生产业得到了发展,将解决张庄300人就业问题，同时推动张庄旅游产业的进一步发展。正在筹建的舞钢市首届乡村文化节在张庄举办，设置有民宿美食、农特产品展示、啤酒花园、童乐区、大型书画展等。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\"><img src=\"/ueditor/php/upload/image/20190710/1562743243173818.jpg\" title=\"1562743243173818.jpg\" alt=\"微信图片_20190710150539.jpg\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">走进张庄社区，这里优美的环境和独特的建筑引人入胜，蓝天白云、鸟语花香、小桥流水加上袅袅炊烟，简直让你分不清这里是景区还是社区。社区内所有灰瓦白墙、自成风格的建筑，都是由清华大学专家整体策划设计的。除了漂亮的两层民居，社区内还配套有宽广的道路、秀美的景观、宽敞广场、高大的戏楼，另外还有公厕、游园、垃圾中转站、污水处理厂、综合服务楼及供水供电系统，社区内还开办有卫生服务站、健身活动房、移动公司、邮政服务、银行系统、大型超市等，社区幼儿园基础设施齐全。</span></p>','/uploads/20190710/223dc83ef80eba728cd310f38c7fac5a.jpg','/uploads/20190710/12fc916396d2c8814cdc2a982deef241.jpg,/uploads/20190710/087246c5feb7839e67ccd3810d611550.jpg','1562743377',1,0,11,0,'象眼旅行—平顶山','113.54267','33.33272',50,12,26,101,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（2天）为佳。</span></p>','<p><span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun;\">平顶山市——舞钢市——尹集镇——张庄村</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190710/1562743349302527.jpg\" title=\"1562743349302527.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(126,6,'河南省平顶山市宝丰县','','宝丰县周营村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">周营中心村位于宝丰县城北10公里，赵庄镇政府东南3公里，石河环绕该村东北两面，土地肥沃，水利条件优越。该中心村下辖周营、白店2个行政村，共有人口1920人，734户，耕地面积3550亩。农民人均纯收入达到1.3万元。是赵庄魔术的发祥地，远近闻名的“魔术专业村”是市级文明村，更是“宝丰文化现象”的孕育之地。先后迎接了刘云山、李长春、曹刚川、徐光春等中央、市领导的调研。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562743661955075.jpg\" alt=\"周营村3\" title=\"周营村3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">建国前，村民为了生存，就学几套木偶、魔术，背井离乡、卖艺谋生，社会上至今仍流传着这样的顺口溜：“周营村，圆周周，不唱大戏玩木偶”。建国后，该村组建了第一个木偶剧团，曾代表市、县参加了河南省首届曲艺、木偶、皮影汇演，其节目《伍子胥过关》荣获一等奖。同年，他们成立了宝丰县第一个魔术团，编排了《箱中换人》、《人头搬家》等20多个魔术节目。改革开放后，该村的魔术团由当初的几个，发展到如今的112个，演出节目380多套，从业人员2900多人，演出足迹遍及全国31个省、市、自治区，并多次为外国友人专场演出，年收入逾亿元。2012年，本村魔术师靳全亮和靳晓东还参加了央视3套《越变越好》和《我要上春晚》的节目录制，进一步提高了赵庄魔术在全国的知名度。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; text-align: center;\" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190710/1562743662357943.jpg\" alt=\"周营村6\" title=\"周营村6\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">目前，该村拥有投资600万元的魔术培训中心1个，投资300万元的魔韵茶楼1座，年生产7200吨的面粉厂1家，年出栏3000头的商品猪养殖场1个。近年来，筹资800多万元，建成了文化广场、文化大院、文化剧场、艺人新居等工程，村容村貌得到了明显的改善。周营中心村社区自启动以来，已建成新民居27套，已全部入住。第二批新居计划50套，已建成，正在装修入住。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; text-align: center;\" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><img src=\"/ueditor/php/upload/image/20190710/1562743662322521.jpg\" alt=\"周营村\" title=\"周营村\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p>','/uploads/20190710/245e701aaa1392cc655ddfa5f6f126cb.jpg','/uploads/20190710/47215b1ff85704b2202721a10916aab0.jpg,/uploads/20190710/55086240002842fd4d15336a53c89f25.jpg','1562743962',1,0,21,0,'象眼旅行—平顶山','113.06004','33.86234',50,12,25,127,'<p><span style=\"font-size: 16px; font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——宝丰县——<span style=\"background-color: rgb(255, 255, 255);\">周营村</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190710/1562743934603360.jpg\" title=\"1562743934603360.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(127,6,'郏县','','郏县临沣寨','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">临沣寨位于河南省中西部郏县堂街镇境内，距县城12公里，南距平顶山市20公里，北距省会郑州市120公里，交通便利；寨内现有居民135户共578人，博物馆总面积约为1.5平方公里（临沣寨占地约7.2万平方米）。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562899638584849.jpg\" alt=\"101  历史文化名村铜匾\" title=\"101  历史文化名村铜匾\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">临沣寨是中原地区保存最完整的古村寨，是中国江北地区堡寨式古村落的典范。寨内现存明清及传统建筑共计297栋926间，其中明清建筑175栋562间，传统建筑122栋364间。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562899638980035.jpg\" alt=\"103  临沣寨全景\" title=\"103  临沣寨全景\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">临沣寨于2013年3月被国务院公布为第七批“全国重点文物保护单位”。在此之前，临沣寨于2002年9月被确定为“省级文物保护单位”；2005年9月被建设部、国家文物局联合公布为第二批“中国历史文化名村”；2009年被列入《联合国战时文物保护公约》的保护范围；2010年元月被公布为“河南省特色景观旅游名村”；2011年8月国家文物局将“中国临沣寨乡村生态博物馆”确定为全国生态博物馆建设试点村（河南省唯一）;2012年12月被住建部、文化部、财政部公布为第一批“中国传统村落”，2013年3月，被国务院公布为第七批全国重点文物保护单位。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562899639873517.jpg\" alt=\"105  御墨翰林院\" title=\"105  御墨翰林院\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">临沣寨以红石古寨、外围水系和千亩芦苇荡为依托，以乡村民俗文化为主题，是集古寨风貌观光、寨外生态休闲、中原民俗文化体验于一体的综合性村落。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562899639616689.jpg\" alt=\"106  一进院月台\" title=\"106  一进院月台\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">主要资源包括：1.红石寨墙、寨门。初建于明代，重修于清同治元年，呈不规则椭圆形，为纯一色红石筑砌而成，高6.7米，厚3-5米不等，围长1100米。寨上设有寨门楼三个，哨楼五个、垛口800余个。按八卦方位设有“溥滨”、“临沣”、“来曛”三个寨门；2.明清古建筑群。寨内有保存完整的明清四合院11处，从明末至今一应俱全，在时代上没有缺环，而且在各个时间段上均有绝对纪年的标志性建筑。“五脊六兽硬山顶，青砖浮雕莲花脊”，属于典型的河南民居风格。特别是以“汝河南岸第一府”著称的朱紫峰宅院，占地7548平方米，是三路五进的四合院建筑群，门楼高达二丈余，飞檐斗拱，木雕彩绘，气势恢宏；3.护寨河、杨柳河。全长1500米、十余米宽的护寨河环寨一周，终年绿水长流，杨柳河经寨东自南向北注入北汝河；4.红石板街道；5.关帝庙、五虎庙；6.临沣苑等。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562899640314824.jpg\" alt=\"108  东花厅\" title=\"108  东花厅\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">随着临沣寨不断的对外宣传、推介，先后有多家新闻单位在此拍摄纪录片。2008年10月，央视四套《走遍中国》栏目，在此拍摄完成了上、下集纪录片《临沣寨传奇》之《古堡森森》、《祸起萧墙》，并于2009年2月分八个时段播出；2009年10月，央视十套《百科探秘》栏目在此也拍摄完成了专题片《风云临沣寨》；2012年9月，央视九套拍摄完成了三集纪录片《临沣寨传奇》之《古堡森森》、《悲情王府》、《百年绝唱》，现都已播出。并且自2004年以来，《摊牌》、《做局》、《挂帅》、《天下盐商》《香魂塘畔的香魂女》等电影、电视剧，分别在此地取景。2017年以来，中央一套中国影像方志栏目、中央十套地理中国栏目及平顶山电视台印象郏县栏目先后在临沣寨拍摄纪录片。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562899640409382.jpg\" alt=\"109  临沣寨四合院\" title=\"109  临沣寨四合院\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p><br/></p>','/uploads/20190712/b3553962c4f238e000c816f2be48fc73.jpg','/uploads/20190712/859c90e87a7627fa583ce9ac91586b84.jpg,/uploads/20190712/e18a6220ff6a42f44440793e163efdcd.jpg','1562899928',1,0,62,1,'象眼旅行—平顶山','113.29085','33.97043',50,12,20,88,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——郏县——<span style=\"background-color: rgb(255, 255, 255);\">堂街镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190712/1562899904944894.jpg\" title=\"1562899904944894.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(128,6,'舞钢市','','舞钢市曹集村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">曹集街属舞钢市武功乡辖区东侧，与驻马店地区交界处为村委驻地。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900031218184.jpg\" alt=\"曹集村党群综合服务中心\" title=\"曹集村党群综合服务中心\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">村名由来:明初，曹氏祖先自洪洞县迁至此做生意种地，逐渐形成集市，为了防止土匪强盗入内，筑成围街土寨墙一圈，有南门、东门、西门，白天夜晚由每户出人轮流站岗守寨护家。当时寨内人工挖吃水井5眼，用青砖砌井桶，青石条砌成方井口，现保留3眼并使用，其中两眼井水是咸水，3眼井水是甜水。集市为“露水集”，按现在的说:“早上五点起集，到七点集罢”，地点在东西大街，集上有布匹、绸缎、日用杂货、各种食品、铁货农具、羊肉锅、牛肉锅、蒸馍店、屠行、纸行、卷烟厂、油坊、药铺、兽医店、小卖店、染坊、铁匠铺、木匠铺等，故称“曹集街”，现称曹集村。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900031782937.jpg\" alt=\"曹刚川故居3\" title=\"曹刚川故居3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">组成、面积及人口:辖曹集、陈连庄、小屈庄3个自然村，7个村民小组，有440户人家，总人口1506人，耕地面积为2165亩，有汉、回两个民族。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900032585818.jpg\" alt=\"曹集希望小学\" title=\"曹集希望小学\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">学校由来:1908年建立詹山私学即现在的曹集希望小学，建校以来校名曾多次更改为“詹山私学”、“舞阳县詹山小学”、“舞阳县区立第五小学”、“舞阳县区立第二小学”、“舞阳县区立曹集小学”、“舞阳县詹山乡中心小学”、“舞阳县第十一完全小学”、“曹集小学”、“曹集联中”、“武功三中”、“武功实验小学”、“曹集希望小学”。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900032308017.jpg\" alt=\"曹集村文化广场活动\" title=\"曹集村文化广场活动\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">二、传统文化，名人名流，民俗特色<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">曹集街是一个人杰地灵的地方，大小人物层出不穷。如今的曹集村新农村面貌焕然一新，宽敞的马路，明亮的路灯，整洁的街道，优美的环境，整齐的规划。新建社区美丽的小别墅一排排，建成党员群众活动中心大楼、文化广场、篮球场，配套各种健身器材。先后被舞钢市命名为“模范村”、“先进党支部”、“舞钢市非物质文化遗产传承基地”。非遗项目包括“曹集大铜器”“扎走马灯”“扎绣球”等。保留并传承老一辈艺人各项民间艺术活动，其中包括踩高跷、狮子、大汉、刘翠妮、小车、旱船、花轿、二鬼扳跌、河蚌与渔夫、青蛙水族灯、县太爷出巡、大秧歌、各种戏曲等民间艺术活动。定期举办各种文化活动:舞蹈、歌曲、相声、小品、篮球、羽毛球、乒乓球、跳绳等多项活动，被舞钢市定为“舞钢市文化家园试点”。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900033386293.jpg\" alt=\"曹集村希望小学3\" title=\"曹集村希望小学3\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><br/></p>','/uploads/20190712/ab3940fc817b8622204b9d76c6dade95.jpg','/uploads/20190712/a23f90651cb681b9be7cd6ffd4155679.jpg,/uploads/20190712/0f52f3ada42ae643928ad33821f1b68d.jpg','1562900304',1,0,2,0,'象眼旅行—平顶山','113.53696','33.33359',50,12,26,105,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——舞钢市——<span style=\"background-color: rgb(255, 255, 255);\">武功乡</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190712/1562900290469627.jpg\" title=\"1562900290469627.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(129,6,'宝丰','','宝丰县大黄村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">大黄新型农村社区位于赵庄镇政府南1.5公里处，宝赵公路纵贯南北，北有石河环绕，具有得天独厚的地理条件和交通优势。社区共有农户512户，总人口2022人，耕地面积2050亩。综合经济实力居全县前20强。村两委班子务实重干，各项社会事业呈现全面进步的良好态势。省、市、县领导多次到大黄指导调研，对各项工作给予了充分肯定。先后获得省级生态村，市级文明村，平顶山市新农村建设先进村等荣誉，被省民政厅确定为社会化养老示范村。村党支部被县委授予首批“五星级党支部”。村党支部书记马豹子2016年被省委授予“优秀共产党员”称号，并被选举为省党代会党代表、2016“感动中原十大人物”。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900400989861.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">大黄村魔术文化底蕴深厚，是远近闻名的“魔术专业村”，全国重要的魔术道具、服装等魔术文化产品加工销售集散地。为发展旅游产业，大黄隆重推出四季游：春季“赏花休闲游”、夏季“魔幻夏令营”、秋季“开心农场采摘季”、冬季“日用品展销”。到大黄休闲观光，处处是风景，到处是景点，让老百姓居住于风景中，让游客流连于画卷里。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900401899843.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">近年来，大黄村文化学习气氛浓厚，村民们的表演多次出现在县镇举办的文化艺术活动中，获得了好评。通过开展精神文明建设，塑造农民新形象，使村民的思想观念，道德观念、法制观念、生活观念有了很大的变化，现在的大黄村环境雅洁，村民安居乐业，邻里和睦。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190712/1562900401824902.jpg\" alt=\"\" title=\"\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p><br/></p>','/uploads/20190712/ef363d6a80acf0d98d66cbfead810f83.jpg','/uploads/20190712/69a42cf5d06579e366f1ae912cd52b1a.jpg,/uploads/20190712/fe278f53e3f509946c0a6eae66316c5d.jpg','1562900581',1,0,17,1,'象眼旅行—平顶山','114.25965','30.58015',50,12,25,82,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——宝丰县——<span style=\"background-color: rgb(255, 255, 255);\">赵庄镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190712/1562900559529446.jpg\" title=\"1562900559529446.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(130,6,'鲁山县','','鲁山县牛王庙','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">牛王庙村位于鲁山县团城乡政府西南6公里处，总人口1410人，总面积13平方公里。农业主产小麦、玉米、水稻、豆类、辛夷、特产板栗、猕猴桃、核桃、柿子、中药材、木耳、香菇、鹿茸菜、柞蚕茧等，素有“辛夷之乡”、“板栗之乡”美誉。清水河绕山贯城，蜿蜒蛇形，游鱼细石，清澈透底，异草奇葩，林丰果硕，资源丰富，环境优美，物华天宝，人杰地灵，森林覆盖率达92%以上。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900719378210.jpg\" alt=\"水上乐园\" title=\"水上乐园\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">景区内有清水河森林公园，自然景观优美，拥有伏牛山系特有的山区资源，有清澈透碧的清水河；有叹为观止的银丝瀑、三道瀑、有山花烂漫的滑石山、有天地通灵的金钟山、有神奇无比的太子山、树龄千年的皂角树、有无与伦比的辛夷王和有芬芳四溢的辛夷林海，2018年5月份建成党建主题公园，主要用于党建主题思想宣传教育接受党的熏陶。在河道、广场、公园和道路两旁种植各式各样的花籽，一年四季都有鲜花盛开。清水河景区水清山美，让每一位前来的游客都能心旷神怡，流连忘返。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900719884997.jpg\" alt=\"党建主题公园\" title=\"党建主题公园\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">清水河森林公园风情园依托青山绿水、旖旎风光，着力打造“种植园”、“娱乐园”、“采摘园”、“百花园”四大园区。“种植园”让游客躬耕田亩，推碾拉磨，纺花织布，把手菽麦，接受艰苦朴素的教育；“娱乐园”让游客垂钓清池，品玩野味，沐浴疗养，吟诗高歌，体验大自然的魅力；“采摘园”让游客置身瓜田，仰视李下，剥弄山果，回味甘汁，满足硕果累累之喜悦；“百花园”让游客畅游花海，追蜂逐蝶，吸吮花露，与花比艳，感受花仙美子的幽香。清水河森林公园是“自然之美”、“纯净之美”、“健康之美”“风情之美”的靓女，其色可餐，其容可掬，其姿可拥，其秀可吻！</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190712/1562900720191840.jpg\" alt=\"游园1\" title=\"游园1\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p><br/></p>','/uploads/20190712/3c70784e1a7a5554ab6b96980e978fd7.jpg','/uploads/20190712/e7a12903c5ae7fd8a749b6097b9c1278.jpg,/uploads/20190712/3fa1f570e4ad00367cbf366e32103e71.jpg','1562900859',1,0,2,0,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,55,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（2天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——鲁山县——<span style=\"background-color: rgb(255, 255, 255);\">团城乡</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190712/1562900833374882.jpg\" title=\"1562900833374882.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(131,6,'郏县','','姚庄回族乡三郎庙村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">三郎庙村位于郏县姚庄回族乡，是一个古老的回汉聚居村落，是乡政府所在地。这里有楚庄王赐名的“玉泉”、有古老的运粮河、有千年名木思乡槐。全村面积1.7平方公里，总人口1585人，其中回族1450人。近年来，在乡党委、政府的正确领导下，依托2016年河南省美丽乡村建设试点项目，全村动员，全民参与，大力实施美丽乡村建设，发展乡村旅游。先后荣获“中国少数民族特色村寨”、“河南省卫生村”、“河南省传统村落”等荣誉。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900936702186.jpg\" alt=\"1.迎四方牌坊\" title=\"1.迎四方牌坊\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900937593013.jpg\" alt=\"4.茶壶雕塑\" title=\"4.茶壶雕塑\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">该村先后整治运粮河，保护千年古木“思乡槐”，建起文化广场，建成“迎四方”牌坊。突出茶水元素，建设茶壶型雕塑65处，茶叶形立面改造7500平方米。发展环村生态廊道4.3 公里。修筑村内道路5.6公里，安装路灯120盏。实施垃圾分类，为村民购置蓝绿双色垃圾桶500个。修建排水沟2600米，建起污水处理站，打造具有浓郁特色的美丽乡村。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562900937546692.jpg\" alt=\"7.金镶玉展厅\" title=\"7.金镶玉展厅\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; color: rgb(0, 0, 0);\">该村现已发展酒店、饭店、茶馆30余家，烧鸡、烧兔店20多家，牛肉加工店16家，金镶玉加工店4家，矿泉水厂3家，占地800亩的生态园1 个。现如今，三郎庙村年接待游客30万人次，取得了良好的经济和社会效益，走出了一条“茶食兴村，旅游富民”之路。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190712/1562900938665225.jpg\" alt=\"13.孔雀庄园\" title=\"13.孔雀庄园\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p><br/></p>','/uploads/20190712/e2bbce8639278a6c6dc2932293ff175f.jpg','/uploads/20190712/820a45a5e4c7dff3a5448b920ebfabe1.jpg,/uploads/20190712/8d29eaf53f0aabd080d5a7cadf083995.jpg','1562901139',1,0,42,1,'象眼旅行—平顶山','113.29085','33.97043',50,12,20,97,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——郏县——<span style=\"background-color: rgb(255, 255, 255);\">姚庄乡</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190712/1562901121636819.jpg\" title=\"1562901121636819.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(132,6,'叶县','','叶县辛店镇桐树庄村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">叶县辛店镇桐树庄村位于叶县东南，著名的望夫石山上，距县城35公里，东与舞阳县交界，南与舞钢市接壤，全村总面积14.7平方公里，7个自然村，4个村民组，总人口550人，耕地面积540亩，林地面积近2万亩，森林覆盖率90%以上。明太祖初年，由山西洪桐县迁此建村，相传当时这里遍地都是桐树，人们便以此为村名，称桐树庄。2008年被评为市级生态文明村，2010年被评为省级生态文明村，2010年荣获省精神文明创建工作先进村，2018年还被河南省确定为首批生态旅游示范村。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562917925350141.jpg\" alt=\"IMG_6443\" title=\"IMG_6443\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">为响应县委、县政府发展村集体经济政策的号召，2018年初，桐树庄村党支部书记陈国跃带领村两委班子成员到贵州省安顺市塘约村进行学习考察。学习后，村两委迅速举行村党支部会提议、村两委会商议、党员大会审议、村民代表会议等不同层次、多道会议，统一了思想、增强了认识。通过多方论证，目前桐树庄组的800亩土地已流转到位，归集体所有，为村集体经济发展打下坚实基础。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562917926376457.jpg\" alt=\"IMG_6441\" title=\"IMG_6441\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562917927726254.jpg\" alt=\"IMG_6438\" title=\"IMG_6438\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">二、交通条件<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">桐树庄村北2公里就是省道平桐路，东4公里处就是焦桐高速叶县南站出口，西12公里处就是许平南高速旧县站出口，东北6公里处还有平舞铁路东房庄车站，另外，已开工兴建的辛常（辛店至常村）扶贫旅游线路环绕村庄东、西、北三面绕过。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"color: rgb(0, 0, 0); font-family: 宋体, SimSun; font-size: 16px;\">发展林果、软籽石榴、旅游业等高附加值的经济产业。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">三、下一步规划<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">2012年，为纪念红九军将士的英勇事迹，平顶山老促会投资180多万元，在桐树庄村建起了红九军纪念碑，原中央军委副主席张万年亲笔为纪念碑题词：“红军将士永垂不朽”。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562917927984829.jpg\" alt=\"IMG_6533\" title=\"IMG_6533\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">现已被平顶山命名为“传承红色基因、爱国主义教育基地”，桐树庄村也被河南省定为革命老区村。桐树庄村根据新形势下爱国主义教育的特点，结合桐树庄村的红九军纪念碑以及下一步拟建设的红九军纪念馆等红色教育基地，进一步深化红色文化底蕴让党员干部在党建工作中找到价值、文化和精神上的认同感，为红色旅游发展提供人文保障，缅怀为共产主义崇高理想而牺牲的革命先烈，继承和发扬革命传统，以自身优势，探索建立一条完善的红色旅游线路。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">今年年初，支部书记带领村民到井冈山购置井冈山毛竹，目前已种植260亩井冈山毛竹，二期规划种植700亩井冈山毛竹，共达到1000亩，打造“北国竹海”景观。下一步，通过注册红九军爱国教育基地商标，建立竹编织工艺加工业产业链，进一步加强红色旅游品牌开发，推出优质农产品、竹编制手工艺品、红军帽、手工布鞋、手工蓑衣、手工扫帚等红色旅游产品，结合下一步石龙沟景区开发项目，开展“游客重走红军路”等旅游活动，并且与前期村内统一规划的徽派建筑、300亩软籽石榴种植基地，500亩红薯种植基地相结合，开辟出一条集民俗休闲基地、红色教育基地、康复疗养基地、林果采摘基地、生态养殖基地、旅游观光基地综合体的发展道路，大力发展村集体经济，从而达到整村富裕，把桐树庄村建设成一个美丽富饶、文明向上、近悦远来的社会主义新农村。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562917928971999.jpg\" alt=\"IMG_6466\" title=\"IMG_6466\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; color: rgb(0, 0, 0);\">除此之外，还有2300多年历史的“千年银杏树”，该树四世同堂，需六人合抱，被中央电视台誉为“黄河以南第一树”，供奉佛祖已有600多年的毛仁禅寺等景观。<br/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">其次传说众多：石龙沟的传说、仙洞沟的传说、七十二道堰的传说等等。</span><br/></p><p><br/></p>','/uploads/20190712/e1eae4a66d18398ff2fcc950b0c67ebd.jpg','/uploads/20190712/06ff6dd26c7fdb4060411d2f554407a2.jpg,/uploads/20190712/ac2487c27de521dabb839c537f65cab0.jpg','1562918244',1,0,126,0,'象眼旅行—平顶山','113.3575','33.62255',50,12,29,63,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——叶县——<span style=\"background-color: rgb(255, 255, 255);\">辛店镇</span></span></p>','<p>春夏秋冬。</p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190712/1562918205373921.jpg\" title=\"1562918205373921.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"text-align: center;\">长按识别关注 让旅行变得更简单</p>','平顶山市文化广电和旅游局'),(133,6,'叶县','','叶县常村镇瓦房庄村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">叶县常村镇瓦房庄村位于常村镇西部，距常村镇4公里，有4个自然村，3个村民组，117户共480人，耕地面积226.9亩，是旅游开发重点村；</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190712/1562918324170224.jpg\" alt=\"52ca7102fde4047d58481c705af9103\" title=\"52ca7102fde4047d58481c705af9103\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"background-color: rgb(255, 255, 255); font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">辖区内石门水库是叶县外八景之一，历史人文资源丰富，曾留下李白、高孝通、元丹青等名人到此巡游的佳话，瓦房庄村地处老青山南，山清水秀、树高林密、环境幽静、自然景观迷人，是我县唯一的“河南省百村万户旅游富民工程”旅游特色村，也是叶县南部山区旅游开发重点区，自然禀赋丰富，旅游发展前景广阔；该村林果基地占地面积100余亩，投资资金10万余元，按照统一规划设计、管理要求种植苹果35亩，桃子40亩，青梨、红梨、李子共25亩，带动贫困劳动力10余人，带动贫困户增加收入，形成一村一品的产业特色。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190712/1562918325442836.jpg\" alt=\"微信图片_201906270911452\" title=\"微信图片_201906270911452\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190712/1562918325130438.jpg\" alt=\"微信图片_2019062709114515\" title=\"微信图片_2019062709114515\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><img src=\"/ueditor/php/upload/image/20190712/1562918326617332.jpg\" alt=\"微信图片_2019062709114514\" title=\"微信图片_2019062709114514\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p><br/></p>','/uploads/20190712/f85e7604bf6f2bf748c6486e157fb9f5.jpg','/uploads/20190712/0e70d33f64734bbfb137e03ae8444bbe.jpg,/uploads/20190712/35f031b36453ea2ce5a58e8393e9f329.jpg','1562918602',1,0,6,0,'象眼旅行—平顶山','113.3575','33.62255',50,12,29,62,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——叶县——<span style=\"background-color: rgb(255, 255, 255);\">常村镇</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190712/1562918586426372.jpg\" title=\"1562918586426372.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(134,6,'郏县','','郏县广阔天地乡大李庄村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">大李庄村，是1955年毛主席题词的广阔天地乡乡政府所在地，是知识青年上山下乡的发源地。该村交通便利、农业高效、产业合理、生态良好、乡风文明、民生保障、人才丰富、支部坚强、旅游发达、村民富裕。是全国文明村镇、国家特色景观旅游名镇、河南省历史文化名村、河南省传统村落、市级卫生村、市级示范交管站、县级文明村、县级新农村建设先进村、县级新型农村合作医疗工作先进村、县级新型农村养老保险工作先进村、县级社会治安综合工作先进村、县级人口与计划生育工作先进村，曾获得县级民间艺术金奖、县级民间艺术表演奖等100多项省市县级荣誉称号和奖项。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190712/1562919729513107.jpg\" alt=\"文艺演出\" title=\"文艺演出\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: \" helvetica=\"\" max-width:=\"\" color:=\"\" font-size:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">村党支部努力实现乡村的绿色发展，建成了500余亩的“生态文化公园”，打造了人与自然的和谐共生的发展新格局；积极打造知青旅游，建成了AAA级知青园景区，将“广阔天地大有作为纪念馆”建成河南省爱国主义教育示范基地，让知青旅游从这里走向全国；积极繁荣先进的农村文化，提倡新事新办，生态环保，反对陈规陋习，焕发乡风文明新气象。</span></p>','/uploads/20190712/40ac8206fec5abd93c757bde2c0a06bf.jpg','/uploads/20190712/46cf04629771d77499ecf2ac2e212a4c.jpg,/uploads/20190712/d55d0b9c7a806a6b52f23d1540c41529.jpg','1562919907',1,0,11,1,'象眼旅行—平顶山','113.29085','33.97043',50,12,20,94,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p><span style=\"font-family: 宋体, SimSun;\">平顶山市——郏县——<span style=\"background-color: rgb(255, 255, 255);\">广阔天地乡</span></span></p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190712/1562919883642370.jpg\" title=\"1562919883642370.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局'),(135,6,'鲁山县','','鲁山县寺沟村','<p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: left;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">寺沟村位于团城乡西北部，下辖村民组10个，422户1386口人。全村总面积约10平方公里，森林覆盖率达95%以上，清水河蜿蜒穿行，林丰果硕，资源丰富，环境优美，素有“天然氧吧”的美称。</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190716/1563265950148430.jpg\" alt=\"乡村文化\" title=\"乡村文化\" style=\"font-size: 16px; box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">近年来，寺沟村逐步确立以发展乡村生态旅游业为主导的特色产业，充分利用农村森林景观、田园风光、山水资源和乡村文化，发展各具特色的乡村休闲旅游业。形成了以梅花鹿养殖基地、鱼藕混养、食用菌和水稻种植基地为主线的休闲农家线路。同时着力打造马老沟组特色民宿、仓房庄组职业摩托车赛基地、樊庄组烧烤广场的筹建，将传统的农业发展逐步引向农业观光、农事体验、特色农庄、农情民舍等附加值高的乡村旅游。</span><br/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190716/1563265951414073.jpg\" alt=\"清水河\" title=\"清水河\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%;\"/></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190716/1563265951503102.jpg\" alt=\"鱼藕混养\" title=\"鱼藕混养\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; vertical-align: middle;\"/></p><p><br/></p>','/uploads/20190716/baa3122606ef51cce51a782ec3ac2259.jpg','/uploads/20190716/2e66f9360696f6d21b318924f5c022ae.jpg,/uploads/20190716/1bc1fa275b351084306da17b3b7d4558.jpg','1563266135',1,0,25,1,'象眼旅行—平顶山','112.90915','33.73634',50,12,22,55,'<p><span style=\"font-family: 宋体, SimSun;\">自驾游（1天）为佳。</span></p>','<p>平顶山市——鲁山县——观音堂乡</p>','<p><span style=\"font-family: 宋体, SimSun;\">春夏秋冬。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\"><img src=\"/ueditor/php/upload/image/20190716/1563266114107515.jpg\" title=\"1563266114107515.jpg\" alt=\"微信图片_20190707093953.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun;\">长按识别关注 让旅行变得更简单</span></p>','平顶山市文化广电和旅游局');

