#
# Structure for table "ddsc_spot_play"
#

DROP TABLE IF EXISTS `ddsc_spot_play`;
CREATE TABLE `ddsc_spot_play` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sid` int(11) NOT NULL DEFAULT '0' COMMENT '景点id',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `marray` varchar(255) DEFAULT NULL COMMENT '简介',
  `content` text COMMENT '详情',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `status` tinyint(3) NOT NULL DEFAULT '0' COMMENT '推荐 0否 1是',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8 COMMENT='玩转景区';

