# Data for table "ddsc_spot_play"
#

INSERT INTO `ddsc_spot_play` VALUES (2,9,'测试222','测试测试测试测试','<p>测试测试测试测试测试测试测试测试测试测试测试测试</p>','/uploads/20190524/fae18010a0d1faf6ec19ec01ef4a6030.jpg',0),(3,9,'测试222','测试测试测试测试','<p>测试测试测试测试测试测试测试测试测试测试测试测试</p>','/uploads/20190524/fae18010a0d1faf6ec19ec01ef4a6030.jpg',0),(7,7,'测试222','测试测试测试测试','<p>测试测试测试测试测试测试测试测试测试测试测试测试</p>','/uploads/20190524/fae18010a0d1faf6ec19ec01ef4a6030.jpg',0),(8,7,'测试222','测试测试测试测试','<p>测试测试测试测试测试测试测试测试测试测试测试测试</p>','/uploads/20190524/fae18010a0d1faf6ec19ec01ef4a6030.jpg',0),(9,5,'测试222','测试测试测试测试','<p>测试测试测试测试测试测试测试测试测试测试测试测试</p>','/uploads/20190524/fae18010a0d1faf6ec19ec01ef4a6030.jpg',0),(10,5,'测试222','测试测试测试测试','<p>测试测试测试测试测试测试测试测试测试测试测试测试</p>','/uploads/20190524/fae18010a0d1faf6ec19ec01ef4a6030.jpg',0),(11,11,'景区1','景区1景区1简介','<p>景区1景区1详情</p>','/uploads/20190615/87def08a8d87f138911e3b4312257d09.jpg',0),(13,39,'瀑布红石峡区','以小溪水、瀑布、高峡平湖、环球飞车表演观赏为主；','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">以小溪水、瀑布、高峡平湖、环球飞车表演观赏为主；</span></p></a><img src=\"/ueditor/php/upload/image/20190715/1563158835807179.jpg\" title=\"1563158835807179.jpg\" alt=\"4.jpg\"/></p>','/uploads/20190715/de998f141c72d19b6535c8546d7dd80b.jpg',0),(14,39,'原始森林区','以六叠瀑布、玻璃桥、悬崖栈道、观景台，及原始森林杜鹃花海的流星观赏为主','<p><a style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; color: rgb(51, 51, 51); font-size: 12px; white-space: normal;\"><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\">以六叠瀑布、玻璃桥、悬崖栈道、观景台，及原始森林杜鹃花海的流星观赏为主</span></p><p style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"><span style=\"box-sizing: border-box; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); max-width: 100%; font-size: 16px;\"><img src=\"/ueditor/php/upload/image/20190715/1563158862529088.jpg\" title=\"1563158862529088.jpg\" alt=\"6.jpg\"/></span></p></a></p>','/uploads/20190715/d83e6a53ec09718793a3d7be6360f77f.jpg',0),(15,40,'和合峰','尧山重要代表性景观，海拔高1700米，相对高度190米','<p><a></a></p><p style=\"text-indent: 2em;\"><a>尧山重要代表性景观，海拔高1700米，相对高度190米，北面和南面均为绝壁，唯独朝向西的一面，中间有明显的断裂裂缝从上直通峰底，形成一高一矮两峰，两峰似情侣、似夫妻、似兄弟，一高一低紧紧依偎，密不可分。传说，和合峰为和、合二仙的化身，和、合二仙原为兄弟，二人互敬互让亲密无间，情同手足，化石与此，故称和合峰，是团结、和谐的象征。</a></p><p><a><img src=\"/ueditor/php/upload/image/20190718/1563444854653189.jpg\" title=\"1563444854653189.jpg\" alt=\"1.jpg\"/></a></p><p><br/></p>','/uploads/20190718/c9910275e037333373f353fda56c7b15.jpg',0),(16,40,'尧山索道','索道全长3000米，高差1000米，每厢容量8人','<p style=\"line-height: 2em; text-indent: 2em;\"><a></a></p><p><a>尧山索道投资1.6亿元，于2008年10月2日投入使用，它采用奥地利多贝玛亚公司生产的单线循环式脱挂抱索器吊厢式设备，由山东省泰安索道安装公司安装。索道全长3000米，高差1000米，每厢容量8人，单程运行时间约6分钟，每小时的单向运载量达1200人次。它与通往主峰玉皇顶的1500米高标准仿生态栈道（飞云栈道）连接一起，成为景区一条新的风景线。</a></p><p><a><img src=\"/ueditor/php/upload/image/20190718/1563445004291918.jpg\" title=\"1563445004291918.jpg\" alt=\"10.jpg\"/></a></p><p><br/></p>','/uploads/20190718/eaa92cd73578d8e71601e15f49da3aa6.jpg',0);

