#
# Structure for table "ddsc_spot_ticket"
#

DROP TABLE IF EXISTS `ddsc_spot_ticket`;
CREATE TABLE `ddsc_spot_ticket` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sid` int(11) NOT NULL DEFAULT '0' COMMENT '景区id',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `type` varchar(255) DEFAULT NULL COMMENT '类型',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '价格',
  `marray` varchar(255) DEFAULT NULL COMMENT '简介',
  `yprice` decimal(10,2) NOT NULL DEFAULT '0.00',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COMMENT='景区门票';

