#
# Structure for table "ddsc_strat"
#

DROP TABLE IF EXISTS `ddsc_strat`;
CREATE TABLE `ddsc_strat` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `tag` varchar(255) DEFAULT NULL COMMENT '简介',
  `author` varchar(255) DEFAULT NULL COMMENT '作者',
  `image` varchar(255) DEFAULT NULL COMMENT '图片',
  `content` longtext COMMENT '内容',
  `recome` tinyint(3) NOT NULL DEFAULT '0' COMMENT '推荐 0否 1是',
  `status` tinyint(3) NOT NULL DEFAULT '1' COMMENT '状态 0关闭 1开启',
  `sort` int(11) NOT NULL DEFAULT '50' COMMENT '排序',
  `time` varchar(255) DEFAULT NULL COMMENT '发布时间',
  `looks` int(11) NOT NULL DEFAULT '0' COMMENT '浏览量',
  `addr` varchar(255) DEFAULT NULL COMMENT '地址',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 COMMENT='攻略';

#
# Data for table "ddsc_strat"
#

INSERT INTO `ddsc_strat` VALUES (9,'河南珍藏的最美瀑布，就在鲁山','尧山黑龙潭瀑布又称九曲瀑布，是尧山国家重点风景名胜区主要瀑布之一.......','旅途掠影','/uploads/20190627/17f8dd21645a383a239f6a693ef95261.jpg','<p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">河南地处中国中部，不像南方那样水资源丰富，所以河南人对水的感情也更亲切，对瀑布也更是情有独钟。在河南很多的山地景区中都有或大或小的瀑布，有的气吞山河，有的细若游丝，有的如悬空中，有的曲折蜿蜒。在这些众多的瀑布中有挂瀑布，在美景的映衬下显得低调了许多。这就是尧山景区的黑龙潭瀑布。</p><p><br/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" font-size:=\"\">尧山景区的黑龙潭瀑布</span><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">尧山黑龙潭瀑布又称九曲瀑布，是尧山国家重点风景名胜区主要瀑布之一，尧山山峰奇特，瀑布众多，森林茂密，大山壁立，异峰如塑，怪石纷呈，是尧山自然风光的一大特色。站立主峰玉皇顶上，千岩万壑，飞龙走凤，远近高低，景色迥异，构成了一幅幅美不尽收的图画。黑龙潭瀑布作为景区的重要景观供游客观赏。</p><p><img src=\"http://p3.pstatp.com/large/pgc-image/4ba72daba2504d32b2409348e29ba98e\" img_width=\"640\" img_height=\"457\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p><img src=\"http://p1.pstatp.com/large/pgc-image/8daf329491154049933e04d896b1f01a\" img_width=\"452\" img_height=\"652\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">尧山黑龙潭瀑布高70余米，呈三弯九曲之状，水面根据枯水与丰水期分别宽1-6米不等，由于水流下切磨蚀与岩石承受侵蚀能力不同等因素，断崖上形成数个深度0.3-1.5米不等的凹窝，呈之字形自上而下分布，使壁面凹凸不平，水流形成三弯九曲之状，故也称之为九曲瀑布。</p><p><img src=\"http://p3.pstatp.com/large/pgc-image/d10138ea10db4f34b4198b9573d41358\" img_width=\"640\" img_height=\"373\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p><img src=\"http://p3.pstatp.com/large/pgc-image/89536adf986644159b0e030207f5891c\" img_width=\"600\" img_height=\"401\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">瀑布两边的崖壁上，秀木扶疏，青苔密布，各色野花撒落其间，与雪白的瀑布交相辉映，织成一幅锦绣图案。由于石人山降水丰沛，上游植被繁茂，水源涵养好，瀑水常年不断。瀑布注入下端的黑龙潭，訇响声传出半里之外。</p><p><img src=\"http://p1.pstatp.com/large/pgc-image/8083c3aedaf74729babc2639afcc2b59\" img_width=\"640\" img_height=\"1138\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p><img src=\"http://p1.pstatp.com/large/pgc-image/4afe01c35b5d48f897291c61fbb6c480\" img_width=\"640\" img_height=\"424\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p class=\"pgc-img-caption\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-size: 12px; color: rgb(119, 119, 119); line-height: 16px;\"><br/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\"><br style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box;\"/></p><p class=\"ql-align-justify\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; margin-top: 16px; margin-bottom: 16px; padding: 0px; color: rgb(34, 34, 34); font-family: \" pingfang=\"\" hiragino=\"\" sans=\"\" microsoft=\"\" wenquanyi=\"\" micro=\"\" helvetica=\"\" white-space:=\"\" background-color:=\"\">每到夏季，山水如兽直扑潭中，九穴则全然不见，留下击鼓撞钟般的吼叫，崩花溅玉，一派雷霆万钧、大气磅礴的壮观景象。平日里，潺潺飞流从穴缘泻下，瀑白穴幽，交相映衬。瀑下潭水清澈透底，涟漪阵阵，碧光闪耀，令人唏嘘。</p><p><img src=\"http://p3.pstatp.com/large/pgc-image/7bbadb6e1cac4eeab7928e9b0edef4e8\" img_width=\"640\" img_height=\"427\" alt=\"河南珍藏的最美瀑布，就在鲁山\" inline=\"0\" style=\"-webkit-tap-highlight-color: transparent; box-sizing: border-box; border-style: none; max-width: 100%; display: block; margin: 10px auto;\"/></p><p><br/></p>',1,1,50,'1561609312',2,'河南省鲁山县尧山镇尧山风景区'),(10,'昭平湖风景名胜区','昭平湖风景名胜区秀丽的自然风景，绚丽多彩的平湖晚霞，泛舟水上，把酒临风，仰蓝天白云，眺青山起伏，俯碧波绿水，其景其趣其情令人宠辱皆忘，心旷神怡。','象眼旅行—平顶山','/uploads/20190707/6969c33cd2d7cb189a8ec751d3a992a9.png','<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 0em;\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">昭平湖风景名胜区，原名昭平台水库，源于汉光帝刘秀与王莽昆阳之战时，刘秀在此拜始祖刘累后筑台招兵而得之。昭平湖风景名胜区位于河南省鲁山县境内，景区面积40km2。气候温和、四季分明，年均气温14.4℃。昭平湖风景名胜区丘陵起伏，沟壑纵横，水天一色，一望无际。秀丽山水与人文景观交相辉映，是集疗养、度假、休闲、刘氏寻根、娱乐于一体的中原旅游胜地。昭平湖这颗璀灿的明珠镶嵌在中原黄金旅游带上，鲁山的墨子故里石人山，郏县的东坡葬地三苏坟，宝丰的汝官窑故址，汝州的千年古刹风穴寺，叶县的叶公故里等著名文物旅游景点分布在景区四周，铁路公路纵横，交通十分便利。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br style=\"box-sizing: border-box; max-width: 100%;\"/>昭平湖一望无际，水天一色，周围峰峦叠翠，鸟语花香，充满了诗情画意。昭平湖风景名胜区的名胜古迹遐迩闻名，湖内巧夺天工的姑嫂石栩栩如生，动人故事浮想联篇；幽雅的湖心宝岛金山环绿树成荫，芳草青青，思想家墨子著书立说的“墨子著经阁”居高临下，尽览湖光山色；新石器时代文化遗址，两汉时期鲁阳城故地，刘姓始祖刘累封邑地，省级文保单位“邱公城”岛随水位的涨落时隐时现，呈现出海市蜃楼般的梦景；汉光武帝刘秀筑台招兵的招兵台、扬家名将焦赞、孟良屯扎寨之地的孟良寨和杨家岭尽现古代战争之遗迹。昭平湖风景名胜区秀丽的自然风景，绚丽多彩的平湖晚霞，泛舟水上，把酒临风，仰蓝天白云，眺青山起伏，俯碧波绿水，其景其趣其情令人宠辱皆忘，心旷神怡。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468420990562.png\" alt=\"昭平湖22\" title=\"昭平湖22\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline;\"/><img src=\"/ueditor/php/upload/image/20190707/1562468421798505.png\" alt=\"昭平湖11\" title=\"昭平湖11\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">二、景区概况</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; text-indent: 27px;\">水库内有金山岛、姑嫂石及夏代刘累邑、鲁阳古洽所邱公城遗址。水中有山，山中有水，石人山与昭平湖相映成趣，风景奇特壮观。水天一色，周围峰峦叠翠、鸟语花香，充满了诗情画意。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; text-indent: 27px; font-family: 宋体, SimSun;\">三、昭平湖典故</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 28px;\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">寻根望祖——刘氏寻根 在</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=98299&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">河南省鲁山县</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">昭平湖景区，山水相间，景色优美，刘氏祖庭文物广布，为华夏刘姓始祖刘累的故地。始祖刘累是远古部落陶唐氏首领尧的后裔，生活在夏代</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=6152511&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">孔甲</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">时期。《</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=1197593&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">左传</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">》、《</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=21344&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">史记</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">》等文献记载：刘累出生时手中有“刘累”纹样，以为吉祥，遂以为名。刘累以御龙，训龙著称，受命为孔甲训养龙，被赐为“</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=70795112&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">御龙氏</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">”。后因雌龙死去，刘累因惧怕孔甲治罪而携家眷潜逃</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=110419&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">鲁县</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">（今昭平湖景区</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=176943681&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">邱公城遗址</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">），并在此生活，终老。从此，中华刘姓从这里起源，</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=68371843&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘氏宗族</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">从这里繁衍。刘累不仅是刘姓始祖，也是</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=712343&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">龙姓</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">、</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=508075&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">范姓</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">的始祖。 刘姓是中国当代四大姓氏之一，历史上称帝称王者66人之多，刘姓统治的封建王朝长达650年。历史上涌观了</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=83016&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘邦</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">、刘秀、</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=5061517&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘备</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">、</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=7699647&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘安</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">、</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=60613561&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘墉</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">、</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=719002&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘鄂</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">等杰出的刘姓精英人物以及</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=214500&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘半农</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">、</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=181576&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘志丹</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">、</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=46013&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘胡兰</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">、</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=67629&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘少奇</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">、</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=51981&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘伯承</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">等革命志士，他们为中华文明的发展做出了重要贡献。为了满足海内外8000万刘氏后裔瞻仰祭拜始祖的宿愿，后人在“中华御</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=7871584&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">龙文化</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">苑”内刘姓始祖墓前重修“刘祖坛”，历史学家、考古学家、政界名人、海内外刘氏宗亲都十分关爱这里，挥毫泼墨，树碑题词。中华人民共和国已故国家主席刘少奇之子</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=99896320&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">刘源</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">将军为刘祖坛题词“始祖刘累繁衍开万代基业，宏扬御龙精神创</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=22737107&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; text-decoration: underline; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; color: rgb(0, 0, 0);\">中华复兴</span></a><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun; text-decoration: none; color: rgb(0, 0, 0);\">”。现有青碑林列。2004年5月份第四届世界刘氏宗亲联谊会在鲁山县成功召开，来自世界各地的刘氏后裔共聚一堂，祭拜先祖，表达对始祖刘累的崇敬之情。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468421486775.jpg\" alt=\"刘累公图像1\" title=\"刘累公图像1\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">四、旅游最佳路线</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; text-indent: 34px;\">昭平湖地处山区与平原结合部，丘陵起伏、沟壑纵横、烟波浩淼。独特的地理地貌造就了“一湖出平峡，万源聚山川”的绮丽自然美景。湖波环绕的姑嫂奇石巧夺天工；闻名于世的墨子著经阁掩映在苍松翠柏中；刘氏祖庭邱公城岛随湖水时隐时现，岛上散落着史前、夏商、周汉时期的文物，有“中原文物宝藏”之美誉。湖东岸的中华刘姓始祖苑庄严肃穆，刘秀拜祖招兵台雄浑壮观；鲁阳公挥戈返日的传说神秘动人；商周时期遗存的日月同辉的大型腾龙地画惟妙惟肖、神秘辉宏；气势雄伟的拦河大坝，壮观的“中原第一闸”杨家岭溢洪道泄洪闸；颇具江南水乡的坝下生态园，葱茏叠翠的水杉森林氧吧，绚丽多姿的平湖晚霞，令人叹为观止。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; text-indent: 34px; font-family: 宋体, SimSun;\">昭平湖景区游程安排:</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; text-indent: 34px; font-family: 宋体, SimSun;\">昭平湖一日游 上午：景区大门→九龙壁→杨家岭→坝下生态林→招兵台山→中华刘姓始祖苑（刘累墓）下午：大坝→游乐平台→码头→邱公城→金山环→条山岭→姑嫂石→湖边浴场→晚餐 晚上：游乐平台→篝火晚会→温泉浴→宾馆</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; text-indent: 34px; font-family: 宋体, SimSun;\">精品线路：</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"text-indent: 28px; box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">考古</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=10361584&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"text-indent: 28px; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; color: rgb(0, 130, 221); -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; text-decoration: underline;\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun;\">寻根游</span></a><span style=\"text-indent: 28px; box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">：景区大门→九龙壁→杨家岭→大坝→招兵台山→中华刘姓始祖苑→御龙广场→牌楼→碑林→</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=174277247&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"text-indent: 28px; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; color: rgb(0, 130, 221); -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; text-decoration: underline;\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun;\">始祖殿</span></a><span style=\"text-indent: 28px; box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">→刘累墓→世界刘氏纪念馆→世界</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=72908298&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"text-indent: 28px; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; color: rgb(0, 130, 221); -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; text-decoration: underline;\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun;\">刘氏会馆</span></a><span style=\"text-indent: 28px; box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">→码头→邱公城遗址→金山环岛→条山岭→姑嫂奇石→鲁山土龙遗址→返回码头</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"text-indent: 28px; box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">水利工程游：景区大门→九龙壁→杨家岭→大坝→招兵台山→中华刘姓始祖苑→白土沟付坝→分水闸→杨家岭大桥→尧沟溢洪道泄洪闸（五孔闸）→杨家岭</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=8865303&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"text-indent: 28px; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; color: rgb(0, 130, 221); -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; text-decoration: underline;\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun;\">非常溢洪道</span></a><span style=\"text-indent: 28px; box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">泄洪闸（十六孔闸）→</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=7651942&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"text-indent: 28px; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; color: rgb(0, 130, 221); -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; text-decoration: underline;\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun;\">水力发电厂</span></a><span style=\"text-indent: 28px; box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">→输水洞→</span><a href=\"https://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=64452919&ss_c=ssc.citiao.link\" target=\"_blank\" style=\"text-indent: 28px; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; color: rgb(0, 130, 221); -webkit-tap-highlight-color: rgba(255, 0, 0, 0); font-size: 16px; box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun; text-decoration: underline;\"><span style=\"box-sizing: border-box; max-width: 100%; font-family: 宋体, SimSun;\">王新宇</span></a><span style=\"text-indent: 28px; box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">纪念碑→进水口→码头→邱公城遗址→金山环岛→条山岭→姑嫂奇石→返回码头</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468423866016.png\" alt=\"昭平湖之游1\" title=\"昭平湖之游1\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468426821194.png\" alt=\"昭平湖之游2\" title=\"昭平湖之游2\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468427950516.png\" alt=\"昭平湖之游3\" title=\"昭平湖之游3\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468429323812.png\" alt=\"昭平湖之游4\" title=\"昭平湖之游4\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; white-space: normal; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-size: 17px; letter-spacing: 0.544px; text-align: justify; background-color: rgb(255, 255, 255); box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">五、昭平湖之娱</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: -apple-system-font, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei UI&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; white-space: normal; clear: both; min-height: 1em; color: rgb(51, 51, 51); font-size: 17px; letter-spacing: 0.544px; text-align: justify; background-color: rgb(255, 255, 255); box-sizing: border-box !important; overflow-wrap: break-word !important;\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">昭平湖烟波浩渺，水域开阔，历史积淀深厚，湖滨温泉水质好、水温高。您到这里休闲沐浴、温泉SPA，可以高歌一曲、篝火舞蹈，亦可乘船破浪、湖边静思，也可参加盛大的刘姓祭祖大典。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468430999217.png\" alt=\"昭平湖之娱1\" title=\"昭平湖之娱1\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468431136784.png\" alt=\"昭平湖之娱2\" title=\"昭平湖之娱2\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468433827468.png\" alt=\"昭平湖之娱3\" title=\"昭平湖之娱3\" width=\"677\" height=\"396\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">六、昭平湖之购</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; text-indent: 34px; font-family: 宋体, SimSun;\">&nbsp;昭平湖水域广阔，山峦起伏，为大量野生鱼类、山珍特产提供了良好的生长场所，故而特色鱼类银鱼、花鲢、鲤鱼、草鱼、鲫鱼丰富，板栗、山药、木耳、花菇、拳菜、野生蘑菇、核桃，水库西瓜、梨子、水蜜桃、蓝莓、葡萄等物产丰富，鲁山特产花瓷、鲁山丝绸、蚕丝被等特色旅游商品供您选购。景区内有商铺多家，供您挑选。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468433171740.png\" alt=\"昭平湖之购1\" title=\"昭平湖之购1\" width=\"600\" height=\"323\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468434177225.png\" alt=\"昭平湖之购2\" title=\"昭平湖之购2\" width=\"600\" height=\"392\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468434731499.png\" alt=\"昭平湖之购3\" title=\"昭平湖之购3\" width=\"600\" height=\"444\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">七、昭平湖之食</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; text-align: justify; text-indent: 34px;\">昭平湖地区物产富饶，食材丰沛，形成了独具特色的餐饮文化。在这里您可以品尝地方小吃鲁山胡辣汤、羊杂可、浆面条、油茶、鲁山槲坠（粽子的一种）等，河南地方风味名吃揽锅菜，高大上的鲁山水席。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: 宋体, SimSun; font-size: 16px; text-align: justify; text-indent: 34px;\">昭平湖风景区内目前拥有特色宾馆、酒店四家，主营昭平湖河鲜、山野菜特色、渔家特色餐饮。在这里您可以品尝特色鲜鱼、鲜虾，山珍野菜、鲁山特色水席、地方风味揽锅菜。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468435349628.png\" alt=\"昭平湖之食1\" title=\"昭平湖之食1\" width=\"600\" height=\"400\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468436512862.png\" alt=\"昭平湖之食2\" title=\"昭平湖之食2\" width=\"600\" height=\"361\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468439588005.png\" alt=\"昭平湖之食3\" title=\"昭平湖之食3\" width=\"600\" height=\"606\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">八、住宿</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">昭平湖景区内有特色温泉酒店森林温泉、尧龙湾2家、农家小院1家，周边有四星级酒店玉京温泉酒店、皇姑浴温泉度假酒店，驻地婆娑街有快捷宾馆4家。可满足您不能层次的温泉沐浴、住店需要。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468440935377.png\" alt=\"昭平湖住宿2\" title=\"昭平湖住宿2\" width=\"600\" height=\"395\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468440397779.png\" alt=\"昭平湖住宿1\" title=\"昭平湖住宿1\" width=\"600\" height=\"300\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468442806632.png\" alt=\"昭平湖住宿3\" title=\"昭平湖住宿3\" width=\"600\" height=\"375\" border=\"0\" hspace=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px;\">九、交通信息</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 16px; text-indent: 34px;\">昭平湖景区位于北纬33°42′—33°47′，东经112°42′—112°46′。景区处于南北气候过度带地区，四季分明，气候温和，年平均气温14.2℃；地理位置优越，处于武汉、西安、郑州三大城市之间，属伏牛山大旅游格局的东大门，处于鲁山百里温泉带的东端；国道207、311线在景区交汇、穿境而过，郑尧（郑州至尧山）、太澳（太原至澳门）高速公路从景区旁通过，距焦枝铁路鲁山站仅10公里，距郑州新郑国际机场120公里、洛阳机场110公里，南阳机场100公里，正在筹建的平顶山机场仅10公路。便利的交通，优越的地理位置，为海内外游客的到来提供了方便。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468442613194.jpg\" alt=\"昭平湖路线图\" title=\"昭平湖路线图\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%; display: inline;\"/></p><h2 style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 17.28px; font-weight: normal; max-width: 100%; color: rgb(102, 102, 102); white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">乘车路线：</span></h2><h3 style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 17.28px; font-weight: normal; max-width: 100%; color: rgb(102, 102, 102); white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">景区直达汽车路线：</span></h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（1）郑州陇海汽车站每天下午14：40从郑州陇海汽车站或郑州中心汽车站出发，全程高速（郑尧高速），票价65元；</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（2）洛阳一运汽车站每天下午14：40从洛阳一运汽车站出发，全程高速（二广高速转郑尧高速），票价63元；</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（3）漯河中心汽车站每天下午14：00从漯河中心汽车站出发，全程高速（郑尧高速），票价48元；</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（4）平顶山贸易广场鹰城汽车站每天上午5:40 7:30 10:40 下午 13:00 15:00 16:30 共6班车直达尧山景区，全程高速（郑尧高速），票价28元；</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（5）鲁山火车站对面的鲁山长途汽车站发往昭平湖景区的旅游班车平均每10分钟一班。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box; max-width: 100%;\"/></p><h3 style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 17.28px; font-weight: normal; max-width: 100%; color: rgb(102, 102, 102); white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">昭平湖自驾游行车路线：</span></h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（1）郑州至昭平湖景区：在郑州嵩山南路，过南四环，上郑尧高速，在鲁山出站口下车，沿尧山方向，按尧山景区指示牌行驶至昭平湖景区；</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（2）洛阳、南阳、焦作、济源、三门峡至昭平湖景区：洛阳、南阳、焦作、济源、三门峡：上二广高速沿南阳、襄樊方向(正南)到下汤站下，按景区指示牌行驶至昭平湖景区；</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（3）安阳、濮阳、新乡、鹤壁至昭平湖景区：安阳、濮阳、新乡、鹤壁：京珠高速到漯河，转南洛高速，到平顶山西转郑尧高速（沿尧山方向）在鲁山出站口下车，沿尧山方向，按尧山景区指示牌行驶至昭平湖景区；</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（4）陕西省至昭平湖景区：连霍高速---（洛阳转）二广高速---（平顶山转）郑尧高速，在鲁山下汤站下，按景区指示牌行驶至昭平湖景区；</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; text-indent: 34px; text-align: justify; background: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px; font-family: 宋体, SimSun;\">（5）山西省至昭平湖景区：山西地市：上二广高速沿南阳、襄樊方向(正南)到下汤站，按景区指示牌行驶至昭平湖景区。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"box-sizing: border-box; max-width: 100%; font-size: 16px;\"></span><img src=\"/ueditor/php/upload/image/20190707/1562468443410176.png\" alt=\"昭平湖路线图\" title=\"昭平湖路线图\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; max-width: 100%;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/ueditor/php/upload/image/20190707/1562468748303510.jpg\" title=\"1562468748303510.jpg\" alt=\"微信图片_20190707093953.jpg\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 16px;\">长按识别关注 让旅行变得更简单</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><br/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: none; word-break: break-all; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; list-style-type: none; padding-inline-start: 0px; margin-block-start: 0px; margin-block-end: 0px; max-width: 100%; color: rgb(102, 102, 102); font-size: 17.28px; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p>',1,1,50,'1562468791',0,'鲁山县昭平湖景区');

#
# Structure for table "ddsc_sys"
#

DROP TABLE IF EXISTS `ddsc_sys`;
CREATE TABLE `ddsc_sys` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL COMMENT '公司名称',
  `username` varchar(255) DEFAULT NULL COMMENT '负责人',
  `url` varchar(255) DEFAULT NULL COMMENT '网站域名',
  `qq` char(20) DEFAULT NULL COMMENT '客服QQ',
  `icp` varchar(255) DEFAULT NULL COMMENT 'icp备案号',
  `email` varchar(255) DEFAULT NULL COMMENT '邮箱',
  `tel` varchar(255) DEFAULT NULL COMMENT '固定电话',
  `phone` char(11) DEFAULT NULL COMMENT '手机号码',
  `longs` varchar(255) DEFAULT NULL COMMENT '经度',
  `lats` varchar(255) DEFAULT NULL COMMENT '纬度',
  `addr` varchar(255) DEFAULT NULL COMMENT '公司地址',
  `content` text COMMENT '公司简介',
  `pclogo` varchar(255) DEFAULT NULL COMMENT '电脑端logo',
  `waplogo` varchar(255) DEFAULT NULL COMMENT '手机端logo',
  `qrcode` varchar(255) DEFAULT NULL COMMENT '微信二维码',
  `wx` varchar(255) DEFAULT NULL COMMENT '微信公众号',
  `fax` varchar(255) DEFAULT NULL COMMENT '公司传真',
  `telphone` varchar(255) DEFAULT NULL COMMENT '400电话',
  `follow` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='网站基本信息';

