#
# Structure for table "ddsc_travel"
#

DROP TABLE IF EXISTS `ddsc_travel`;
CREATE TABLE `ddsc_travel` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL COMMENT '名称',
  `phone` varchar(255) DEFAULT NULL COMMENT '联系电话',
  `code` varchar(255) DEFAULT NULL COMMENT '证书号',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='旅行社列表';

#
# Data for table "ddsc_travel"
#

INSERT INTO `ddsc_travel` VALUES (2,'中国旅行社平顶山分社','0375-2955558','LH-HEN-CJ00012'),(3,'店面展示','123','undefined');

#
# Structure for table "ddsc_user"
#

DROP TABLE IF EXISTS `ddsc_user`;
CREATE TABLE `ddsc_user` (
  `uid` int(11) NOT NULL AUTO_INCREMENT,
  `nickname` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '昵称',
  `time` varchar(255) DEFAULT NULL COMMENT '注册时间',
  `image` text COMMENT '头像',
  `card` varchar(255) NOT NULL DEFAULT '' COMMENT '二维码',
  `fid` int(11) NOT NULL DEFAULT '0' COMMENT '上级id',
  `phone` varchar(20) NOT NULL DEFAULT '',
  `pwd` varchar(255) DEFAULT NULL COMMENT '密码',
  `integ` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '用户积分',
  `openid` varchar(255) DEFAULT NULL COMMENT '用户openID',
  PRIMARY KEY (`uid`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='用户列表';

