#
# Structure for table "ddsc_user_coupon"
#

DROP TABLE IF EXISTS `ddsc_user_coupon`;
CREATE TABLE `ddsc_user_coupon` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `money` int(11) NOT NULL DEFAULT '0' COMMENT '满减金额',
  `coupon` int(11) NOT NULL DEFAULT '0' COMMENT '优惠券金额',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '使用状态 0否 1是',
  `time` varchar(255) DEFAULT NULL COMMENT '领取时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COMMENT='用户优惠券';

