#
# Structure for table "ddsc_user_coupon_only"
#

DROP TABLE IF EXISTS `ddsc_user_coupon_only`;
CREATE TABLE `ddsc_user_coupon_only` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `cid` int(11) NOT NULL DEFAULT '0' COMMENT '优惠券id',
  `hid` int(11) NOT NULL DEFAULT '0' COMMENT '酒店id',
  `money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单金额',
  `coupon` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '优惠券',
  `status` tinyint(3) NOT NULL DEFAULT '0' COMMENT '使用状态 0否 1是',
  `time` varchar(255) DEFAULT NULL COMMENT '领取时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='酒店专用优惠券';

